pragma solidity ^0.8.0;

import "./ILPAdapter.sol";

/**
 * @title ICurveAdapter
 * @notice Interface for CurveAdapter contract.
 */
interface ICurveAdapter is ILPAdapter {
    /**
     * @dev
     * Default - It's pool that is not from stablecoins and is not V2 Factory.
     Oracle for this pool isn't secured from pool manipulations, so we can't create secure getPrice function.
     the example of such pool is tricrypto2 (USDT/WBTC/ETH): 0xD51a44d3FaE010294C616388b506AcdA1bfAAE46.
     * STABLE - It's pool based on stablecoins or eth, like 3pool, crvUSD/USDT or steth
     *          3pool: 0xbEbc44782C7dB0a1A60Cb6fe97d0b483032FF1C7
     * V2 - It's V2 Factory pool that have LP-oracle inside, like TriCRV, TricryptoUSDC
     *      TricryptoUSDC pool: 0x7f86bf177dd4f3494b841a37e810a34dd56c829b
    */
    enum CurvePoolType { Default, Stable, V2 }

    error RemoveLiquidityError();
    error ReentrantLockError();
    error ZeroPrice();
    error NativeETHNotSupported();
    error InvalidSlippage();
    error SlippageExceeded(uint256 expectedValueUSD, uint256 receivedValueUSD);
    error UnsupportedTokenCount(uint256 tokenCount);

    event TokenRegisteredRepeat(address lp, address lpPool, uint256 nTokens, CurvePoolType poolType);
    event TokenRegistered(address lp, address lpPool, uint256 nTokens, CurvePoolType poolType);
    event Withdraw(address asset, uint256 amount);
    event PriceProviderUpdated(address asset, address priceProvider);

    struct CurveLPInfo {
        address pool;
        CurvePoolType poolType;
        address[] underlyingTokens;
    }

    /* ==================== MUTABLE METHODS ==================== */
    /**
      * @notice Register the given LP token address and set the LP pool
      * @dev Checks that none of the underlying tokens is native ETH
      * @param lp address of LP token
      * @param lpPool address of pool of {lp} LP-token
      * @param nTokens number of underlyingTokens
     **/
    function registerLP(address lp, address lpPool, uint256 nTokens, CurvePoolType poolType) external;

    /**
    * @notice Add/remove priceProvider address for lp token.
      * @dev We have a particular case with the tricrypto2 pool in the Ethereum network,
      * as it is without the lp_rice() function, and it is implemented in the separate smart contract.
      * Function can be called only for registered lp and only by admin.
      * @param lp address of LP token
      * @param priceProvider address of separate smart contract that contains lp_price() function
     **/
    function updateLpPriceProvider(address lp, address priceProvider) external;
}","keccak256":"0xd787907e6d9a8b6b10c4f0761024bb486ecdf0023fb9c5266b23818be66e5b6c","license":"BSL 1.1"},"contracts/exchange/contracts/periphery/contracts/interfaces/adapters/ICurveHackyPool.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

// solhint-disable func-name-mixedcase
interface ICurveHackyPool {
    // We do not expect to write in the store on this call.
    // Our expectation is 1 sload operation for the `lock` status check + revert.
    // Because of it this function can be view which opens a possibility to do
    // a verification in the price provider on the `getPrice` fn execution.
    function remove_liquidity(uint256 _tokenAmount, uint256[2] calldata _amounts) external view;
    function remove_liquidity(uint256 _tokenAmount, uint256[3] calldata _amounts) external view;
    function remove_liquidity(uint256 _tokenAmount, uint256[4] calldata _amounts) external view;
    function remove_liquidity(uint256 _tokenAmount, uint256[5] calldata _amounts) external view;
    function remove_liquidity(uint256 _tokenAmount, uint256[6] calldata _amounts) external view;
    function remove_liquidity(uint256 _tokenAmount, uint256[7] calldata _amounts) external view;
    function remove_liquidity(uint256 _tokenAmount, uint256[8] calldata _amounts) external view;
}
