pragma solidity ^0.8.0;

enum DiscountType {
    aggressive,
    middle,
    conservative
}

struct Order {
    address token2sell;
    uint256 amountLeft;
    address token2buy;
    uint256 token2buyAmount;
    address beneficiary;
    uint256 creationTimestamp;
    DiscountType discountType;
    address caller;
}

struct OrderWithId {
    Order order;
    uint256 orderId;
}

interface IExchange {
    /* ===================== ERRORS ===================== */
    error OrderExist(uint256 orderId);
    error OrderNotExist(uint256 orderId);
    error IncorrectToken(address orderToken2buy, address inToken);
    error SmallAmountLeft();
    error UserIsNotBeneficiary();
    error IsNotTopStaker(address staker);
    error OrderNotForThisStaker(uint256 orderId, address staker);
    error NotEnoughReturnValue();
    error CallbackError();

    /* ======================== EVENTS ========================= */
    event OrderAdded(uint256 orderId);
    event OrderCanceled(uint256 orderId);
    event OrderFilled(uint256 orderId);
    event OrderFilledPartially(uint256 orderId);
    event LpOrdersAdded(address LPToken, uint256[] orderIds);

    /* ==================== MUTABLE METHODS ==================== */

    /**
      * @notice Add order to swap {token2sell} to {token2buy}.
      * @dev This swap is async, because depends on how arbitrage bots will fill this order. Emits OrderAdded() event.
      * Before addOrder user needs to approve token2sell for exchange contract
      * @param token2sell - Address of token to sell
      * @param amount2sell - Amount of {token2sell} to sell
      * @param token2buy - Address of token to buy
      * @param beneficiary - Address that will receive {token2buy} after filling order.
      * @param discountType - Speed of decreasing swap price. { 0 - aggressive, 1 - middle, 2 - conservative }
     **/
    function addOrder (
        address token2sell,
        uint256 amount2sell,
        address token2buy,
        address beneficiary,
        DiscountType discountType
    ) external returns (uint256);

    /**
      * @notice Only user who place order or who is beneficiary can cancelOrder();
      * @param orderId - Id of order to cancel.
     **/
    function cancelOrder (uint256 orderId) external;

    /**
      * @notice Fill order with flash-loan like logic
      * @dev Caller contract receive orders {token2sell} and after that his stableunitFillOrderCallback() is called.
      * At the end of thi callback contract should send some amount of another token back to Exchange contract.
      * @param orderId - Id of order to fill.
      * @param inAmount - Amount of {token2sell} to receive
      * @param data - encoded data to pass to callback function
     **/
    function fillOrderAsFlashLoan (
        uint256 orderId,
        uint256 inAmount,
        bytes memory data
    ) external returns (uint256 outAmount);

    /**
      * @notice Function to update price curve - how fast {token2sell} price will decrease/increase
      * @param discountType - discount type to change
      * @param newPriceCurve - parameters of formula
     **/
    function setPriceCurve(DiscountType discountType, uint256[2] calldata newPriceCurve) external;

    /* ==================== VIEW METHODS ==================== */
    /**
      * @return The amount of outcome that caller will receive if he will fill {orderId} with {inAmount} of {token2sell}
     **/
    function getExpectedOrderOutcome (uint256 orderId, uint256 inAmount) external view returns (uint256);

    /**
      * @return Order with id = {orderId}
     **/
    function getOrder (uint256 orderId) external view returns (Order memory);

    /**
      * @return The amount of not full-filled orders
     **/
    function getOrdersCount () external view returns (uint256);

    /**
      * @return The array of all not-filled orders. May be deleted after adding GraphQL for orderIds
     **/
    function getAllOrders () external view returns (OrderWithId[] memory);
}
","keccak256":"0x82e1cafa53606d6b49f02ca18891db728c3dfd3c678387e8d4a6e2ccbbda8a1b","license":"BSL 1.1"},"contracts/interfaces/ISuLiquidationMiddleware.sol":{"content":"// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

/* @notice This contract is middleware between our Lending module and Exchange module.
 * This contract is responsible for getting collateral to liquidate from lending module, sell it using exchange module.
 * After sell users with StablePro can swap them to another stablecoin.
 * Also DAO can send profit after profitable liquidations to foundation or get swapped StablePro.
 */
interface ISuLiquidationMiddleware {
    /* ===================== ERRORS ===================== */
    error BadStablecoin(address badStablecoin);
    error NoProfit();
    error NoStablePro();

    /* ===================== EVENTS ===================== */
    event CollateralBorrowed(uint256 debt);
    event StableProSwapped(uint256 amount);

    /* ==================== MUTABLE METHODS ==================== */

    /**
      * @notice Set order to swap collateral to stablecoin to exchange module and store his debt to the SuManager
      * @dev TriggerLiquidation in SuManager need to send collateral to this contract and call this function
      * @param collateral - collateral token address
      * @param amount - The amount of LP-token to be unwrapped
      * @param debt - debt to the SuManager - how much StablePro user borrow using this collateral
     **/
    function borrowCollateralToSell (
        address collateral,
        uint256 amount,
        uint256 debt
    ) external;

    /**
      * @notice Swap users StablePro to Stablecoin that Middleware receive from exchange module after filling order
      * @dev User need to approve StablePro for Middleware
      * @param stablecoinToken - what stablecoin user want to receive
      * @param stablecoinAmountEDecimal - The amount of stablecoinToken to receive
     **/
    function swapStableProToStablecoin(
        address stablecoinToken,
        uint256 stablecoinAmountEDecimal
    ) external;

    /**
      * @notice DAO can send profit after liquidations that accumulates in this contract to foundation
     **/
    function sendProfitToFoundation() external;

    /**
      * @notice After swapping Stablecoin to users StablePro Middleware can send them to DAO,
      * that can send them to liquidity pool, burn them and etc.
     **/
    function sendStableProToDAO() external;

    function addWhitelistStablecoin(address _stablecoin) external;

    function removeWhitelistStablecoin(address _stablecoin) external;

    /* ==================== VIEW METHODS ==================== */

    function hasWhitelistStablecoin(address _stablecoin) external view returns(bool);
}
","keccak256":"0x47e708b7f9d12306cbb71c34ece650f10fb932347c19eb158f10a3e1ba2b4419","license":"BSL 1.1"},"contracts/interfaces/ISuManager.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "./ISuManagerParameters.sol";
import "./ISuManagerHelpers.sol";
import "./ISuManagerLiquidation.sol";

/**
 * @title SuManager
 * @notice Manager mighty over the vault. Allows users to interact with their CDPs.
 * User does only interact with manager as proxy to the vault.
 **/
interface ISuManager is ISuManagerParameters, ISuManagerHelpers, ISuManagerLiquidation {
    /* ===================== EVENTS ===================== */
    /**
     * @notice Even triggered when user deposit collateral
     **/
    event Join(address indexed asset, address indexed owner, uint256 main, uint256 stablecoin);

    /**
     * @notice Event triggered when user withdraws collateral
     **/
    event Exit(address indexed asset, address indexed owner, uint256 main, uint256 stablecoin);

    /* ===================== ERRORS ===================== */
    error Restricted();
    error UselessTransaction();
    error UnsupportedDecimals();
    error UnderCollateralized();
    error SmallCollateral();

    /* ==================== METHODS ==================== */
    /**
     * @notice This function is called by user to deposit collateral and receive stablecoin
     * @dev Before calling this function user has to approve the Vault to take his collateral
     **/
    function join(
        address asset,
        uint256 assetAmountEDecimal,
        uint256 stablecoinAmountE18,
        uint256 lockupPeriodSeconds
    ) external;

    /**
     * @notice User can pay back the stablecoin and take his collateral
     * instead of passing both assetAmount and stablecoinAmount
     * better user just to pass one of them
     * also pass preferred rate and maybe acceptable diff percent
     * that's the purpose of passing both to protect user from rate fluctuations
     **/
    function exit(address asset, uint256 assetAmountEDecimal, uint256 stablecoinAmountE18) external;
}
