pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";

import "../interfaces/ISuManager.sol";
import "../interfaces/ISuLiquidationMiddleware.sol";
import "../exchange/interfaces/IExchange.sol";
import "../periphery/contracts/access-control/SuAuthenticated.sol";
import "../periphery/contracts/interfaces/oracle/ISuOracle.sol";

/**
 * @title SuLiquidationMiddleware
 * @dev See ISuLiquidationMiddleware interface for full details.
 */
contract SuLiquidationMiddleware is SuAuthenticated, ISuLiquidationMiddleware {
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    ISuOracle internal _oracle;
    address  internal _exchangeModule;
    address public foundation;
    address public stablePro;

    // how much StablePro we get from SuManager
    int256 public middlewareDebt;
    EnumerableSetUpgradeable.AddressSet internal _stablecoinWhitelist;

    function initialize(
        address _authControl,
        address _suExchangeModule,
        address _suOracle,
        address _stablePro,
        address _foundation
    ) public initializer {
        __suAuthenticatedInit(_authControl);

        _oracle = ISuOracle(_suOracle);
        _exchangeModule = _suExchangeModule;
        stablePro = _stablePro;
        foundation = _foundation;
    }

    // TODO: make partial liquidation
    function borrowCollateralToSell (
        address collateral,
        uint256 amount,
        uint256 debt
    ) public onlyLiquidationAccess {
        // TODO: create algorithm, how to choose stablecoin
        address stablecoin = EnumerableSetUpgradeable.at(_stablecoinWhitelist, 0);
        middlewareDebt += int256(debt);
        IERC20(collateral).approve(_exchangeModule, amount);

        IExchange(_exchangeModule).addOrder(
            collateral,
            amount,
            stablecoin,
            address(this),
            DiscountType.middle // TODO: should depend on HF
        );

        emit CollateralBorrowed(debt);
    }

    // user need to approve StablePro
    function swapStableProToStablecoin(
        address stablecoinToken,
        uint256 stableProAmountE18
    ) external {
        if (!hasWhitelistStablecoin(stablecoinToken)) revert BadStablecoin(stablecoinToken);

        uint256 stablecoinAmountEDecimal = stableProAmountE18 * 1e18 / _oracle.getFiatPrice1e18(stablecoinToken);

        TransferHelper.safeTransferFrom(stablePro, msg.sender, address(this), stableProAmountE18);
        TransferHelper.safeTransfer(stablecoinToken, msg.sender, stablecoinAmountEDecimal);

        middlewareDebt -= int256(stableProAmountE18);

        emit StableProSwapped(stableProAmountE18);
    }

    function sendProfitToFoundation () public onlyDAO {
        if (middlewareDebt < 0) {
            TransferHelper.safeTransfer(stablePro, foundation, uint256(-middlewareDebt));
        } else {
            revert NoProfit();
        }
    }

    function sendStableProToDAO () public onlyDAO {
        uint256 balance = IERC20Upgradeable(stablePro).balanceOf(address(this));
        if (balance > 0) {
            TransferHelper.safeTransfer(stablePro, msg.sender, balance);
        } else {
            revert NoStablePro();
        }
    }

    function addWhitelistStablecoin(address _stablecoin) public onlyDAO {
        EnumerableSetUpgradeable.add(_stablecoinWhitelist, _stablecoin);
    }

    function removeWhitelistStablecoin(address _stablecoin) public onlyDAO {
        EnumerableSetUpgradeable.remove(_stablecoinWhitelist, _stablecoin);
    }

    function hasWhitelistStablecoin(address _stablecoin) public view returns(bool) {
        return EnumerableSetUpgradeable.contains(_stablecoinWhitelist, _stablecoin);
    }
}
