pragma solidity ^0.8.0;

import "../interfaces/ISafeEngine.sol";
import "../interfaces/ITaxCollector.sol";
import "../../periphery/contracts/access-control/SuAuthenticatedNonUpgradeable.sol";

// FIXME: This contract was altered compared to the production version.
// It doesn't use LibNote anymore.
// New deployments of this contract will need to include custom events (TO DO).

// CHANGED: added interface and moved struct there.
// Added SuAuthenticated instead of authorizedAccounts

contract TaxCollector is ITaxCollector, SuAuthenticatedNonUpgradeable {
    mapping (bytes32 => CollateralType) public collateralTypes;
    ISafeEngine public safeEngine;   // CDP Engine
    address public primaryTaxReceiver;   // Debt Engine
    uint256 public base;  // Global, per-second stability fee contribution [ray]

    // --- Init ---
    constructor(address accessControl, address safeEngine_) SuAuthenticatedNonUpgradeable(accessControl) public {
        safeEngine = ISafeEngine(safeEngine_);
    }

    // --- Disable ---
    uint public contractEnabled;
    function disableContract() external onlyDisablerAccess {
        contractEnabled = 0;
        emit DisableContract();
    }

    // --- Math ---
    function _rpow(uint x, uint n, uint b) internal pure returns (uint z) {
      assembly {
        switch x case 0 {switch n case 0 {z := b} default {z := 0}}
        default {
          switch mod(n, 2) case 0 { z := b } default { z := x }
          let half := div(b, 2)  // for rounding.
          for { n := div(n, 2) } n { n := div(n,2) } {
            let xx := mul(x, x)
            if iszero(eq(div(xx, x), x)) { revert(0,0) }
            let xxRound := add(xx, half)
            if lt(xxRound, xx) { revert(0,0) }
            x := div(xxRound, b)
            if mod(n,2) {
              let zx := mul(z, x)
              if and(iszero(iszero(x)), iszero(eq(div(zx, x), z))) { revert(0,0) }
              let zxRound := add(zx, half)
              if lt(zxRound, zx) { revert(0,0) }
              z := div(zxRound, b)
            }
          }
        }
      }
    }
    uint256 constant ONE = 10 ** 27;
    function _add(uint x, uint y) internal pure returns (uint z) {
        z = x + y;
        require(z >= x);
    }
    function _diff(uint x, uint y) internal pure returns (int z) {
        z = int(x) - int(y);
        require(int(x) >= 0 && int(y) >= 0);
    }
    function _rmul(uint x, uint y) internal pure returns (uint z) {
        z = x * y;
        require(y == 0 || z / y == x);
        z = z / ONE;
    }

    // --- Administration ---
    function initializeCollateralType(bytes32 collateralType) external onlyAdmin {
        CollateralType storage i = collateralTypes[collateralType];
        require(i.stabilityFee == 0, "TaxCollector/collateralType-already-init");
        i.stabilityFee = ONE;
        i.latestUpdateTime  = block.timestamp;
    }
    function modifyParameters(bytes32 collateralType, bytes32 what, uint data) external onlyDAO {
        require(
            block.timestamp == collateralTypes[collateralType].latestUpdateTime,
            "TaxCollector/latestUpdateTime-not-updated"
        );
        if (what == "stabilityFee") collateralTypes[collateralType].stabilityFee = data;
        else revert("TaxCollector/modifyParameters-unrecognized-param");
    }
    function modifyParameters(bytes32 what, uint data) external onlyDAO {
        if (what == "base") base = data;
        else revert("TaxCollector/modifyParameters-unrecognized-param");
    }
    function modifyParameters(bytes32 what, address data) external onlyDAO {
        if (what == "primaryTaxReceiver") primaryTaxReceiver = data;
        else revert("TaxCollector/modifyParameters-unrecognized-param");
    }

    function taxSingle(bytes32 collateralType) external returns (uint rate) {
        require(
            block.timestamp >= collateralTypes[collateralType].latestUpdateTime,
            "TaxCollector/invalid-block-timestamp"
        );
        (, uint prevRate,,,,,,) = safeEngine.collateralTypes(collateralType);
        /// rate = oldRate * (1 + StabilityRate)^(time since last taxSingle)
        rate = _rmul(
            _rpow(
                _add(base, collateralTypes[collateralType].stabilityFee),
                block.timestamp - collateralTypes[collateralType].latestUpdateTime,
                ONE
            ),
            prevRate
        );
        safeEngine.updateAccumulatedRate(collateralType, primaryTaxReceiver, _diff(rate, prevRate));
        collateralTypes[collateralType].latestUpdateTime = block.timestamp;
    }
}

