// SPDX-License-Identifier: BSL 1.1

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "../interfaces/ISuVault.sol";
import "../interfaces/ISuCdpRegistry.sol";
import "./SuCollateralRegistry.sol";

/**
 * @title SuCdpRegistry
 * @notice Here we store protocol collaterals and use it in GraphQL
 * @dev See ISuCdpRegistry interface for full details.
 */
contract SuCdpRegistry is Initializable, SuCollateralRegistry, ISuCdpRegistry {
    // mapping from collateral token to list of borrowers
    mapping (address => address[]) private cdpList;

    // mapping from collateral token to borrower to the cdpList index
    mapping (address => mapping (address => uint256)) private cdpIndex;

    ISuVault public vault;

    function initialize(address _authControl, address _vault) public initializer {
        if (_vault == address(0)) revert ZeroAddress();
        vault = ISuVault(_vault);

        __suCollateralRegistryInit(_authControl);
    }

    // anyone can create checkpoint?
    function checkpoint(address asset, address owner) public {
        if (asset == address(0) || owner == address(0)) revert ZeroAddress();

        bool listed = isListed(asset, owner);
        bool alive = isAlive(asset, owner);

        if (alive && !listed) {
            _addCdp(asset, owner);
        } else if (listed && !alive) {
            _removeCdp(asset, owner);
        }
    }

    function _removeCdp(address asset, address owner) internal {
        // take the index by collateral and borrower
        uint256 id = cdpIndex[asset][owner];

        // then delete this index
        delete cdpIndex[asset][owner];

        // if the index is not the last one
        uint256 lastId = cdpList[asset].length - 1;

        // swap the last element with the element to be deleted
        if (id != lastId) {
            address lastOwner = cdpList[asset][lastId];
            cdpList[asset][id] = lastOwner;
            cdpIndex[asset][lastOwner] = id;
        }

        // delete the last element
        cdpList[asset].pop();

        // can we optimize this remove function by changing the structure?

        emit Removed(asset, owner);
    }

    function _addCdp(address asset, address owner) internal {
        // remember the index of the new element
        cdpIndex[asset][owner] = cdpList[asset].length;

        // add the new element to the end of the list
        cdpList[asset].push(owner);

        emit Added(asset, owner);
    }

    /* ==================== VIEW METHODS ==================== */

    function isAlive(address asset, address owner) public view returns (bool) {
        return vault.debtWithFeesE18(asset, owner) != 0;
    }

    function isListed(address asset, address owner) public view returns (bool) {
        if (cdpList[asset].length == 0) { return false; }
        return cdpIndex[asset][owner] != 0 || cdpList[asset][0] == owner;
    }

    function getCdpsByCollateral(address asset) external view returns (CDP[] memory cdps) {
        address[] memory owners = cdpList[asset];
        cdps = new CDP[](owners.length);
        for (uint16 i = 0; i < owners.length; ++i) {
            cdps[i] = CDP(asset, owners[i]);
        }
    }

    function getCdpsByOwner(address owner) external view returns (CDP[] memory r) {
        CDP[] memory cdps = new CDP[](collaterals.length);
        uint16 actualCdpsCount;

        for (uint16 i = 0; i < collaterals.length; ++i) {
            if (isListed(collaterals[i], owner)) {
                cdps[actualCdpsCount++] = CDP(collaterals[i], owner);
            }
        }

        r = new CDP[](actualCdpsCount);

        for (uint16 i = 0; i < actualCdpsCount; ++i) {
            r[i] = cdps[i];
        }
    }

    function getAllCdps() external view returns (CDP[] memory r) {
        uint256 totalCdpCount = getCdpsCount();

        uint256 cdpCount;

        r = new CDP[](totalCdpCount);

        for (uint16 i = 0; i < collaterals.length; i++) {
            address[] memory owners = cdpList[collaterals[i]];
            for (uint256 j = 0; j < owners.length; j++) {
                r[cdpCount++] = CDP(collaterals[i], owners[j]);
            }
        }
    }

    function getCdpsCount() public view returns (uint256 totalCdpCount) {
        totalCdpCount = 0;
        for (uint16 i = 0; i < collaterals.length; ++i) {
            totalCdpCount += cdpList[collaterals[i]].length;
        }
    }

    function getCdpsCountForCollateral(address asset) public view returns (uint256) {
        return cdpList[asset].length;
    }
}

contracts/lending/SuCollateralRegistry.s.content:
// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "./SuVaultParameters.sol";
import "../interfaces/ISuCollateralRegistry.sol";
import "../periphery/contracts/access-control/SuAuthenticated.sol";

/**
 * @title SuCollateralRegistry
 * @dev See ISuCollateralRegistry interface for full details.
 */
abstract contract SuCollateralRegistry is SuAuthenticated, ISuCollateralRegistry {
    mapping(address => CollateralRegistryInfo) public collateralInfo;
    address[] public collaterals;

    function __suCollateralRegistryInit(address _authControl) internal {
        __suAuthenticatedInit(_authControl);
    }

    function addCollateral(address asset) public onlyDAO {
        if (asset == address(0)) revert ZeroAddress();

        if (isCollateral(asset) || collateralInfo[asset].isDisabled) revert AlreadyExist();

        collateralInfo[asset] = CollateralRegistryInfo({ index: collaterals.length, isDisabled: false });
        collaterals.push(asset);

        emit CollateralAdded(asset);
    }

    function disableCollateral(address asset) public onlyDAO {
        if (collateralInfo[asset].isDisabled) revert AlreadyDisabled();
        if (!isCollateral(asset)) revert NotExist();

        collateralInfo[asset].isDisabled = true;

        emit CollateralDisabled(asset);
    }

    function enableCollateral(address asset) public onlyDAO {
        if (!collateralInfo[asset].isDisabled) {
            if (isCollateral(asset)) {
                revert AlreadyEnabled();
            } else {
                revert NotExist();
            }
        }

        collateralInfo[asset].isDisabled = false;

        emit CollateralEnabled(asset);
    }

    function isCollateral(address asset) public view returns(bool) {
        if (collaterals.length == 0) { return false; }

        return !collateralInfo[asset].isDisabled && (collateralInfo[asset].index != 0 || collaterals[0] == asset);
    }
}

contracts/lending/SuVault.sol.content:
// SPDX-License-Identifier: BSL 1.1

/*
  Copyright 2020 Unit Protocol: Artem Zakharov (az@unit.xyz).
*/
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "./SuVaultParameters.sol";
import "../StablePro.sol";
import "../reward/RewardChefV2.sol";
import "../interfaces/ISuVault.sol";
import "../emergency/SuPausableAbstract.sol";

/**
 * @title SuVault
 * @notice Users don't have direct access to it, only through SuManager
 * @dev See ISuVault interface and SuVaultParameters contract for full details.
 */
contract SuVault is SuVaultParameters, SuPausableAbstract, ISuVault  {
    StablePro public STABLE_PRO;

    mapping(address => mapping(address => uint256)) public collateralsEDecimal;
    mapping(address => mapping(address => uint256)) public debtWithFeesE18;
    mapping(address => mapping(address => uint256)) public debtsE18;
    mapping(address => uint256) public tokenDebtsWithFeesE18;
    mapping(address => uint256) public tokenDebtsE18;
    mapping(address => mapping(address => uint256)) public stabilityFeeE18;
    mapping(address => mapping(address => uint256)) public lastUpdate;
    mapping(address => mapping(address => uint256)) public liquidationBlock;

    /**
     * @notice check if liquidation process not started for asset of user
     **/
    modifier notLiquidating(address asset, address user) {
        if (liquidationBlock[asset][user] > 0) revert LiquidationIsTriggered();
        _;
    }

    RewardChefV2 public rewardChef;

    /**
     * @notice vault is initialize with parameters for auth (we are using OZ instead)
     * and it accept address for wrapped eth, main stable coin, and probably governance token
     **/
    function initialize(
        address _authControl,
        address _stablePro,
        address _foundation,
        address _rewardChef,
        address _suPausable
    ) public initializer {
        __suVaultParametersInit(_authControl, payable(this), _foundation);
        __suPausableAbstractInit(_suPausable);

        STABLE_PRO = StablePro(_stablePro);
        rewardChef = RewardChefV2(_rewardChef);
    }

    /**
     * @notice do not accept direct payments from users because they will be stuck on contract address
     **/
    receive() external payable {
        revert Restricted();
    }

    function update(
        address asset,
        address user
    ) public onlyVaultAccess notLiquidating(asset, user) whenNotPaused(asset) {
        // probably should be checked if zero then skip
        uint256 newDebtWithFeesE18 = getTotalDebtE18(asset, user);

        tokenDebtsWithFeesE18[asset] = tokenDebtsWithFeesE18[asset] - debtWithFeesE18[asset][user] + newDebtWithFeesE18;
        debtWithFeesE18[asset][user] = newDebtWithFeesE18;
        stabilityFeeE18[asset][user] = protocolStabilityFeeE18[asset];
        lastUpdate[asset][user] = block.timestamp;
    }

    // public?
    function destroy(address asset, address user) public onlyVaultAccess whenNotPaused(asset) {
        delete stabilityFeeE18[asset][user];
        delete lastUpdate[asset][user];
        delete liquidationBlock[asset][user];
    }

    function deposit(
        address asset,
        address user,
        uint256 amountEDecimal
    ) external onlyVaultAccess notLiquidating(asset, user) whenNotPaused(asset) {
        SafeERC20Upgradeable.safeTransferFrom(IERC20Upgradeable(asset), user, address(this), amountEDecimal);
        collateralsEDecimal[asset][user] = collateralsEDecimal[asset][user] + amountEDecimal;
    }

    function withdraw(
        address asset,
        address user,
        address recipient,
        uint256 amountEDecimal
    ) public onlyVaultAccess whenNotPaused(asset) {
        if (amountEDecimal > collateralsEDecimal[asset][user]) revert WithdrawExceeded();
        collateralsEDecimal[asset][user] = collateralsEDecimal[asset][user] - amountEDecimal;
        if (recipient != address(0)) {
            SafeERC20Upgradeable.safeTransfer(IERC20Upgradeable(asset), recipient, amountEDecimal);
        }
    }

    // TODO: what role should we have? Need whenNotPaused(asset)?
    function emergencyWithdraw(
        address asset,
        address user,
        uint256 amountEDecimal
    ) external onlyVaultAccess whenNotPaused(asset) {
        if (amountEDecimal > collateralsEDecimal[asset][user]) revert WithdrawExceeded();
        collateralsEDecimal[asset][user] = collateralsEDecimal[asset][user] - amountEDecimal;
        SafeERC20Upgradeable.safeTransfer(IERC20Upgradeable(asset), user, amountEDecimal);

        // solhint-disable-next-line no-empty-blocks
        try rewardChef.resetAmount(asset, user) {} catch {}
    }

    function borrow(
        address asset,
        address user,
        uint256 amountE18,
        uint256 lockupPeriodSeconds
    ) external onlyVaultAccess notLiquidating(asset, user) whenNotPaused(asset) {
        update(asset, user);

        debtsE18[asset][user] = debtsE18[asset][user] + amountE18;
        debtWithFeesE18[asset][user] = debtWithFeesE18[asset][user] + amountE18;
        tokenDebtsE18[asset] = tokenDebtsE18[asset] + amountE18;
        tokenDebtsWithFeesE18[asset] = tokenDebtsWithFeesE18[asset] + amountE18;

        if (tokenDebtsWithFeesE18[asset] > tokenDebtLimitE18[asset]) revert AssetDebtLimit();

        STABLE_PRO.mint(user, amountE18);
        rewardChef.increaseAmount(asset, user, amountE18, lockupPeriodSeconds);
    }

    function _cutDebt(address asset, address user, uint256 stablecoinAmountE18) internal onlyVaultAccess {
        if (stablecoinAmountE18 > debtWithFeesE18[asset][user]) revert WrongDebt();
        if (stablecoinAmountE18 > tokenDebtsWithFeesE18[asset]) revert WrongTotalDebt();

        uint256 fee = stablecoinAmountE18 > debtsE18[asset][user] ? stablecoinAmountE18 - debtsE18[asset][user] : 0;

        // current debt of user by given collateral
        // is being decreased by chosen amount
        debtWithFeesE18[asset][user] = debtWithFeesE18[asset][user] - stablecoinAmountE18;
        // we don't need to count fee here
        debtsE18[asset][user] = debtsE18[asset][user] + fee - stablecoinAmountE18;

        // total debt by asset is being decreased too
        // this value is used to limit total collateral allowed debt
        tokenDebtsWithFeesE18[asset] = tokenDebtsWithFeesE18[asset] - stablecoinAmountE18;
        tokenDebtsE18[asset] = tokenDebtsE18[asset] + fee - stablecoinAmountE18;
        rewardChef.decreaseAmount(asset, user, stablecoinAmountE18 - fee);
    }

    function liquidate(
        address asset,
        address user,
        address recipient,
        uint256 assetAmountEDecimal,
        uint256 stablecoinAmountE18
    ) external onlyVaultAccess whenNotPaused(asset) {
        if (assetAmountEDecimal == 0 && stablecoinAmountE18 == 0) revert UselessTransaction();

        // reduce debt and don't repay, stablecoinAmount == 0 in case of withdraw
        if (stablecoinAmountE18 != 0) {
            _cutDebt(asset, user, stablecoinAmountE18);
        }

        // vault will transfer collateral to the user
        if (assetAmountEDecimal != 0) {
            withdraw(asset, user, recipient, assetAmountEDecimal);
        }

        // clean state
        if (debtWithFeesE18[asset][user] == 0) {
            destroy(asset, user);
            emit PositionLiquidated(asset, user, recipient, assetAmountEDecimal, stablecoinAmountE18);
        } else {
            emit PositionLiquidatedPartial(asset, user, recipient, assetAmountEDecimal, stablecoinAmountE18);
        }
    }

    function triggerLiquidation(
        address asset,
        address positionOwner
    ) external onlyVaultAccess notLiquidating(asset, positionOwner) whenNotPaused(asset) {
        emit LiquidationTriggered(asset, positionOwner);

        liquidationBlock[asset][positionOwner] = block.number;
    }

    function getTotalDebtE18(address asset, address user) public view returns (uint256) {
        uint256 debtWithFeeE18 = debtWithFeesE18[asset][user];
        uint256 feeE18 = calculateFeeE18(asset, user, debtWithFeeE18);
        // debtWithFee before lastUpdate + fee after lastUpdate
        return debtWithFeeE18 + feeE18;
    }

    function calculateFeeE18(address asset, address user, uint256 amountE18) public view returns (uint256) {
        uint256 sFeePercentE18 = stabilityFeeE18[asset][user];
        uint256 timePast = block.timestamp - lastUpdate[asset][user];

        return amountE18 * sFeePercentE18 * timePast / (365 days) / 1e18;
    }

    function payFeeAndBurn(
        address repayer,
        uint256 stablecoinsToRepaymentE18,
        uint256 stablecoinsToFoundationE18
    ) external onlyVaultAccess {
        emit PositionRepaid(repayer, stablecoinsToRepaymentE18, stablecoinsToFoundationE18);

        // the repayer transfers fees and excesses over the repayment
        if (stablecoinsToFoundationE18 != 0) {
            SafeERC20Upgradeable.safeTransferFrom(STABLE_PRO, repayer, foundation, stablecoinsToFoundationE18);
        }

        // we burn stablecoin from user
        STABLE_PRO.burn(repayer, stablecoinsToRepaymentE18);
    }

    function setRewardChef(address _rewardChef) public onlyVaultAccess {
        rewardChef = RewardChefV2(_rewardChef);
    }
}

contracts/lending/SuVaultParameters.sol.content:
// SPDX-License-Identifier: BSL 1.1

import "../interfaces/ISuVaultParameters.sol";
import "../periphery/contracts/access-control/SuAuthenticated.sol";

pragma solidity ^0.8.0;

// TODO: merge with ManagerParameters
/**
 * @title SuVaultParameters
 * @dev See ISuVaultParameters interface for full details.
 */
abstract contract SuVaultParameters is SuAuthenticated, ISuVaultParameters {
    mapping(address => uint256) public protocolStabilityFeeE18;
    mapping(address => uint256) public tokenDebtLimitE18;
    address public foundation;
    address public vault;

    // how can vault address be known at this moment?
    // Precult based on CREATE spec
    // can be created another function to set vault address once deployed?
    // Yes, possibly with some logic change
    function __suVaultParametersInit(
        address _authControl,
        address payable _vault,
        address _foundation
    )  internal onlyInitializing {
        if (_vault == address(0) || _foundation == address(0)) revert ZeroAddress();

        __suAuthenticatedInit(_authControl);
        vault = _vault;
        foundation = _foundation;
    }

    function setFoundation(address newFoundation) external onlyDAO {
        if (newFoundation == address(0)) revert ZeroAddress();
        foundation = newFoundation;
    }

    // is called by Manager
    function setCollateral(
        address asset,
        uint256 stabilityFeeValueE18,
        uint256 stablecoinLimitE18
    ) external onlyVaultAccess {
        // stability fee should be validated in range, what is stability fee should be described here?
        setStabilityFeeE18(asset, stabilityFeeValueE18);
        // why debt limit for collateral is necessary? to manage risks in case of collateral failure?
        setTokenDebtLimitE18(asset, stablecoinLimitE18);
    }

    function setStabilityFeeE18(address asset, uint256 newValue) public onlyVaultAccess {
        protocolStabilityFeeE18[asset] = newValue;
    }

    function setTokenDebtLimitE18(address asset, uint256 limit) public onlyVaultAccess {
        tokenDebtLimitE18[asset] = limit;
    }
}

