pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";

import {Math} from "../utils/Math.sol";
import "../interfaces/IAccountingEngine.sol";
import "../interfaces/ISafeEngine.sol";
import "../interfaces/ICoinJoin.sol";
import "../../interfaces/IRewardChefV2.sol";
import "../../periphery/contracts/interfaces/oracle/ISuOracle.sol";
import "../../periphery/contracts/access-control/SuAuthenticatedNonUpgradeable.sol";
import "../../periphery/contracts/interfaces/oracle/ISuOracleAggregator.sol";

// FIXME: This contract was altered compared to the production version.
// It doesn't use LibNote anymore.
// New deployments of this contract will need to include custom events (TO DO).
// Changed the way how we work with Auctions - we use Exchange module.
// Also we added new manual functions to work with rewardChef, stablecoinWhitelist, coinJoin and transfer/swaps.

contract AccountingEngine is SuAuthenticatedNonUpgradeable, Math, IAccountingEngine {
    // --- Data ---
    ISafeEngine public immutable safeEngine;   // CDP Engine
    IExchange public exchange;       // Exchange module
    ISuOracleAggregator public oracle;         // Oracle
    IERC20 public suDAO;             // Protocol governance token
    IERC20 public immutable stablePro; // System coin
    IRewardChefV2 public rewardChef; // RewardModule
    ICoinJoin public coinJoin;       // Adapter to join StablePro

    mapping(address => bool) internal stablecoinWhitelist;
    address public stablecoinToSwap; // Stablecoin to which the collateral will be exchanged during liquidation

    mapping (uint256 => uint256) public debtQueue;  // debt queue
    uint256 public totalQueuedDebt;   // Queued debt            [rad]
    uint256 public totalOnAuctionDebt;   // On-auction debt        [rad]

    uint256 public popDebtDelay;  // DebtAuctionHouse delay             [seconds]

    uint256 public surplusBuffer;  // Surplus buffer         [rad]

    uint256 public contractEnabled;  // Active Flag

    uint256 public defaultRewardEndBlock;  // Active Flag

    // --- Init ---
    constructor(
        address accessControl_,
        address safeEngine_,
        address exchange_,
        address oracle_,
        address suDAO_,
        address rewardChef_,
        address stablePro_,
        address coinJoin_
    ) SuAuthenticatedNonUpgradeable(accessControl_) public {
        safeEngine = ISafeEngine(safeEngine_);
        exchange = IExchange(exchange_);
        oracle = ISuOracleAggregator(oracle_);
        suDAO = IERC20(suDAO_);
        stablePro = IERC20(stablePro_);
        rewardChef = IRewardChefV2(rewardChef_);
        coinJoin = ICoinJoin(coinJoin_);

        safeEngine.approveSAFEModification(coinJoin_);
        contractEnabled = 1;
        defaultRewardEndBlock = 172800; // 1 MONTH

        if (rewardChef_ != address(0)) {
            suDAO.approve(rewardChef_, type(uint256).max); // to call refillReward
        }
    }

    // --- Administration ---
    function modifyParameters(bytes32 what, uint data) external onlyDAO {
        if (what == "popDebtDelay") popDebtDelay = data;
        else if (what == "surplusBuffer") surplusBuffer = data;
        else if (what == "defaultRewardEndBlock") defaultRewardEndBlock = data;
        else revert BadModifyParameters();
    }

    function modifyParameters(bytes32 what, address data) external onlyDAO {
        if (what == "exchange") exchange = IExchange(data);
        if (what == "oracle") oracle = ISuOracleAggregator(data);
        if (what == "suDAO") suDAO = IERC20(suDAO);
        if (what == "coinJoin") coinJoin = ICoinJoin(data);
        if (what == "rewardChef") {
            rewardChef = IRewardChefV2(data);
            suDAO.approve(address(rewardChef), type(uint256).max); // to call refillReward
        } else revert BadModifyParameters();
    }

    function pushDebtToQueue(uint amountToRaise) external onlyLiquidationAccess {
        debtQueue[block.timestamp] = _add(debtQueue[block.timestamp], amountToRaise);
        totalQueuedDebt = _add(totalQueuedDebt, amountToRaise);
    }

    function popDebtFromQueue(uint era) external {
        if (_add(era, popDebtDelay) > block.timestamp) revert PopDebtDelayNotFinished();
        totalQueuedDebt = _sub(totalQueuedDebt, debtQueue[era]);
        debtQueue[era] = 0;
    }

    // Debt settlement
    function settleDebt(uint rad) external {
        if (rad > safeEngine.coinBalance(address(this))) revert InsufficientSurplus();
        if (rad > _sub(_sub(safeEngine.debtBalance(address(this)), totalQueuedDebt), totalOnAuctionDebt)) revert InsufficientDebt();
        safeEngine.settleDebt(rad);
    }

    function cancelAuctionedDebtWithSurplus(uint rad) external {
        if (rad > totalOnAuctionDebt) revert NotEnoughAsh();
        if (rad > safeEngine.coinBalance(address(this))) revert InsufficientSurplus();
        totalOnAuctionDebt = _sub(totalOnAuctionDebt, rad);
        safeEngine.settleDebt(rad);
    }

    function auctionDebt(address token2sell, uint256 amountToSell, uint8 discountType, uint256 auctionDebt) external onlyAdmin returns (uint orderId) {
        uint256 sellValue = oracle.getSafeFiatPrice1e18(token2sell) * amountToSell / 1e18;
        if (safeEngine.coinBalance(address(this)) != 0) revert SurplusNotZero();

        totalOnAuctionDebt = _add(totalOnAuctionDebt, auctionDebt);

        orderId = exchange.addOrder(
            token2sell, // we want to sell token2sell
            amountToSell,
            address(stablePro), // token2buy is stablecoin
            address(this), // beneficiary is the AccountingEngine itself
            discountType, // or whatever discount type you want to use
            address(0) // no collateral join, since it's a direct debt sale
        );
    }

    function auctionSurplus(uint256 surplusAmount, uint8 discountType) external onlyAdmin returns (uint orderId) {
        uint256 coinBalance = safeEngine.coinBalance(address(this));
        uint256 debtBalance = safeEngine.debtBalance(address(this));
        uint256 realBalance = IERC20(stablePro).balanceOf(address(this));
        if (
            coinBalance < surplusBuffer ||
            _add(coinBalance, realBalance * 1e27) < _add(_add(debtBalance, surplusAmount * 1e27), surplusBuffer)
        ) revert InsufficientSurplus();
        if (_sub(_sub(debtBalance, totalQueuedDebt), totalOnAuctionDebt) != 0) revert DebtNotZero();
        // Auction some system coins in return for protocol tokens that are then burnt
        orderId = exchange.addOrder(
            address(stablePro), // we sell StablePro
            surplusAmount,
            address(suDAO), // we buy SuDAO (governance token)
            address(this), // beneficiary is the AccountingEngine itself
            discountType, // or whatever discount type you want to use
            address(0) // no collateral join, since it's a direct surplus sale
        );
        // After filling we can call distributeSurplus();
    }

    function disableContract() external onlyDisablerAccess {
        if (contractEnabled == 0) revert ContractDisabled();
        contractEnabled = 0;
        totalQueuedDebt = 0;
        totalOnAuctionDebt = 0;
        safeEngine.settleDebt(min(safeEngine.coinBalance(address(this)), safeEngine.debtBalance(address(this))));
    }

    // --- Manual functions ---
    function joinStablePro(uint256 amount) external onlyAdmin {
        totalOnAuctionDebt = totalOnAuctionDebt < amount ? 0 : _sub(totalOnAuctionDebt, amount);
        coinJoin.joinNoReward(address(this), amount);
    }

    function getRealStablePro(uint256 amount) external onlyAdmin {
        coinJoin.exitNoReward(address(this), amount);
    }

    function refillStablecoin(address stablecoin, uint256 amount) external {
        TransferHelper.safeTransferFrom(stablecoin, msg.sender, address(this), amount);
        emit StablecoinRefilled(stablecoin, amount);
    }

    function distributeSurplus(uint256 amount) external onlyAdmin {
        if (address(rewardChef.REWARD_TOKEN()) != address(suDAO)) revert BadRewardToken();
        uint256 rewardBlock = rewardChef.rewardEndBlock();
        rewardChef.refillReward(
            amount,
            rewardBlock > 0 ? uint64(rewardBlock) : uint64(block.number + defaultRewardEndBlock)
        );
    }

    function swapStableProToStablecoin(address stablecoinToken, uint256 stableProAmountE18) public {
        if (!stablecoinWhitelist[stablecoinToken]) revert BadStablecoin(stablecoinToken);

        uint256 stablecoinAmountEDecimal = stableProAmountE18 * 1e18 / oracle.getFiatPrice1e18(stablecoinToken);

        TransferHelper.safeTransferFrom(address(stablePro), msg.sender, address(this), stableProAmountE18);
        TransferHelper.safeTransfer(stablecoinToken, msg.sender, stablecoinAmountEDecimal);

        emit StableProSwapped(stableProAmountE18);
    }

    // --- Stablecoin Whitelist ---
    function addWhitelistStablecoin(address stablecoin) external onlyDAO {
        if (stablecoinWhitelist[stablecoin]) revert StablecoinExists(stablecoin);
        stablecoinWhitelist[stablecoin] = true;
        TransferHelper.safeApprove(stablecoin, address(exchange), type(uint256).max);
    }

    function removeWhitelistStablecoin(address stablecoin) external onlyDAO {
        if (!stablecoinWhitelist[stablecoin]) revert StablecoinNotExist(stablecoin);
        stablecoinWhitelist[stablecoin] = false;
        TransferHelper.safeApprove(stablecoin, address(exchange), 0);
    }

    function hasWhitelistStablecoin(address stablecoin) external view returns (bool) {
        return stablecoinWhitelist[stablecoin];
    }

    function setStablecoinToSwap(address stablecoin) external onlyAdmin {
        if (!stablecoinWhitelist[stablecoin]) revert BadStablecoin(stablecoin);
        stablecoinToSwap = stablecoin;
    }
}

