contract Common {
    uint256 constant RAY = 10 ** 27;

    // Internal functions

    function mul(uint x, uint y) internal pure returns (uint z) {
        require(y == 0 || (z = x * y) / y == x, "mul-overflow");
    }

    // Public functions

    function coinJoin_join(address apt, address safe, uint wad) public {
        // Gets DAI from the user's wallet
        CoinJoinLike(apt).systemCoin().transferFrom(msg.sender, address(this), wad);
        // Approves adapter to take the DAI amount
        CoinJoinLike(apt).systemCoin().approve(apt, wad);
        // Joins DAI into the safeEngine
        CoinJoinLike(apt).join(safe, wad);
    }
}

contract DssProxyActions is Common {
    // Internal functions

    function sub(uint x, uint y) internal pure returns (uint z) {
        require((z = x - y) <= x, "sub-overflow");
    }

    function toInt(uint x) internal pure returns (int y) {
        y = int(x);
        require(y >= 0, "int-overflow");
    }

    function toRad(uint wad) internal pure returns (uint rad) {
        rad = mul(wad, 10 ** 27);
    }

    function convertTo18(address collateralJoin, uint256 amt) internal returns (uint256 wad) {
        // For those collaterals that have less than 18 decimals precision we need to do the conversion before passing to modifySAFECollateralization function
        // Adapters will automatically handle the difference of precision
        wad = mul(
            amt,
            10 ** (18 - CollateralJoinLike(collateralJoin).decimals())
        );
    }

    function _getGeneratedDeltaDebt(
        address safeEngine,
        address taxCollector,
        address safe,
        bytes32 collateralType,
        uint wad
    ) internal returns (int deltaDebt) {
        // Updates stability fee rate
        uint rate = taxCollectorLike(taxCollector).taxSingle(collateralType);

        // Gets DAI balance of the safe in the safeEngine
        uint dai = SafeEngineLike(safeEngine).coinBalance(safe);

        // If there was already enough DAI in the safeEngine balance, just exits it without adding more debt
        if (dai < mul(wad, RAY)) {
            // Calculates the needed deltaDebt so together with the existing dai in the safeEngine is enough to exit wad amount of DAI tokens
            deltaDebt = toInt(sub(mul(wad, RAY), dai) / rate);
            // This is neeeded due lack of precision. It might need to sum an extra deltaDebt wei (for the given DAI wad amount)
            deltaDebt = mul(uint(deltaDebt), rate) < mul(wad, RAY) ? deltaDebt + 1 : deltaDebt;
        }
    }

    function _getRepaidDeltaDebt(
        address safeEngine,
        uint dai,
        address safe,
        bytes32 collateralType
    ) internal view returns (int deltaDebt) {
        // Gets actual rate from the safeEngine
        (, uint rate,,,) = SafeEngineLike(safeEngine).collateralTypes(collateralType);
        // Gets actual safeDebt value of the safe
        (, uint safeDebt) = SafeEngineLike(safeEngine).safes(collateralType, safe);

        // Uses the whole dai balance in the safeEngine to reduce the debt
        deltaDebt = toInt(dai / rate);
        // Checks the calculated deltaDebt is not higher than safe.safeDebt (total debt), otherwise uses its value
        deltaDebt = uint(deltaDebt) <= safeDebt ? - deltaDebt : - toInt(safeDebt);
    }

    function _getWipeAllWad(
        address safeEngine,
        address usr,
        address safe,
        bytes32 collateralType
    ) internal view returns (uint wad) {
        // Gets actual rate from the safeEngine
        (, uint rate,,,) = SafeEngineLike(safeEngine).collateralTypes(collateralType);
        // Gets actual safeDebt value of the safe
        (, uint safeDebt) = SafeEngineLike(safeEngine).safes(collateralType, safe);
        // Gets actual dai amount in the safe
        uint dai = SafeEngineLike(safeEngine).coinBalance(usr);

        uint rad = sub(mul(safeDebt, rate), dai);
        wad = rad / RAY;

        // If the rad precision has some dust, it will need to request for 1 extra wad wei
        wad = mul(wad, RAY) < rad ? wad + 1 : wad;
    }

    // Public functions

    function transfer(address gem, address dst, uint amt) public {
        CoinLike(gem).transfer(dst, amt);
    }

    function ethJoin_join(address apt, address safe) public payable {
        // Wraps ETH in WETH
        CollateralJoinLike(apt).collateral().deposit.value(msg.value)();
        // Approves adapter to take the WETH amount
        CollateralJoinLike(apt).collateral().approve(address(apt), msg.value);
        // Joins WETH collateral into the safeEngine
        CollateralJoinLike(apt).join(safe, msg.value);
    }

    function collateralJoin_join(address apt, address safe, uint amt, bool transferFrom) public {
        // Only executes for tokens that have approval/transferFrom implementation
        if (transferFrom) {
            // Gets token from the user's wallet
            CollateralJoinLike(apt).collateral().transferFrom(msg.sender, address(this), amt);
            // Approves adapter to take the token amount
            CollateralJoinLike(apt).collateral().approve(apt, amt);
        }
        // Joins token collateral into the safeEngine
        CollateralJoinLike(apt).join(safe, amt);
    }

    function approveSAFEModification(
        address obj,
        address usr
    ) public {
        SafeModificationLike(obj).approveSAFEModification(usr);
    }

    function denySAFEModification(
        address obj,
        address usr
    ) public {
        SafeModificationLike(obj).denySAFEModification(usr);
    }

    function openSafe(
        address manager,
        bytes32 collateralType,
        address usr
    ) public returns (uint cdp) {
        cdp = ManagerLike(manager).openSafe(collateralType, usr);
    }

    function transferSafeOwnership(
        address manager,
        uint cdp,
        address usr
    ) public {
        ManagerLike(manager).transferSafeOwnership(cdp, usr);
    }

    function transferSafeOwnershipToProxy(
        address proxyRegistry,
        address manager,
        uint cdp,
        address dst
    ) public {
        // Gets actual proxy address
        address proxy = ProxyRegistryLike(proxyRegistry).proxies(dst);
        // Checks if the proxy address already existed and dst address is still the owner
        if (proxy == address(0) || ProxyLike(proxy).owner() != dst) {
            uint csize;
            assembly {
                csize := extcodesize(dst)
            }
            // We want to avoid creating a proxy for a contract address that might not be able to handle proxies, then losing the CDP
            require(csize == 0, "Dst-is-a-contract");
            // Creates the proxy for the dst address
            proxy = ProxyRegistryLike(proxyRegistry).build(dst);
        }
        // Transfers CDP to the dst proxy
        transferSafeOwnership(manager, cdp, proxy);
    }

    function transferCollateral(
        address manager,
        uint cdp,
        address dst,
        uint wad
    ) public {
        ManagerLike(manager).transferCollateral(cdp, dst, wad);
    }

    function transferInternalCoins(
        address manager,
        uint cdp,
        address dst,
        uint rad
    ) public {
        ManagerLike(manager).transferInternalCoins(cdp, dst, rad);
    }

    function modifySAFECollateralization(
        address manager,
        uint cdp,
        int deltaCollateral,
        int deltaDebt
    ) public {
        ManagerLike(manager).modifySAFECollateralization(cdp, deltaCollateral, deltaDebt);
    }

    function lockETH(
        address manager,
        address ethJoin,
        uint cdp
    ) public payable {
        // Receives ETH amount, converts it to WETH and joins it into the safeEngine
        ethJoin_join(ethJoin, address(this));
        // Locks WETH amount into the CDP
        SafeEngineLike(ManagerLike(manager).safeEngine()).modifySAFECollateralization(
            ManagerLike(manager).collateralTypes(cdp),
            ManagerLike(manager).safes(cdp),
            address(this),
            address(this),
            toInt(msg.value),
            0
        );
    }

