    function freeETH(
        address manager,
        address ethJoin,
        uint cdp,
        uint wad
    ) public {
        // Unlocks WETH amount from the CDP
        modifySAFECollateralization(manager, cdp, -toInt(wad), 0);
        // Moves the amount from the CDP safe to proxy's address
        transferCollateral(manager, cdp, address(this), wad);
        // Exits WETH amount to proxy address as a token
        CollateralJoinLike(ethJoin).exit(address(this), wad);
        // Converts WETH to ETH
        CollateralJoinLike(ethJoin).collateral().withdraw(wad);
        // Sends ETH back to the user's wallet
        msg.sender.transfer(wad);
    }

    function freeTokenCollateral(
        address manager,
        address collateralJoin,
        uint cdp,
        uint amt
    ) public {
        uint wad = convertTo18(collateralJoin, amt);
        // Unlocks token amount from the CDP
        modifySAFECollateralization(manager, cdp, -toInt(wad), 0);
        // Moves the amount from the CDP safe to proxy's address
        transferCollateral(manager, cdp, address(this), wad);
        // Exits token amount to the user's wallet as a token
        CollateralJoinLike(collateralJoin).exit(msg.sender, amt);
    }

    function exitETH(
        address manager,
        address ethJoin,
        uint cdp,
        uint wad
    ) public {
        // Moves the amount from the CDP safe to proxy's address
        transferCollateral(manager, cdp, address(this), wad);

        // Exits WETH amount to proxy address as a token
        CollateralJoinLike(ethJoin).exit(address(this), wad);
        // Converts WETH to ETH
        CollateralJoinLike(ethJoin).collateral().withdraw(wad);
        // Sends ETH back to the user's wallet
        msg.sender.transfer(wad);
    }

    function exitTokenCollateral(
        address manager,
        address collateralJoin,
        uint cdp,
        uint amt
    ) public {
        // Moves the amount from the CDP safe to proxy's address
        transferCollateral(manager, cdp, address(this), convertTo18(collateralJoin, amt));

        // Exits token amount to the user's wallet as a token
        CollateralJoinLike(collateralJoin).exit(msg.sender, amt);
    }

    function generateDebt(
        address manager,
        address taxCollector,
        address coinJoin,
        uint cdp,
        uint wad
    ) public {
        address safe = ManagerLike(manager).safes(cdp);
        address safeEngine = ManagerLike(manager).safeEngine();
        bytes32 collateralType = ManagerLike(manager).collateralTypes(cdp);
        // Generates debt in the CDP
        modifySAFECollateralization(manager, cdp, 0, _getGeneratedDeltaDebt(safeEngine, taxCollector, safe, collateralType, wad));
        // Moves the DAI amount (balance in the safeEngine in rad) to proxy's address
        transferInternalCoins(manager, cdp, address(this), toRad(wad));
        // Allows adapter to access to proxy's DAI balance in the safeEngine
        if (SafeEngineLike(safeEngine).cdpRights(address(this), address(coinJoin)) == 0) {
            SafeEngineLike(safeEngine).approveSAFEModification(coinJoin);
        }
        // Exits DAI to the user's wallet as a token
        CoinJoinLike(coinJoin).exit(msg.sender, wad);
    }

    function repayDebt(
        address manager,
        address coinJoin,
        uint cdp,
        uint wad
    ) public {
        address safeEngine = ManagerLike(manager).safeEngine();
        address safe = ManagerLike(manager).safes(cdp);
        bytes32 collateralType = ManagerLike(manager).collateralTypes(cdp);

        address own = ManagerLike(manager).owns(cdp);
        if (own == address(this) || ManagerLike(manager).cdpCan(own, cdp, address(this)) == 1) {
            // Joins DAI amount into the safeEngine
            coinJoin_join(coinJoin, safe, wad);
            // Paybacks debt to the CDP
            modifySAFECollateralization(manager, cdp, 0, _getRepaidDeltaDebt(safeEngine, SafeEngineLike(safeEngine).coinBalance(safe), safe, collateralType));
        } else {
            // Joins DAI amount into the safeEngine
            coinJoin_join(coinJoin, address(this), wad);
            // Paybacks debt to the CDP
            SafeEngineLike(safeEngine).modifySAFECollateralization(
                collateralType,
                safe,
                address(this),
                address(this),
                0,
                _getRepaidDeltaDebt(safeEngine, wad * RAY, safe, collateralType)
            );
        }
    }

