    function repayAllDebt(
        address manager,
        address coinJoin,
        uint cdp
    ) public {
        address safeEngine = ManagerLike(manager).safeEngine();
        address safe = ManagerLike(manager).safes(cdp);
        bytes32 collateralType = ManagerLike(manager).collateralTypes(cdp);
        (, uint safeDebt) = SafeEngineLike(safeEngine).safes(collateralType, safe);

        address own = ManagerLike(manager).owns(cdp);
        if (own == address(this) || ManagerLike(manager).cdpCan(own, cdp, address(this)) == 1) {
            // Joins DAI amount into the safeEngine
            coinJoin_join(coinJoin, safe, _getWipeAllWad(safeEngine, safe, safe, collateralType));
            // Paybacks debt to the CDP
            modifySAFECollateralization(manager, cdp, 0, -int(safeDebt));
        } else {
            // Joins DAI amount into the safeEngine
            coinJoin_join(coinJoin, address(this), _getWipeAllWad(safeEngine, address(this), safe, collateralType));
            // Paybacks debt to the CDP
            SafeEngineLike(safeEngine).modifySAFECollateralization(
                collateralType,
                safe,
                address(this),
                address(this),
                0,
                -int(safeDebt)
            );
        }
    }

