    function lockETHAndGenerateDebt(
        address manager,
        address taxCollector,
        address ethJoin,
        address coinJoin,
        uint cdp,
        uint wadD
    ) public payable {
        address safe = ManagerLike(manager).safes(cdp);
        address safeEngine = ManagerLike(manager).safeEngine();
        bytes32 collateralType = ManagerLike(manager).collateralTypes(cdp);
        // Receives ETH amount, converts it to WETH and joins it into the safeEngine
        ethJoin_join(ethJoin, safe);
        // Locks WETH amount into the CDP and generates debt
        modifySAFECollateralization(manager, cdp, toInt(msg.value), _getGeneratedDeltaDebt(safeEngine, taxCollector, safe, collateralType, wadD));
        // Moves the DAI amount (balance in the safeEngine in rad) to proxy's address
        transferInternalCoins(manager, cdp, address(this), toRad(wadD));
        // Allows adapter to access to proxy's DAI balance in the safeEngine
        if (SafeEngineLike(safeEngine).cdpRights(address(this), address(coinJoin)) == 0) {
            SafeEngineLike(safeEngine).approveSAFEModification(coinJoin);
        }
        // Exits DAI to the user's wallet as a token
        CoinJoinLike(coinJoin).exit(msg.sender, wadD);
    }

    function openLockETHAndGenerateDebt(
        address manager,
        address taxCollector,
        address ethJoin,
        address coinJoin,
        bytes32 collateralType,
        uint wadD
    ) public payable returns (uint cdp) {
        cdp = openSafe(manager, collateralType, address(this));
        lockETHAndGenerateDebt(manager, taxCollector, ethJoin, coinJoin, cdp, wadD);
    }

    function lockTokenCollateralAndGenerateDebt(
        address manager,
        address taxCollector,
        address collateralJoin,
        address coinJoin,
        uint safe,
        uint collateralAmount,
        uint deltaWad,
        bool transferFrom
    ) public {
        address safeHandler = ManagerLike(manager).safes(safe);
        address safeEngine = ManagerLike(manager).safeEngine();
        bytes32 collateralType = ManagerLike(manager).collateralTypes(safe);
        // Takes token amount from user's wallet and joins into the safeEngine
        collateralJoin_join(collateralJoin, safeHandler, collateralAmount, transferFrom);
        // Locks token amount into the SAFE and generates debt
        modifySAFECollateralization(manager, safe, toInt(convertTo18(collateralJoin, collateralAmount)), _getGeneratedDeltaDebt(safeEngine, taxCollector, safeHandler, collateralType, deltaWad));
        // Moves the COIN amount (balance in the safeEngine in rad) to proxy's address
        transferInternalCoins(manager, safe, address(this), toRad(deltaWad));
        // Allows adapter to access to proxy's COIN balance in the safeEngine
        if (SafeEngineLike(safeEngine).cdpRights(address(this), address(coinJoin)) == 0) {
            SafeEngineLike(safeEngine).approveSAFEModification(coinJoin);
        }
        // Exits COIN to the user's wallet as a token
        CoinJoinLike(coinJoin).exit(msg.sender, deltaWad);
    }

    function openLockTokenCollateralAndGenerateDebt(
        address manager,
        address taxCollector,
        address collateralJoin,
        address coinJoin,
        bytes32 collateralType,
        uint amtC,
        uint wadD,
        bool transferFrom
    ) public returns (uint cdp) {
        cdp = openSafe(manager, collateralType, address(this));
        lockTokenCollateralAndGenerateDebt(manager, taxCollector, collateralJoin, coinJoin, cdp, amtC, wadD, transferFrom);
    }

    function repayDebtAndFreeETH(
        address manager,
        address ethJoin,
        address coinJoin,
        uint cdp,
        uint wadC,
        uint wadD
    ) public {
        address safe = ManagerLike(manager).safes(cdp);
        // Joins DAI amount into the safeEngine
        coinJoin_join(coinJoin, safe, wadD);
        // Paybacks debt to the CDP and unlocks WETH amount from it
        modifySAFECollateralization(
            manager,
            cdp,
            -toInt(wadC),
            _getRepaidDeltaDebt(ManagerLike(manager).safeEngine(), SafeEngineLike(ManagerLike(manager).safeEngine()).coinBalance(safe), safe, ManagerLike(manager).collateralTypes(cdp))
        );
        // Moves the amount from the CDP safe to proxy's address
        transferCollateral(manager, cdp, address(this), wadC);
        // Exits WETH amount to proxy address as a token
        CollateralJoinLike(ethJoin).exit(address(this), wadC);
        // Converts WETH to ETH
        CollateralJoinLike(ethJoin).collateral().withdraw(wadC);
        // Sends ETH back to the user's wallet
        msg.sender.transfer(wadC);
    }

    function repayAllDebtAndFreeETH(
        address manager,
        address ethJoin,
        address coinJoin,
        uint cdp,
        uint wadC
    ) public {
        address safeEngine = ManagerLike(manager).safeEngine();
        address safe = ManagerLike(manager).safes(cdp);
        bytes32 collateralType = ManagerLike(manager).collateralTypes(cdp);
        (, uint safeDebt) = SafeEngineLike(safeEngine).safes(collateralType, safe);

        // Joins DAI amount into the safeEngine
        coinJoin_join(coinJoin, safe, _getWipeAllWad(safeEngine, safe, safe, collateralType));
        // Paybacks debt to the CDP and unlocks WETH amount from it
        modifySAFECollateralization(
            manager,
            cdp,
            -toInt(wadC),
            -int(safeDebt)
        );
        // Moves the amount from the CDP safe to proxy's address
        transferCollateral(manager, cdp, address(this), wadC);
        // Exits WETH amount to proxy address as a token
        CollateralJoinLike(ethJoin).exit(address(this), wadC);
        // Converts WETH to ETH
        CollateralJoinLike(ethJoin).collateral().withdraw(wadC);
        // Sends ETH back to the user's wallet
        msg.sender.transfer(wadC);
    }

    function repayDebtAndFreeTokenCollateral(
        address manager,
        address collateralJoin,
        address coinJoin,
        uint cdp,
        uint amtC,
        uint wadD
    ) public {
        address safe = ManagerLike(manager).safes(cdp);
        // Joins DAI amount into the safeEngine
        coinJoin_join(coinJoin, safe, wadD);
        uint wadC = convertTo18(collateralJoin, amtC);
        // Paybacks debt to the CDP and unlocks token amount from it
        modifySAFECollateralization(
            manager,
            cdp,
            -toInt(wadC),
            _getRepaidDeltaDebt(ManagerLike(manager).safeEngine(), SafeEngineLike(ManagerLike(manager).safeEngine()).coinBalance(safe), safe, ManagerLike(manager).collateralTypes(cdp))
        );
        // Moves the amount from the CDP safe to proxy's address
        transferCollateral(manager, cdp, address(this), wadC);
        // Exits token amount to the user's wallet as a token
        CollateralJoinLike(collateralJoin).exit(msg.sender, amtC);
    }

    function repayAllDebtAndFreeTokenCollateral(
        address manager,
        address collateralJoin,
        address coinJoin,
        uint cdp,
        uint amtC
    ) public {
        address safeEngine = ManagerLike(manager).safeEngine();
        address safe = ManagerLike(manager).safes(cdp);
        bytes32 collateralType = ManagerLike(manager).collateralTypes(cdp);
        (, uint safeDebt) = SafeEngineLike(safeEngine).safes(collateralType, safe);

        // Joins DAI amount into the safeEngine
        coinJoin_join(coinJoin, safe, _getWipeAllWad(safeEngine, safe, safe, collateralType));
        uint wadC = convertTo18(collateralJoin, amtC);
        // Paybacks debt to the CDP and unlocks token amount from it
        modifySAFECollateralization(
            manager,
            cdp,
            -toInt(wadC),
            -int(safeDebt)
        );
        // Moves the amount from the CDP safe to proxy's address
        transferCollateral(manager, cdp, address(this), wadC);
        // Exits token amount to the user's wallet as a token
        CollateralJoinLike(collateralJoin).exit(msg.sender, amtC);
    }
