contracts/interfaces/ISuCollateralRegist.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

/**
 * @title SuCollateralRegistry
 * @notice Simple enumerableSet of asset.
 * @dev It's used only by CdpRegistry. Can be change to https://docs.openzeppelin.com/contracts/4.x/api/utils#EnumerableSet
 **/
interface ISuCollateralRegistry {
    /* ===================== EVENTS ===================== */
    event CollateralAdded(address indexed asset);
    event CollateralRemoved(address indexed asset);

    /* ===================== ERRORS ===================== */

    error ZeroAddress();
    error AlreadyExist();
    error NotExist();

    /* ====================== VARS ====================== */

    // @notice mapping of collateral to its index in collateralList
    // it allowed to find collateral by address in the list without loop
    function collateralId ( address ) external view returns ( uint256 );

    /* ==================== METHODS ==================== */

    // @notice after deployment more collaterals can be added by managers
    function addCollateral ( address asset ) external;

    // @notice as well collaterals can be removed
    function removeCollateral ( address asset ) external;

    // @notice check whether asset is collateral
    function isCollateral ( address asset ) external view returns ( bool );
}

contracts/interfaces/ISuLiquidation.sol.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "../interfaces/ISuLiquidationDiscount.sol";

interface ISuLiquidation is ISuLiquidationDiscount {
    /* ==================== MUTABLE METHODS ==================== */

    // @notice Swaps stablecoins from the whitelist to the collateral. After that swap collateral to stablecoin.
    // As the result of such arbitrage liquidation bot receive profit in stablecoins
    // @dev core params are the same as in swapStablecoinToAsset()
    // @param asset The address of the collateral token
    // @param owner The owner of the collateral
    // @param stablecoinToken The address of the stablecoin token
    // @param stablecoinAmountEDecimal The amount of stablecoins for the collateral buyout
    // @param shouldUserReceiveStablecoin - should contract transfer all received stablecoins to user.
    // We use this parameter as false for flashSwapStablecoinToAssetAndSwapBack()
    function swapStablecoinToAssetAndSwapBack(
        address asset,
        address owner,
        address stablecoinToken,
        uint256 stablecoinAmountEDecimal,
        bool flashLoanIsUsed
    ) external returns (uint256 profit);

    // @notice Swaps stablecoins from the whitelist to the collateral. After that swap collateral to stablecoin.
    // All this is using flash loan, so liquidator don't need big amount of stablecoin to liquidate
    // As the result of such arbitrage liquidation bot receive profit in stablecoins.
    // @dev here we only call for flash loan {stablecoinAmountEDecimal} of {stablecoinToken} token
    // all code after we receive loan is in uniswapV3FlashCallback
    // params are the same as in swapStablecoinToAssetAndSwapBack() except last bool parameter
    function flashSwapStablecoinToAssetAndSwapBack(
        address asset,
        address owner,
        address stablecoinToken,
        uint256 stablecoinAmountEDecimal
    ) external;

    // @notice We need that to catch when flash loan is done
    function uniswapV3FlashCallback(uint fee0, uint _fee1, bytes calldata data) external;

    // @notice Swaps StablePro to stablecoins from the whitelist
    // The contract transfers stablecoins to the liquidator.
    // At the same moment the liquidator burns and transfers to the foundation the amount of his StablePro.
    // @dev This is a second step of two. It's a finilization of liquidation
    // @param stablecoinToken The address of the stablecoin token
    // @param stablecoinAmountEDecimal The amount of stablecoins
    function swapStableProToStablecoin(address stablecoinToken, uint256 stablecoinAmountEDecimal) external;

    /* ==================== VIEW METHODS ==================== */

    // @notice Calculates a liquidation discount. Depends on a block number
    function calculateLiquidationDiscount(address asset, address owner) external view returns (uint256);
}

