// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";

import "../interfaces/ISuManagerLiquidationV2.sol";
import "../interfaces/ISuVault.sol";
import "../interfaces/ISuManager.sol";
import "../interfaces/ISuCdpRegistry.sol";
import "../liquidator/v2/interfaces/ISuLiquidationMiddleware.sol";

import "../access-control/SuAuthenticated.sol";

abstract contract SuManagerLiquidationV2 is ReentrancyGuardUpgradeable, SuAuthenticated, ISuManagerLiquidationV2 {
    ISuVault private _vault;
    ISuManager private _manager;
    ISuCdpRegistry private _cdpRegistry;
    ISuLiquidationMiddleware private _liquidationMiddleware;
    address private _stablePro;
    address public foundation;

    int256 public middlewareDebt;

    modifier _checkpoint(address asset, address owner) {
        _;
        _cdpRegistry.checkpoint(asset, owner);
    }

    function __SuManagerLiquidationV2_init(
        address _suManager,
        address _suVault,
        address _suCdpRegistry,
        address _suLiquidationMiddleware,
        address _suStablePro,
        address _foundation
    ) internal onlyInitializing {
        __ReentrancyGuard_init();

        _manager = ISuManager(_suManager);
        _vault = ISuVault(_suVault);
        _cdpRegistry = ISuCdpRegistry(_suCdpRegistry);
        _liquidationMiddleware = ISuLiquidationMiddleware(_suLiquidationMiddleware);
        _stablePro = _suStablePro;
        foundation = _foundation;
    }

    function triggerLiquidation(address asset, address owner) external {
        if (_vault.liquidationBlock(asset, owner) > 0) revert LiquidationIsTriggered();
        if (!_manager.isLiquidatablePosition(asset, owner)) revert PositionIsSafe();

        // summarize debt with accumulated fees
        _vault.update(asset, owner);

        _vault.triggerLiquidation(asset, owner);

        middlewareDebt += int256(_vault.debtsE18(asset, owner));

        uint256 collateralsEDecimal = _vault.collateralsEDecimal(asset, owner);
        uint256 debtsE18 = _vault.debtsE18(asset, owner);
        uint256 debtWithFeesE18 = _vault.debtWithFeesE18(asset, owner);

        // we need to cut all debt (with fees) of owner and send collateral to _liquidationMiddleware
        _vault.liquidate(
            asset,
            owner,
            address(_liquidationMiddleware),
            collateralsEDecimal,
            debtWithFeesE18
        );

        _liquidationMiddleware.borrowCollateralToSell(asset, collateralsEDecimal, debtsE18);
    }

    function returnDebt (uint256 amount, address sender) public onlyLiquidationAccess {
        TransferHelper.safeTransferFrom(_stablePro, sender, address(this), amount);
        middlewareDebt -= int256(amount);
    }

    function sendProfitToFoundation () public onlyOwner {
        if (middlewareDebt < 0) {
            TransferHelper.safeTransferFrom(_stablePro, address(this), foundation, uint256(-middlewareDebt));
        } else {
            revert NoProfit();
        }
    }
}

