// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-periphery/contracts/interfaces/IPeripheryImmutableState.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "hardhat/console.sol";

import "../../../oracles/vendor/PoolAddress.sol";
import "../interfaces/IArbitrageHelper.sol";
import "../interfaces/IExchange.sol";

contract ArbitrageHelper is Initializable, IArbitrageHelper {
    // uint256(tokenA + tokenB) => fee for tokenA-tokenB pool (10000 / 3000 / 500)
    mapping (uint256 => uint24) public poolFees;

    ISwapRouter internal SWAP_ROUTER;
    address internal UNISWAP_FACTORY;
    address internal WETH;
    IExchange internal EXCHANGE;

    function initialize(address _exchange, address _swapRouter, address _uniswapFactory) public initializer {
        EXCHANGE = IExchange(_exchange);
        SWAP_ROUTER = ISwapRouter(_swapRouter);
        UNISWAP_FACTORY = _uniswapFactory;
        WETH = IPeripheryImmutableState(address(SWAP_ROUTER)).WETH9();
    }

    // onlyOwner?
    function setPoolFee(address tokenA, address tokenB, uint24 poolFee) public {
        poolFees[getPoolFeeId(tokenA, tokenB)] = poolFee;
    }

    function getPoolFeeId(address tokenA, address tokenB) internal pure returns (uint256) {
        return uint256(keccak256(abi.encodePacked(tokenA, tokenB)));
    }

    function getPoolFee(address tokenA, address tokenB) public view returns (uint24) {
        uint24 storedPoolFee = poolFees[getPoolFeeId(tokenA, tokenB)];
        // if we don't have this pair in poolFees then we should use default value = 3000
        return storedPoolFee != 0 ? storedPoolFee : uint24(3000);
    }

    function getPool(address tokenA, address tokenB) internal view returns (address) {
        return PoolAddress.computeAddress(
            UNISWAP_FACTORY,
            PoolAddress.getPoolKey(tokenA, tokenB, getPoolFee(tokenA, tokenB))
        );
    }

    function poolExist(address tokenA, address tokenB) internal view returns (bool) {
        // add try-catch ?
        return IUniswapV3Pool(getPool(tokenA, tokenB)).factory() == UNISWAP_FACTORY;
    }

    // can use address(this) instead of recipient
    function swapByUniswap(address token2sell, uint256 amount2sell, uint256 minToken2buy, address recipient, bytes memory path) internal returns (uint256 amountOut) {
        ISwapRouter.ExactInputParams memory params = ISwapRouter.ExactInputParams({
            path: path,
            recipient: recipient,
            deadline: block.timestamp,
            amountIn: amount2sell,
            amountOutMinimum: minToken2buy
        });

        // We need to approve {token2sell} to be used in Swap Router and send to this contract to swap it
        TransferHelper.safeApprove(token2sell, address(SWAP_ROUTER), amount2sell);
        if (recipient != address(this)) {
            TransferHelper.safeTransferFrom(token2sell, recipient, address(this), amount2sell);
        }

        // swap {token2sell} to {token2buy} and return the amount of the received {token2buy}
        amountOut = SWAP_ROUTER.exactInput(params);
    }

    function swapByParaswap(address token2sell, uint256 amount2sell, ParaswapData memory paraswapData) public {
        // We need to approve {token2sell} to be used in Paraswap
        TransferHelper.safeApprove(token2sell, paraswapData.tokenTransferProxy, amount2sell);

        console.log("before call");
        (bool success, bytes memory resp) = paraswapData.contractAddress.call(paraswapData.data);
        console.log(success);
        if (!success) {
            revert SwapByParaSwapError();
        }
    }

    function swapWithFlashLoan(uint256 orderId, address tokenA, address tokenB, uint256 tokenBAmount, ParaswapData memory paraswapData) public {
        address pool = getPool(tokenB, WETH);
        bool isFirstInPool = IUniswapV3Pool(pool).token0() == tokenB;
        bytes memory data = abi.encode(FlashCallbackData({
            orderId: orderId,
            tokenA: tokenA,
            tokenB: tokenB,
            tokenBAmount: tokenBAmount,
            recipient: msg.sender,
            isFirstInPool: isFirstInPool,
            paraswapData: paraswapData
        }));
        IUniswapV3Pool(pool).flash(
            address(this),
            isFirstInPool ? tokenBAmount : 0,
            isFirstInPool ? 0 : tokenBAmount,
            data
        );
    }

    function uniswapV3FlashCallback(uint fee0, uint fee1, bytes calldata data) external {
        FlashCallbackData memory decoded = abi.decode(data, (FlashCallbackData));

        address pool = getPool(decoded.tokenB, WETH);
        if (msg.sender != pool) revert NotAPool(msg.sender);

        uint256 maxOrderOutAmount = EXCHANGE.getExpectedOrderOutcome(decoded.orderId);
        uint256 amountToSend = maxOrderOutAmount > decoded.tokenBAmount ? decoded.tokenBAmount : maxOrderOutAmount;
        TransferHelper.safeTransfer(decoded.tokenB, decoded.recipient, amountToSend);

        (uint256 tokenAReceived, uint256 outAmount) = EXCHANGE.fillOrder(
            decoded.orderId,
            amountToSend,
            decoded.recipient,
            false
        );
        uint256 tokenBLeft = amountToSend - outAmount;

        TransferHelper.safeTransferFrom(decoded.tokenA, decoded.recipient, address(this), tokenAReceived);

        uint256 balanceBefore = IERC20(decoded.tokenB).balanceOf(address(this));
        console.log("balanceBefore", balanceBefore);
        swapByParaswap(decoded.tokenA, tokenAReceived, decoded.paraswapData);
        uint256 balanceAfter = IERC20(decoded.tokenB).balanceOf(address(this));
        console.log('balanceAfter', balanceAfter);
        uint256 tokenBReceived = balanceAfter - balanceBefore;
        console.log('tokenBReceived', tokenBReceived);

        uint256 swapProfit = tokenBReceived - amountToSend;
        uint256 fee = decoded.isFirstInPool ? fee0 : fee1;
        if (swapProfit <= fee) revert SwapNotProfitable();

        // Send tokenBToRepay (Repay borrow)
        TransferHelper.safeTransfer(decoded.tokenB, pool, decoded.tokenBAmount + fee);
        // and send swapped {stablecoinToken} minus fee from flash-loan to liquidator (userProfit)
        TransferHelper.safeTransfer(decoded.tokenB, decoded.recipient, swapProfit - tokenBLeft - fee);
    }

    uint256[45] private __gap;
}

contracts/liquidator/v2/contracts/Exchan.content:
// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

import "../interfaces/IExchange.sol";
import "../interfaces/IExchangeCallback.sol";
import "../interfaces/ITopStakers.sol";
import "../../../interfaces/ISuOracle.sol";
import "../../../access-control/SuAuthenticated.sol";

contract Exchange is SuAuthenticated, IExchange {
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.UintSet;
    // todo: add enumerableSet of all IDs
    mapping (uint256 => Order) public orders;
    EnumerableSetUpgradeable.UintSet private orderIdSet;

    // priceCurve is like price_t = price_0 * (1 - t/priceCurve[0]) - priceCurve[1]
    // from timePassed = 0 to t priceCurve decrease linear, but after t (when discount is 20%), it's constant
    mapping (DiscountType => uint256[2]) public priceCurve;

    uint256 public DUST_THRESHHOLD;
    ISwapRouter internal SWAP_ROUTER;
    address internal UNISWAP_FACTORY;

    ISuOracle public ORACLE;
    ITopStakers public TOP_STAKERS;

    function initialize(
        address _authControl,
        address _oracleAddress,
        address _topStakersAddress,
        address _swapRouter,
        address _uniswapFactory
    ) public initializer {
        __SuAuthenticated_init(_authControl);

        // -20% for 3min/10h/24h
        priceCurve[DiscountType.aggressive] = [180 * 5, 0];
        priceCurve[DiscountType.middle] = [36_000 * 5, 0];
        priceCurve[DiscountType.conservative] = [86_400 * 5, 0];
        DUST_THRESHHOLD = 100 * 1e18;

        SWAP_ROUTER = ISwapRouter(_swapRouter);
        UNISWAP_FACTORY = _uniswapFactory;
        ORACLE = ISuOracle(_oracleAddress);
        TOP_STAKERS = ITopStakers(_topStakersAddress);
    }

    function setPriceCurve(DiscountType discountType, uint256[2] calldata newPriceCurve) public onlyOwner {
        priceCurve[discountType] = newPriceCurve;
    }

    function addOrder(
        address token2sell,
        uint256 amount2sell,
        address token2buy,
        address beneficiary,
        DiscountType discountType
    ) external returns (uint256 orderId) {
        orderId = uint256(
            keccak256(
                abi.encodePacked(block.number, msg.sender, token2sell, amount2sell, token2buy)
            )
        );
        if (orders[orderId].creationTimestamp != 0) orderId += 1;
        if (orders[orderId].creationTimestamp != 0) revert OrderExist(orderId);

        TransferHelper.safeTransferFrom(token2sell, msg.sender, address(this), amount2sell);
        orders[orderId] = Order(
            token2sell,
            amount2sell,
            token2buy,
            0,
            beneficiary,
            block.timestamp,
            discountType,
            msg.sender
        );
        EnumerableSetUpgradeable.add(orderIdSet, orderId);
        emit OrderAdded(orderId);
    }

    function cancelOrder(uint256 orderId) public {
        Order memory order = orders[orderId];

        if (order.creationTimestamp == 0) revert OrderNotExist(orderId);
        if (msg.sender != order.beneficiary && msg.sender != order.caller) revert UserIsNotBeneficiary();

        TransferHelper.safeTransfer(order.token2sell, order.beneficiary, order.amountLeft);
        delete orders[orderId];
        EnumerableSetUpgradeable.remove(orderIdSet, orderId);

        emit OrderCanceled(orderId);
    }

    // = x * 10^18 * 10^(18 - decimals) like in Oracle
    function getOrderPrice(address token2sell, uint256 timePassed, DiscountType discountType) public view returns (uint256) {
        uint256 curveDiv = priceCurve[discountType][0];
        uint256 curveSub = priceCurve[discountType][1];
        uint256 price = ORACLE.getFiatPrice1e18(token2sell);

        if (timePassed > curveDiv) {
            // minimum is 80%;
            return price * 8 / 10;
        }

        return price - price * timePassed / curveDiv - curveSub;
    }

    // = x * 10^(tokenB.decimals)
    // as default, after dividing we can lose precision, so for external users we show roundUp value
    function getOutcome(address tokenA, address tokenB, uint256 amountA, uint256 timePassed, DiscountType discountType) public view returns (uint256) {
        uint256 priceA = getOrderPrice(tokenA, timePassed, discountType); // x * 10^18 * 10^(18 - d1)
        uint256 priceB = ORACLE.getFiatPrice1e18(tokenB); // y * 10^18 * 10^(18 - d2)

        // a * 10^d1 * p1 * 10^(18 + 18 -d1) / (y * 10^18 * 10^(18 - d2)) =
        // a * p1 * 10^36 / (y * 10^(36 - d2)) =
        // (a * p1 / y) * 10^d2
        uint256 res = amountA * priceA / priceB;

        if (amountA != res * priceB / priceA) {
            // Here we lose some precision during dividing in getOutcome() => we roundUp
            return res + 1;
        }
        return res;
    }

    // return x * (10 ** token2buy.decimals)
    // we can lose precision, so external users could increase the result +1 or can wait 1 second
    function getExpectedOrderOutcome(uint256 orderId) public view returns (uint256) {
        Order memory order = orders[orderId];

        return getOutcome(
            order.token2sell,
            order.token2buy,
            order.amountLeft,
            block.timestamp - order.creationTimestamp,
            order.discountType
        );
    }

    function fillOrder(uint256 orderId, uint256 tokenBAmount, address user, bool needCallback) external returns (uint256, uint256) {
        Order storage order = orders[orderId];

        if (order.creationTimestamp == 0) revert OrderNotExist(orderId);

        uint256 timePassed = block.timestamp - order.creationTimestamp;

        // grace period
        if (timePassed <= 60) {
            uint256 numberOfStakers = TOP_STAKERS.getTopStakersLength();
            if (!TOP_STAKERS.isTopStaker(user)) revert IsNotTopStaker(user);
            if (uint160(user) % numberOfStakers != orderId % numberOfStakers) revert OrderNotForThisStaker(orderId, user);
        }

        // = x * 10^(token2buyDecimals)
        uint256 maxOutAmount = getExpectedOrderOutcome(orderId);
        uint256 orderPrice = getOrderPrice(order.token2sell, timePassed, order.discountType);
        uint256 priceB = ORACLE.getFiatPrice1e18(order.token2buy);

        uint256 outAmount = tokenBAmount > maxOutAmount ? maxOutAmount : tokenBAmount;

        // = x * 10^6 * p2 * 10^(36-6) / (p1 * 10^18)
        // = x * 10^d2 * p2 * 10^(36-d2) / (p1 * 10^18 * 10^(18-d1))
        // = (x / y) * 10^(token2sellDecimals)
        uint256 inAmount = outAmount * priceB / orderPrice;

        order.token2buyAmount += outAmount;
        // inAmount > order.amountLeft could be only in case when outAmount is maxOutAmount and we increase it not to lose precision
        if (inAmount >= order.amountLeft) {
            inAmount = order.amountLeft;
            order.amountLeft = 0;
        } else {
            order.amountLeft -= inAmount;
        }

        // DUST_THRESHHOLD is x * 1e18
        if (order.amountLeft > 0 && orderPrice * order.amountLeft / 1e18 <= DUST_THRESHHOLD) {
            revert SmallAmountLeft();
        }

        TransferHelper.safeTransferFrom(order.token2buy, user, order.beneficiary, outAmount);
        TransferHelper.safeTransfer(order.token2sell, user, inAmount);

        if (order.amountLeft == 0) {
            if (needCallback) {
                IExchangeCallback(order.caller).exchangeCallback(orderId, order.token2buyAmount);
            }
            delete orders[orderId];
            EnumerableSetUpgradeable.remove(orderIdSet, orderId);
            emit OrderFilled(orderId);
        } else {
            emit OrderFilledPartially(orderId);
        }

        return (inAmount, outAmount);
    }

    function getOrder (uint256 orderId) public view returns (Order memory) {
        return orders[orderId];
    }

    function getOrdersCount() public view returns (uint256) {
        return EnumerableSetUpgradeable.length(orderIdSet);
    }

    function getOrderAt(uint256 index) external view returns (Order memory) {
        uint256 orderId = EnumerableSetUpgradeable.at(orderIdSet, index);
        return orders[orderId];
    }

    function getAllOrders() external view returns (OrderWithId[] memory) {
        uint256[] memory orderIdValues = EnumerableSetUpgradeable.values(orderIdSet);
        uint256 len = orderIdValues.length;
        OrderWithId[] memory result = new OrderWithId[](len);
        for (uint256 i = 0; i < len; i++) {
            uint256 orderId = orderIdValues[i];
            result[i] = OrderWithId({
                order: orders[orderId],
                orderId: orderId
            });
        }
        return result;
    }

    function supportsInterface(bytes4 interfaceId) public view returns (bool) {
        return interfaceId == type(IExchange).interfaceId;
    }

    uint256[45] private __gap;
}

