// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";

import "../../../access-control/SuAuthenticated.sol";
import "../../../interfaces/ISuManager.sol";
import "../../../interfaces/ISuOracle.sol";
import "../interfaces/ISuLiquidationMiddleware.sol";
import "../interfaces/IExchange.sol";

contract SuLiquidationMiddleware is SuAuthenticated, ISuLiquidationMiddleware {
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    ISuManager internal _manager;
    ISuOracle internal _oracle;
    IExchange internal _exchangeModule;
    EnumerableSetUpgradeable.AddressSet internal _stablecoinWhitelist;
    address public foundation;
    address public stablePro;

    function initialize(
        address _authControl,
        address _suManager,
        address _suExchangeModule,
        address _suOracle,
        address _stablePro,
        address _foundation
    ) public initializer {
        __SuAuthenticated_init(_authControl);

        _manager = ISuManager(_suManager);
        _exchangeModule = IExchange(_suExchangeModule);
        _oracle = ISuOracle(_suOracle);
        stablePro = _stablePro;
        foundation = _foundation;
    }

    function borrowCollateralToSell (
        address collateral,
        uint256 amount,
        uint256 debt
    ) public onlyLiquidationAccess {
        address stablecoin = EnumerableSetUpgradeable.at(_stablecoinWhitelist, 0);

        _exchangeModule.addOrder(
            collateral,
            amount,
            stablecoin,
            address(this),
            DiscountType.middle
        );

        emit CollateralBorrowed(debt);
    }

    function exchangeCallback(uint256 orderId, uint256 amountOut) external {
//        Order memory order = _exchangeModule.getOrder(orderId);
//        uint256 debt = order.token2buyAmount;
//        int256 profit = int256(amountOut) - int256(debt);
//
//        _manager.returnDebt(debt);
//        if (profit > 0) {
//            SafeERC20Upgradeable.safeTransfer(IERC20Upgradeable(stablePro), foundation, uint256(profit));
//        }
    }

    // user need to approve StablePro
    function swapStableProToStablecoin(
        address stablecoinToken,
        uint256 stableProAmountE18
    ) external {
        if (!hasWhitelistStablecoin(stablecoinToken)) revert BadStablecoin(stablecoinToken);

        uint256 stablecoinAmountEDecimal = stableProAmountE18 * 1e18 / _oracle.getFiatPrice1e18(stablecoinToken);

        // user send stableProAmountE18 to manager
        _manager.returnDebt(stableProAmountE18, msg.sender);
        // contract send stablecoin to user
        SafeERC20Upgradeable.safeTransfer(IERC20Upgradeable(stablecoinToken), msg.sender, stablecoinAmountEDecimal);

        emit StableProSwapped(stableProAmountE18);
    }

    function addWhitelistStablecoin(address _stablecoin) public onlyOwner {
        EnumerableSetUpgradeable.add(_stablecoinWhitelist, _stablecoin);
    }

    function removeWhitelistStablecoin(address _stablecoin) public onlyOwner {
        EnumerableSetUpgradeable.remove(_stablecoinWhitelist, _stablecoin);
    }

    function hasWhitelistStablecoin(address _stablecoin) public view returns(bool) {
        return EnumerableSetUpgradeable.contains(_stablecoinWhitelist, _stablecoin);
    }
}

