// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

enum DiscountType {
    aggressive,
    middle,
    conservative
}

struct Order {
    address token2sell;
    uint256 amountLeft;
    address token2buy;
    uint256 token2buyAmount;
    address beneficiary;
    uint256 creationTimestamp;
    DiscountType discountType;
    address caller;
}

struct OrderWithId {
    Order order;
    uint256 orderId;
}

interface IExchange {
    /* ===================== ERRORS ===================== */
    error OrderExist(uint256 orderId);
    error OrderNotExist(uint256 orderId);
    error IncorrectToken(address orderToken2buy, address inToken);
    error SmallAmountLeft();
    error UserIsNotBeneficiary();
    error IsNotTopStaker(address staker);
    error OrderNotForThisStaker(uint256 orderId, address staker);

    /* ===================== EVENTS ===================== */
    event OrderAdded(uint256 orderId);
    event OrderCanceled(uint256 orderId);
    event OrderFilled(uint256 orderId);
    event OrderFilledPartially(uint256 orderId);

    /* ==================== MUTABLE METHODS ==================== */

    function addOrder (
        address token2sell,
        uint256 amount2sell,
        address token2buy,
        address beneficiary,
        DiscountType discountType
    ) external returns (uint256);

    function cancelOrder (uint256 orderId) external;

    function fillOrder (
        uint256 orderId,
        uint256 tokenBAmount,
        address user,
        bool needCallback
    ) external returns (uint256 inAmount, uint256 outAmount);

    function setPriceCurve(DiscountType discountType, uint256[2] calldata newPriceCurve) external;

    /* ==================== VIEW METHODS ==================== */
    function getOrder (uint256 orderId) external view returns (Order memory);

    // @return how much tokenB user will receive after full filling of order
    function getExpectedOrderOutcome (uint256 orderId) external view returns (uint256);

    function getOrdersCount () external view returns (uint256);

    function getOrderAt (uint256 index) external view returns (Order memory);

    function getAllOrders () external view returns (OrderWithId[] memory);
}

// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

// @title Callback for IExchange#addOrder
// @notice Any contract that calls IExchange#addOrder must implement this interface
interface IExchangeCallback {
    // @notice Called to `msg.sender` after filling the order via IExchange#fillOrder
    // that was added by IExchange#addOrder.
    // @param orderId Id of the order that was fully filled
    // @param amountOut The amount of token2buy that beneficiary received
    function exchangeCallback(uint256 orderId, uint256 amountOut) external;
}

