// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

import "../interfaces/ISuChainlinkOracle.sol";
import "../access-control/SuAccessControlSingleton.sol";

contract SuChainlinkOracle is ISuChainlinkOracle, SuAuthenticated {
    mapping (address => address) public assetToFeed;
    mapping (address => uint8) public assetToFeedDecimals;

    function initialize(address _authControl) public initializer {
        __SuAuthenticated_init(_authControl);
    }

    function setAssetFeed(address asset, address chainlinkDataFeed, uint8 chainlinkDataFeedDecimals) external onlyOwner {
        assetToFeed[asset] = chainlinkDataFeed;
        // some decimals are different from real token decimals, so we need to store them
        assetToFeedDecimals[asset] = chainlinkDataFeedDecimals;
    }

    // please see ISuOracle description
    function getFiatPrice1e18(address asset) public view returns (uint) {
        address feed = assetToFeed[asset];
        uint8 decimals = assetToFeedDecimals[asset];
        (
            /*uint80 roundID*/,
            int256 answer,
            /*uint256 startedAt*/,
            /*uint256 updatedAt*/,
            /*uint80 answeredInRound*/
        ) = AggregatorV3Interface(feed).latestRoundData();
        require(answer > 0, "Oracle returns price <= 0");
        // examples of answers and what we want to get:
        // ETH/USD = 134165000000 = 1341.65*1e8 =>(want) 1341*1e18
        // BTC/USD = 1928068190485 = 19,280.68190485*1e8 =>(want) 19k*1e18 * (18-8)[for asset decimals]
        // USDC/USD = 99990000 = 0.99990000*1e8 =>(want) => 0.9(9) * 1e18 * (18-6)[for asset decimals]
        int256 priceFeed1e18 = scalePrice(answer, decimals, 18);
        uint8 assetDecimals = IERC20Metadata(asset).decimals();
        int256 returnedUsdPrice1e18 = scalePrice(priceFeed1e18, assetDecimals, 18);
        return uint(returnedUsdPrice1e18);
    }

    function scalePrice(int256 _price, uint8 _priceDecimals, uint8 _decimals) internal pure returns (int256) {
        if (_priceDecimals < _decimals) {
            return _price * int256(10 ** uint256(_decimals - _priceDecimals));
        } else if (_priceDecimals > _decimals) {
            return _price / int256(10 ** uint256(_priceDecimals - _decimals));
        }
        return _price;
    }
}

contracts/oracles/SuOracleAggregator.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.0;

/*
    OracleAggregator aggregates several oracles inside, such as: getUsdPrice asset -> id -> call to implementation
    We have several oracle implementations of ISuOracle (they are inside this folder)
*/

import "../access-control/SuAccessControlSingleton.sol";
import "../interfaces/ISuOracleAggregator.sol";

contract SuOracleAggregator is ISuOracleAggregator, SuAuthenticated {
    mapping (address => uint256) public assetToOracle;
    mapping (uint256 => ISuOracle) public oracleImplementations;

    function initialize(address _authControl) public initializer {
        __SuAuthenticated_init(_authControl);
    }

    function getFiatPrice1e18(address asset) external view returns (uint256) {
        uint256 oracleId = assetToOracle[asset];
        require(oracleId != 0, "No oracle for the asset");
        ISuOracle oracleImplementation = oracleImplementations[oracleId];
        require(address(oracleImplementation) != address(0), "No oracle implementation" );
        return oracleImplementation.getFiatPrice1e18(asset);
    }

    function setOracleImplementation(uint256 oracleId, ISuOracle oracleImplementation) external onlyOwner {
        require(oracleId != 0, "OracleId == 0");
        require(address(oracleImplementation) != address(0), "OracleImplementation == 0");
        oracleImplementations[oracleId] = oracleImplementation;
    }

    function setOracleIdForAssets(address[] memory assets, uint256 oracleId) external onlyOwner {
        require(address(oracleImplementations[oracleId]) != address(0), "OracleImplementation == 0");
        for (uint256 i = 0; i < assets.length; i++) {
            assetToOracle[assets[i]] = oracleId;
        }
    }
}

