// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity ^0.8.0;
pragma abicoder v2;

import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";

import "../vendor/balancer/IVault.sol";
import "../vendor/balancer/WeightedPoolUserData.sol";
import "../vendor/aave/IAavePool.sol";
import "../interfaces/oracle/ISuOracle.sol";

struct CallbackData {
    address lpToken;
    address lpVault;
    address contractAddress;
    bytes32 poolId;
    address[] underlyingTokens;
    address adapter;
    uint256 tokenIndex;
}

interface IAdapterUnsafePrice {
    function getFiatPrice1e18Unsafe(address asset) external view returns (uint256);
}

contract SimpleFlashLoanOracleBalTest {
    address private AAVE_POOL = 0x87870Bca3F3fD6335C3F4ce8392D69350B4fA4E2;
    uint256 public priceAfterFlashLoan;
    uint256 public priceUnsafeAfterFlashLoan;
    ISuOracle public ORACLE;

    constructor(address _oracle) {
        ORACLE = ISuOracle(_oracle);
    }

    /**
        @param lpToken Address of asset to check price of
        @param lpVault Address of vault of LP token
        @param poolId ID of pool of LP token
        @param underlyingTokens Array of underlying tokens of LP token
        @param assetToLoan Address of asset to loan
        @param amountToLoan Amount of asset to loan
    */
    function checkLPOnFlashLoanAttack(
        address lpToken,
        address lpVault,
        bytes32 poolId,
        address[] memory underlyingTokens,
        address assetToLoan,
        uint256 amountToLoan,
        uint256 tokenIndex,
        address adapter
    ) external {
        bytes memory data = abi.encode(CallbackData({
            lpToken: lpToken,
            lpVault: lpVault,
            poolId: poolId,
            underlyingTokens: underlyingTokens,
            adapter: adapter,
            tokenIndex: tokenIndex,
            contractAddress: address(this)
        }));
        IERC20(assetToLoan).approve(lpVault, amountToLoan);

        IAavePool(AAVE_POOL).flashLoanSimple(address(this), assetToLoan, amountToLoan, data, 0);
    }

    function executeOperation(address asset, uint256 amount, uint256 fee, address sender, bytes calldata data) external returns (bool) {
        CallbackData memory decoded = abi.decode(data, (CallbackData));

        uint256 nTokens = decoded.underlyingTokens.length;
        IAsset[] memory assets = new IAsset[](nTokens);
        uint256[] memory maxAmountsIn = new uint[](nTokens);
        uint256[] memory minAmountsOut = new uint[](nTokens);
        for (uint16 i = 0; i < nTokens; ++i) {
            assets[i] = IAsset(address(decoded.underlyingTokens[i]));
            maxAmountsIn[i] = i == decoded.tokenIndex ? amount : 0;
            minAmountsOut[i] = 0;
        }

        IVault(decoded.lpVault).joinPool(
            decoded.poolId,
            payable(decoded.contractAddress),
            payable(decoded.contractAddress),
            IVault.JoinPoolRequest({
                assets: assets,
                maxAmountsIn: maxAmountsIn,
                userData: abi.encode(WeightedPoolUserData.JoinKind.EXACT_TOKENS_IN_FOR_BPT_OUT, maxAmountsIn, 0),
                fromInternalBalance: false
            })
        );
        uint256 lpAmountAfterJoin = IERC20(decoded.lpToken).balanceOf(decoded.contractAddress);

        priceAfterFlashLoan = ORACLE.getFiatPrice1e18(decoded.lpToken);
        priceUnsafeAfterFlashLoan = IAdapterUnsafePrice(decoded.adapter).getFiatPrice1e18Unsafe(decoded.lpToken);

        IVault(decoded.lpVault).exitPool(
            decoded.poolId,
            payable(decoded.contractAddress),
            payable(decoded.contractAddress),
            IVault.ExitPoolRequest({
                assets: assets,
                minAmountsOut: minAmountsOut,
                userData: abi.encode(WeightedPoolUserData.ExitKind.EXACT_BPT_IN_FOR_ONE_TOKEN_OUT, lpAmountAfterJoin, decoded.tokenIndex),
                toInternalBalance: false
            })
        );

        TransferHelper.safeApprove(asset, AAVE_POOL, amount + fee);
        return true;
    }

    function getPriceAfterFlashLoan() public view returns (uint256) {
        return priceAfterFlashLoan;
    }

    function getUnsafePriceAfterFlashLoan() public view returns (uint256) {
        return priceUnsafeAfterFlashLoan;
    }
}

