        IERC20(assetToLoan).approve(lpPool, amountToLoan);

        IAavePool(AAVE_POOL).flashLoanSimple(address(this), assetToLoan, amountToLoan, data, 0);
    }

    function executeOperation(address asset, uint256 amount, uint256 fee, address sender, bytes calldata data) external returns (bool) {
