pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "../interfaces/ISuOracle.sol";
import "../access-control/SuAccessControlSingleton.sol";

contract SuChainlinkOracle is ISuOracle, SuAuthenticated {
    mapping (address => address) public assetToFeed;
    mapping (address => uint8) public assetToDecimals;

    constructor(address _authControl) SuAuthenticated(_authControl) {
    }

    function setAssetFeed(address _collateral, address _feed, uint8 _decimals) external onlyOwner {
        assetToFeed[_collateral] = _feed;
        assetToDecimals[_collateral] = _decimals;
    }

    function getUsdPrice1e18(address asset) public override view returns (uint) {
        address feed = assetToFeed[asset];
        uint8 decimals = assetToDecimals[asset];

        (
            /*uint80 roundID*/,
            int256 answer,
            /*uint256 startedAt*/,
            /*uint256 updatedAt*/,
            /*uint80 answeredInRound*/
        ) = AggregatorV3Interface(feed).latestRoundData();

        int256 price = scalePrice(answer, decimals, 18);

        return uint(price);
    }

    function scalePrice(int256 _price, uint8 _priceDecimals, uint8 _decimals)
        internal
        pure
        returns (int256)
    {
        if (_priceDecimals < _decimals) {
            return _price * int256(10 ** uint256(_decimals - _priceDecimals));
        } else if (_priceDecimals > _decimals) {
            return _price / int256(10 ** uint256(_priceDecimals - _decimals));
        }
        return _price;
    }
}
