contract GainableERC20v3Upgradeable is Initializable, ContextUpgradeable, IERC20Upgradeable, IERC20MetadataUpgradeable {
    using SafeCastUpgradeable for uint256;
    using SafeCastUpgradeable for uint128;
    using SafeCastUpgradeable for uint64;
    using SafeCastUpgradeable for int256;

    /**
     * ⌈log2(1e18)⌉ = 60 bit
     *
     */
    struct IAccount {
        uint256 balance;
        int256 rewardDebt;
        bool isGainDisabled;
    }

    struct IRewardPool {
        uint128 accRewardPerShare;
        uint64 lastRewardBlock;
        //        uint256 lpSupply;
    }

    uint256 private constant ACC_REWARD_TOKEN_PRECISION = 1e12;
    IRewardPool public rewardPool;

    mapping(address => IAccount) public _accounts;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;



    // ==========================REWARDER================================
    uint256 public rewardPerBlock;
    uint256 public rewardEndBlock;

    function refillReward(uint256 amount, uint64 endBlock) public {
        require(block.number < endBlock, "EndBlock should be greater than current block");
        updateRewardPool();

        transferFrom(msg.sender, address(this), amount);
        uint256 rewardLeftAmount;
        // if there are active rewards leftovers
        if (rewardEndBlock > 0) {
            // if we call refillReward before old endBlock ends
            if (block.number < rewardEndBlock) {
                rewardLeftAmount = rewardPerBlock * (rewardEndBlock - block.number);
            } else {
                // if we start the new reward interval that has nothing in common with the old noe
                rewardLeftAmount = 0;
            }
        }
        rewardPerBlock = (rewardLeftAmount + amount) / (endBlock - block.number);
        rewardEndBlock = endBlock;
    }

    /**
     *  @dev returns total amount of rewards allocated to the all pools on the rage (startBlock, endBlock]
     *      i.e. excluding startBlock but including endBlock
     */
    function rewardsBetweenBlocks(uint256 startBlock, uint256 endBlock) public view returns (uint256) {
        // if all rewards were allocation before our rage - then answer is 0
        if (rewardEndBlock <= startBlock) {
            return 0;
        } else {
            // if rewards allocates on the whole range, than just calc rectangle area
            if (endBlock < rewardEndBlock) {
                return (endBlock - startBlock) * rewardPerBlock;
            } else {
                // other-vice, rewards end its allocation during our rage, so we have to calc only until rewardEndBlock
                return (rewardEndBlock - startBlock) * rewardPerBlock;
            }
        }
    }

    function updateRewardPool() public {
        if (block.number > rewardPool.lastRewardBlock) {
            uint256 totalRewardsSinceLastUpdate = rewardsBetweenBlocks(rewardPool.lastRewardBlock, block.number);
            uint256 activeSupply = _totalSupply;
            // totalSupply - undistributed reward - disabled accounts
            if (activeSupply > 0) {
                rewardPool.accRewardPerShare = rewardPool.accRewardPerShare +
                (totalRewardsSinceLastUpdate * ACC_REWARD_TOKEN_PRECISION / activeSupply).toUint128();
            }
            rewardPool.lastRewardBlock = block.number.toUint64();
        }
    }

    function pendingRewards(address account) public view returns (uint256) {
        // simulate updateRewardPool() in-place
        uint256 totalRewardsSinceLastUpdate = rewardsBetweenBlocks(rewardPool.lastRewardBlock, block.number);
        uint256 activeSupply = _totalSupply;
        // TODO: we need to have some initial _totalSupply or we'll have division by zero here.
        if (activeSupply == 0) {
            return 0;
        }
        // totalSupply - undistributed reward - disabled accounts
        uint256 currentAccRewardPerShare = rewardPool.accRewardPerShare +
            (totalRewardsSinceLastUpdate * ACC_REWARD_TOKEN_PRECISION / activeSupply).toUint128();

        // simulate harvest()
        IAccount memory user = _accounts[account];
        int256 completeAccountReward = (user.balance * currentAccRewardPerShare / ACC_REWARD_TOKEN_PRECISION).toInt256();
        uint256 pending = (completeAccountReward - user.rewardDebt).toUint256();
        return pending;
    }

    function harvest(address account) internal {
        IAccount storage user = _accounts[account];
        updateRewardPool();
        uint256 _pendingRewards = pendingRewards(account);
        user.balance = user.balance + _pendingRewards;
        user.rewardDebt = (user.balance * rewardPool.accRewardPerShare / ACC_REWARD_TOKEN_PRECISION).toInt256();
    }

    function getYieldAPR() public view returns (uint256) {
        if (block.number > rewardEndBlock) {
            return 0;
        }
        uint256 rewardsLeftToDistribute = rewardPerBlock * (rewardEndBlock - block.number);
        uint256 activeSupply = totalSupply() - rewardsLeftToDistribute;
        uint256 blocksPerYear = 4 * 60 * 24 * 365;

        return rewardPerBlock * blocksPerYear * 1e18 / activeSupply;
    }
    //===================================================================

    /**
