    function triggerLiquidation(address asset, address owner) external {

        // check liquidation is not triggered yet
        require(vault.liquidationBlock(asset, owner) == 0, "Unit Protocol: LIQUIDATING_POSITION");

        // calculate valut of collateral
        uint usdValue = getCollateralUsdValueE18(asset, owner);

        // and check if it can be liquidated
        require(_isLiquidatablePosition(asset, owner, usdValue), "Unit Protocol: SAFE_POSITION");

        // summarize debt with accumulated fees
        vault.update(asset, owner);

        // then vault will trigger liquidation and auction begins
        vault.triggerLiquidation(asset, owner);

        emit LiquidationTriggered(asset, owner);
    }

    // will stablecoin paid by liquidator be burned immediately?
    /// Yes
    // how can cascading liquidation happen step by step?
    /// please see https://ratiofinance.medium.com/ratio-risk-lesson-2-cascading-liquidations-e91e04050f47
    /// @notice Liquidates a position's collateral completely
    /// @dev Supports a partial liquidation
    /// @param asset The address of the main collateral token of a position
    /// @param owner The owner of a position
    /// @param repayer The person who repaies by debt and recieves a position's collateral
    /// @param repaymentE18 The amount of repayment
    /// @param excessAndFeeE18 The amount of stablecoins which transfer to foundation
    /// @param assetAmountEDecimal The position's collateral which be recieved by repayer
    /// @return destroyed Returns "true" if the position completely liquidated
    function liquidateAndRepay(
        address asset,
        address owner,
        address repayer,
        uint repaymentE18,
        uint excessAndFeeE18,
        uint assetAmountEDecimal
    ) public nonReentrant checkpoint(asset, owner) onlyLiquidationAccess returns (bool destroyed) {
        // check if it can be liquidated
        require(vault.liquidationBlock(asset, owner) > 0, "Unit Protocol: NOT_LIQUIDATING_POSITION");

        console.log("LIQUIDATE liquidate (assetAmount, repayment): ", assetAmountEDecimal, repaymentE18);
        destroyed = vault.liquidate(asset, owner, repayer, assetAmountEDecimal, repaymentE18);

        // not allowed to left some dust after partial closure the position
        _ensureMinCollateralThreshold(asset, msg.sender);

        // burn stablecoin from the vault
        console.log("REPAY repay (repayment, excessAndFee):", repaymentE18, excessAndFeeE18);
        vault.repay(repayer, repaymentE18, excessAndFeeE18);
    }

    /// @notice Liquidates a position
    /// @dev Supports a partial liquidation
    /// @param asset The address of the main collateral token of a position
    /// @param owner The owner of the collateral
    /// @param repayer The person who repaies by debt and transfers stablecoins to the foundation
    /// @param stablecoinsToRepaymentE18 The amount of stablecoins which will be burned as a debt repaymention
    /// @param assetAmountEDecimal The position's collateral which be recieved by repayer
    /// @return destroyed Returns "true" if the position completely liquidated
    function liquidate(
        address asset,
        address owner,
        address repayer,
        uint stablecoinsToRepaymentE18,
        uint assetAmountEDecimal
    ) public nonReentrant checkpoint(asset, owner) onlyLiquidationAccess returns (bool destroyed) {
        // check if it can be liquidated
        require(vault.liquidationBlock(asset, owner) > 0, "Unit Protocol: NOT_LIQUIDATING_POSITION");

        console.log("LIQUIDATE liquidate (assetAmount, repayment): ", assetAmountEDecimal, stablecoinsToRepaymentE18);

        destroyed = vault.liquidate(asset, owner, repayer, assetAmountEDecimal, stablecoinsToRepaymentE18);

        // not allowed to left some dust after partial closure the position
        _ensureMinCollateralThreshold(asset, msg.sender);
    }

    /// @notice Finilizes the liquidation
    /// @param repayer The person who repaies by debt and transfers stablecoins to the foundation
    /// @param stablecoinsToRepaymentE18 The amount of stablecoins which will be burned as a debt repayment
    /// @param stablecoinsToFoundationE18 The amount of stablecoins which will be transfered to the foundation
    function repay(
        address repayer,
        uint stablecoinsToRepaymentE18,
        uint stablecoinsToFoundationE18
    ) public {
        console.log("REPAY repay (repayment, toFondation):", stablecoinsToRepaymentE18, stablecoinsToFoundationE18);

        // burn stablecoin from the vault
        vault.repay(repayer, stablecoinsToRepaymentE18, stablecoinsToFoundationE18);
    }

    /// @notice Returns information about a liquidating position
    /// @dev TODO: should rename to getPositionInfo()
    /// @param asset The address of the main collateral token of a position
    /// @param owner The owner of the collateral
    /// @return liquidationBlock The block number when the liquidation was triggered
    /// @return collateralEDecimal The amount of collateral
    /// @return debtE18 The amount of borrowed stablecoins
    function getPosition(
        address asset,
        address owner
    ) external view returns (
        uint liquidationBlock,
        uint collateralEDecimal,
        uint debtE18
    ) {
        liquidationBlock = vault.liquidationBlock(asset, owner);

        collateralEDecimal = vault.collateralsEDecimal(asset, owner);
        debtE18 = vault.debtsE18(asset, owner);
    }

    /// @notice Returns information about a liquidating position
    /// @param asset The address of the main collateral token of a position
    /// @param owner The owner of the collateral
    /// @return liquidationBlock The block number when the liquidation was triggered
    function getLiquidationBlock(
        address asset,
        address owner
    ) external view returns (uint256) {
        return vault.liquidationBlock(asset, owner);
    }

    // UI-helpers

    // calculate liquidation price
    // can be used inside of _isLiquidatablePosition
    function liquidationPriceE18(
        address asset,
        address owner
    ) external view returns (uint) {
        uint debtE18 = vault.getTotalDebtE18(asset, owner);
        if (debtE18 == 0) return type(uint256).max;

        // TODO initialCollateralRatioE18 or liquidationRatioE18?
        uint collateralLiqPriceE18 = debtE18 * 1e18 / (managerParameters.liquidationRatioE18(asset));

        require(IERC20Metadata(asset).decimals() <= 18, "Unit Protocol: NOT_SUPPORTED_DECIMALS");

        return collateralLiqPriceE18 * 1e18 / vault.collateralsEDecimal(asset, owner) / 10 ** (18 - IERC20Metadata(asset).decimals());
    }

    /// @notice Returns Loan-To-Value in e18
    function getLTVE18(address asset, address owner) external view returns (uint256) {
        uint usdValueE18 = getCollateralUsdValueE18(asset, owner);
        if (usdValueE18 == 0) return 0;

        uint debtE18 = vault.debtsE18(asset, owner);

        return debtE18 * 1e18 / usdValueE18;
    }

    /// @notice Returns Available to Borrow
    /// we have invariant: (collateralAmountEDecimal * collateralPriceE18 / 1e18) * initialCollateralRatioE18 <= debtE18
    /// Has similar logic like in _ensurePositionCollateralization
    function getAvailableToBorrowE18(address asset, address owner) external view returns (uint256) {
        uint usdValueE18 = getCollateralUsdValueE18(asset, owner);
        uint usdLimitE18 = usdValueE18 * managerParameters.initialCollateralRatioE18(asset) / 1e18;

        uint totalDebtE18 = vault.getTotalDebtE18(asset, owner);

        if (usdLimitE18 <= totalDebtE18) return 0;

        return usdLimitE18 - totalDebtE18;
    }

    /// @notice Returns Available to Withdraw
    /// Has similar invariant like in getAvailableToBorrowE18
    function getAvailableToWithdrawE18(address asset, address owner) external view returns (uint256) {
        uint256 collateralAmountEDecimal = vault.collateralsEDecimal(asset, owner);
        uint256 collateralPriceE18 = suOracle.getUsdPrice1e18(asset);
        if (collateralPriceE18 == 0) return 0;
        uint256 debtE18 = vault.debtsE18(asset, owner);

        uint256 minCollateralAmountEDecimal = debtE18 * 1e18 / (collateralPriceE18 * managerParameters.initialCollateralRatioE18(asset));

        return collateralAmountEDecimal - minCollateralAmountEDecimal;
    }
}

