// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.0 <0.9.0;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Context.sol";

/**
 * This is fork on openzeppelin_v4 ERC20
 */
interface IGainable {
    /**
     * @dev Transfers amount of tokens from msg.sender and distributes it to all accounts
     *      proportional to the account's balances.
     *      No mints or burns happens, totalSupply remains unchanged.
     * @param amount of tokens to be distributed
     */
    function distribute(uint256 amount) external;
    event Distributed(uint256 amount, uint256 newMultiplicator1e18);
}

// TODO: check that changes with _preimageBalances work and don't break erc20 logic. Coverage should be 100%
// TODO: finish code for distribute function and test against mint, burn, and all erc20 logic

contract GainErc20v1 is Context, IERC20, IGainable {
    /**
     * Instead of _balances mapping we use _preimageBalances and special multiplicator >= 1 that:
     *      _balances[account] == _preimageBalances[account] * multiplicator
     *      multiplicator is coded as fraction _multiplicator1e18 / 1e18
     *  Original code with _balances preserved in // comments for clarity
     */
    // mapping (address => uint256) private _balances;
    mapping (address => uint256) private _preimageBalances;
    uint256 public _multiplicator1e18;

    mapping (address => mapping (address => uint256)) private _allowances;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;

    function distribute(uint256 amount) external override {
        // TODO: test this implementation
        /**
         *  because it's just re-distribution of existing tokens, total supply doesn't change
         *  total values of recipients = totalSupply - amount
         *  total values of recipients = sum(_preimageBalances of recipients) * multiplicator
         *  after distribution, totalSupply = sum(_preimageBalances of recipients) * newMultiplicator
         *  let s := sum(_preimageBalances of recipients)
         *  totalSupply - amount = s * multiplicator && totalSupply = s * newMultiplicator
         *  <=> totalSupply = s * multiplicator + amount = s * newMultiplicator
         *  <=> s = (totalSupply - amount) / multiplicator && newMultiplicator = totalSupply / s
         *
         *  now, if multiplicator increased, totalSupply != sum(_preimageBalances of everyone) * newMultiplicator
         *  so we have to decrease balanceOf(this) for this amount
         *  however, this is not burn, since totalSupply shouldn't change
         *  after that we can increase multiplicator
         */
        /// @dev  straightforward implementation
        //
        //        this.transferFrom(_msgSender(), address(this), amount);
        //        uint256 s = (_totalSupply - amount) * 1e18 / _multiplicator1e18;
        //        uint256 newMultiplicator1e18 = _totalSupply * 1e18 / s;
        //        require(newMultiplicator1e18 > _multiplicator1e18);
        //        uint256 thisBalance = _preimageBalances[address(this)] * _multiplicator1e18 / 1e18;
        //        _preimageBalances[address(this)] = (thisBalance - amount) * 1e18 / _multiplicator1e18;
        //        _multiplicator1e18 = newMultiplicator1e18;

        /// @dev simplified implementation

        this.transferFrom(_msgSender(), address(this), amount);

        // same as this.balanceOf(address(this));
        uint256 thisBalance = _preimageBalances[address(this)] * _multiplicator1e18 / 1e18;

        // @dev for ACTION_CASE[3] with transfer that can lost some tokens:
        // thisBalance =        19999999999999999999,
        // amount =             20000000000000000000,
        // _multiplicator1e18 = 1500000000000000000

        // "burn" amount
        _preimageBalances[address(this)] = (thisBalance - amount) * 1e18 / _multiplicator1e18;

        // allocate the same amount via increasing multiplicator
        uint256 newMultiplicator1e18 = _multiplicator1e18 * _totalSupply / (_totalSupply - amount);
        _multiplicator1e18 = newMultiplicator1e18;

        emit Distributed(amount, newMultiplicator1e18);
    }

    /**
     * @dev Sets the values for {name} and {symbol}.
     *
     * The defaut value of {decimals} is 18. To select a different value for
     * {decimals} you should overload it.
     *
     * All three of these values are immutable: they can only be set once during
     * construction.
     */
    constructor (string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
        _multiplicator1e18 = 1 * 1e18;
    }

    /**
     * @dev Returns the name of the token.
     */
    function name() public view virtual returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` tokens should
     * be displayed to a user as `5,05` (`505 / 10 ** 2`).
     *
     * Tokens usually opt for a value of 18, imitating the relationship between
     * Ether and Wei. This is the value {ERC20} uses, unless this function is
     * overloaded;
     *
     * NOTE: This information is only used for _display_ purposes: it in
     * no way affects any of the arithmetic of the contract, including
     * {IERC20-balanceOf} and {IERC20-transfer}.
     */
    function decimals() public view virtual returns (uint8) {
        return 18;
    }

    /**
     * @dev See {IERC20-totalSupply}.
     */
    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {IERC20-balanceOf}.
     */
    function balanceOf(address account) public view virtual override returns (uint256) {
        // return _balances[account];
        return _preimageBalances[account] * _multiplicator1e18 / 1e18;
    }

    /**
     * @dev See {IERC20-transfer}.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    /**
     * @dev See {IERC20-allowance}.
     */
    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {IERC20-approve}.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    /**
     * @dev See {IERC20-transferFrom}.
     *
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {ERC20}.
     *
     * Requirements:
     *
     * - `sender` and `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     * - the caller must have allowance for ``sender``'s tokens of at least
     * `amount`.
     */
    function transferFrom(address sender, address recipient, uint256 amount) public virtual override returns (bool) {
        address spender = _msgSender();
        _spendAllowance(sender, spender, amount);
        _transfer(sender, recipient, amount);
        return true;
    }

    /**
     * @dev Atomically increases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

    /**
     * @dev Atomically decreases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     * - `spender` must have allowance for the caller of at least
     * `subtractedValue`.
     */
    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        _approve(_msgSender(), spender, currentAllowance - subtractedValue);

        return true;
    }

    /**
     * @dev Moves tokens `amount` from `sender` to `recipient`.
     *
     * This is internal function is equivalent to {transfer}, and can be used to
     * e.g. implement automatic token fees, slashing mechanisms, etc.
     *
     * Emits a {Transfer} event.
     *
     * Requirements:
     *
     * - `sender` cannot be the zero address.
     * - `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     */
    function _transfer(address sender, address recipient, uint256 amount) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        _beforeTokenTransfer(sender, recipient, amount);

        // uint256 senderBalance = _balances[sender];
        uint256 senderBalance = _preimageBalances[sender] * _multiplicator1e18 / 1e18;
        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
        // _balances[sender] = senderBalance - amount;
        uint256 epsSender = (senderBalance - amount) - ((senderBalance - amount) * 1e18 / _multiplicator1e18) * _multiplicator1e18 / 1e18;
        _preimageBalances[sender] = (senderBalance - amount) * 1e18 / _multiplicator1e18 + epsSender;

        // _balances[recipient] += amount;
        uint256 epsRecipient = amount - ((amount) * 1e18 / _multiplicator1e18) * _multiplicator1e18 / 1e18;
        _preimageBalances[recipient] += (amount) * 1e18 / _multiplicator1e18 + epsRecipient;


        emit Transfer(sender, recipient, amount);
        _afterTokenTransfer(sender, recipient, amount);
    }

    /** @dev Creates `amount` tokens and assigns them to `account`, increasing
     * the total supply.
     *
     * Emits a {Transfer} event with `from` set to the zero address.
     *
     * Requirements:
     *
     * - `to` cannot be the zero address.
     */
    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        // _balances[account] += amount;
        _preimageBalances[account] += amount * 1e18 / _multiplicator1e18;

        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    /**
     * @dev Destroys `amount` tokens from `account`, reducing the
     * total supply.
     *
     * Emits a {Transfer} event with `to` set to the zero address.
     *
     * Requirements:
     *
     * - `account` cannot be the zero address.
     * - `account` must have at least `amount` tokens.
     */
    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        // uint256 accountBalance = _balances[account];
        uint256 accountBalance = _preimageBalances[account] * _multiplicator1e18 / 1e18;

        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        // _balances[account] = accountBalance - amount;
        _preimageBalances[account] = (accountBalance - amount) * 1e18 / _multiplicator1e18;

        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);

        _afterTokenTransfer(account, address(0), amount);
    }

    /**
     * @dev Sets `amount` as the allowance of `spender` over the `owner` s tokens.
     *
     * This internal function is equivalent to `approve`, and can be used to
     * e.g. set automatic allowances for certain subsystems, etc.
     *
     * Emits an {Approval} event.
     *
     * Requirements:
     *
     * - `owner` cannot be the zero address.
     * - `spender` cannot be the zero address.
     */
    function _approve(address owner, address spender, uint256 amount) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    /**
 * @dev Spend `amount` form the allowance of `owner` toward `spender`.
     *
     * Does not update the allowance amount in case of infinite allowance.
     * Revert if not enough allowance is available.
     *
     * Might emit an {Approval} event.
     */
    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(currentAllowance >= amount, "ERC20: insufficient allowance");
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    /**
     * @dev Hook that is called before any transfer of tokens. This includes
     * minting and burning.
     *
     * Calling conditions:
     *
     * - when `from` and `to` are both non-zero, `amount` of ``from``'s tokens
     * will be transferred to `to`.
     * - when `from` is zero, `amount` tokens will be minted for `to`.
     * - when `to` is zero, `amount` of ``from``'s tokens will be burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    /**
     * @dev Hook that is called after any transfer of tokens. This includes
     * minting and burning.
     *
     * Calling conditions:
     *
     * - when `from` and `to` are both non-zero, `amount` of ``from``'s tokens
     * has been transferred to `to`.
     * - when `from` is zero, `amount` tokens have been minted for `to`.
     * - when `to` is zero, `amount` of ``from``'s tokens have been burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}

contracts/mocks/MockGainErc20v1.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/utils/Context.sol";
import "../gain-erc20/GainErc20v1.sol";

contract MockGainErc20v1 is GainErc20v1 {
    constructor(
        string memory name,
        string memory symbol,
        address initialAccount,
        uint256 initialBalance
    ) payable GainErc20v1(name, symbol) {
        _mint(initialAccount, initialBalance);
    }

    function mint(address account, uint256 amount) public {
        _mint(account, amount);
    }

    function burn(address account, uint256 amount) public {
        _burn(account, amount);
    }

    function transferInternal(
        address from,
        address to,
        uint256 value
    ) public {
        _transfer(from, to, value);
    }

    function approveInternal(
        address owner,
        address spender,
        uint256 value
    ) public {
        _approve(owner, spender, value);
    }
}

contracts/mocks/MockV3Aggregator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

// FluxAggregator
// AggregatorV2V3Interface
contract MockV3Aggregator is AggregatorV3Interface {
  uint256 override constant public version = 0;

  uint8 override public decimals;
  int256 public latestAnswer;
  uint256 public latestTimestamp;
  uint256 public latestRound;

  mapping(uint256 => int256) public getAnswer;
  mapping(uint256 => uint256) public getTimestamp;
  mapping(uint256 => uint256) private getStartedAt;

  constructor(
    uint8 _decimals,
    int256 _initialAnswer
  ) {
    decimals = _decimals;
    updateAnswer(_initialAnswer);
  }

  function updateAnswer(
    int256 _answer
  ) public {
    latestAnswer = _answer;
    latestTimestamp = block.timestamp;
    latestRound++;
    getAnswer[latestRound] = _answer;
    getTimestamp[latestRound] = block.timestamp;
    getStartedAt[latestRound] = block.timestamp;
  }

  function updateRoundData(
    uint80 _roundId,
    int256 _answer,
    uint256 _timestamp,
    uint256 _startedAt
  ) public {
    latestRound = _roundId;
    latestAnswer = _answer;
    latestTimestamp = _timestamp;
    getAnswer[latestRound] = _answer;
    getTimestamp[latestRound] = _timestamp;
    getStartedAt[latestRound] = _startedAt;
  }

  function getRoundData(uint80 _roundId)
    external
    view
    override
    returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    )
  {
    return (
      _roundId,
      getAnswer[_roundId],
      getStartedAt[_roundId],
      getTimestamp[_roundId],
      _roundId
    );
  }

  function latestRoundData()
    external
    view
    override
    returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    )
  {
    return (
      uint80(latestRound),
      getAnswer[latestRound],
      getStartedAt[latestRound],
      getTimestamp[latestRound],
      uint80(latestRound)
    );
  }

  function description()
    external
    pure
    override
    returns (string memory)
  {
    return "";
  }  
}
