pragma solidity ^0.8.0;

import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "../interfaces/IArbitrageHelper.sol";
import "../interfaces/IExchange.sol";
import "./CalldataDecoder.sol";
import "../../periphery/contracts/access-control/SuAuthenticated.sol";
import "../../periphery/contracts/interfaces/adapters/ILPAdapter.sol";

/**
 * @title ArbitrageHelper
 * @notice Helper contract to arbitrage using Exchange module
 * @dev See IArbitrageHelper interface for full details.
 */
contract ArbitrageHelper is SuAuthenticated, IArbitrageHelper, CalldataDecoder {
    IExchange internal EXCHANGE;
    mapping(address => ILPAdapter) public lpToAdapter;

    function initialize(address _authControl, address _exchange) public initializer {
        __suAuthenticatedInit(_authControl);

        EXCHANGE = IExchange(_exchange);
    }

    function registerLP(address _lp, address _lpAdapter) public onlyDAO {
        lpToAdapter[_lp] = ILPAdapter(_lpAdapter);

        TransferHelper.safeApprove(_lp, _lpAdapter, 2**256 - 1);
    }

    /**
     * @notice Swap using Paraswap. Sell {amount2sell} token2sell to token using route encoded in paraswapData.data
     * @dev We get that data in bot using ParaSwap API (apiv5.paraswap.io)
     * Docs: https://developers.paraswap.network/api/build-parameters-for-transaction
     */
    function swapByParaswap(address token2sell, uint256 amount2sell, ParaswapData memory paraswapData) internal {
        // All token transfers are through tokenTransferProxy, so we need to make approve to this contract
        TransferHelper.safeApprove(token2sell, paraswapData.tokenTransferProxy, amount2sell);
        // solhint-disable-next-line avoid-low-level-calls
        (bool success,) = paraswapData.contractAddress.call(paraswapData.data);
        if (!success) {
            revert SwapByParaSwapError();
        }
    }
    /**
     * @notice as in swapByParaswap, look into utils/1inch.ts
     * @dev Docs: https://docs.1inch.io/docs/aggregation-protocol/api/swap-params/
     */
    function swapBy1Inch(address token2sell, uint256 amount2sell, address contractAddress, bytes memory data) internal {
        TransferHelper.safeApprove(token2sell, contractAddress, amount2sell);

        // solhint-disable-next-line avoid-low-level-calls
        (bool success,) = contractAddress.call(data);
        if (!success) {
            revert SwapBy1InchError();
        }
    }

    function swapWithFlashLoan(
        uint256 orderId,
        address token2sell,
        uint256 token2sellAmount,
        InchData memory inchData
    ) public {
        InchData[] memory inchDataArr = new InchData[](1);
        inchDataArr[0] = inchData;
        // We do a flash-loan of {token2sell} in amount of {token2sellAmount} to our contract.
        // {data} will be decoded in stableunitFillOrderCallback() - the callback after we get flash-loan
        EXCHANGE.fillOrderAsFlashLoan(
            orderId,
            token2sellAmount,
            abi.encode(FlashCallbackData({
                isLP: false,
                orderId: orderId,
                token2sell: token2sell,
                token2sellAmount: token2sellAmount,
                recipient: msg.sender,
                inchData: inchDataArr
            }))
        );
    }

    function swapLPWithFlashLoan(
        uint256 orderId,
        address token2sell,
        uint256 token2sellAmount,
        InchData[] memory inchData
    ) public {
        EXCHANGE.fillOrderAsFlashLoan(
            orderId,
            token2sellAmount,
            abi.encode(FlashCallbackData({
                isLP: true,
                orderId: orderId,
                token2sell: token2sell,
                token2sellAmount: token2sellAmount,
                recipient: msg.sender,
                inchData: inchData
            }))
        );
    }

    /**
     * @notice This is callback function from Exchange contract.
     * fillOrderAsFlashLoan call (msg.sender).executeOperation(...) after transferring loan to sender
     * @dev behaviour like in uniswapV3FlashCallback:
     * https://docs.uniswap.org/contracts/v3/guides/flash-integrations/flash-callback
     * and AAVE flash-loan callback:
     * https://github.com/aave/aave-v3-core/blob/master/contracts/flashloan/interfaces/IFlashLoanSimpleReceiver.sol
    **/
    function stableunitFillOrderCallback(
        address asset,
        uint256 amount,
        address sender,
        bytes calldata data
    ) external returns (bool) {
        FlashCallbackData memory decoded = abi.decode(data, (FlashCallbackData));

        if (msg.sender != address(EXCHANGE)) revert NotAPool(msg.sender);
        if (sender != address(this)) revert BadSender(sender);

        uint256 balanceBefore = IERC20(asset).balanceOf(address(this));

        if (decoded.isLP) {
            if (address(lpToAdapter[decoded.token2sell]) == address(0)) revert LPTokenNotRegistered(decoded.token2sell);
            if (
                !lpToAdapter[decoded.token2sell].isAdapterLP(decoded.token2sell)
            ) revert LPTokenNotSupported(decoded.token2sell);

            WithdrawResult[] memory unwrapResult = lpToAdapter[decoded.token2sell].withdraw(
                decoded.token2sell,
                decoded.token2sellAmount
            );
            uint256 inchDataNTokens = decoded.inchData.length;
            uint256 unwrapNTokens = unwrapResult.length;

            // if withdraw don't unwrap to all underlying tokens, then we need to find 1inch data to use in swap
            // else we need to use all 1inch data for each unwrapped tokens
            bool isCorrectUnwrap = inchDataNTokens == unwrapNTokens;
            for (uint16 i = 0; i < unwrapNTokens; ++i) {
                address underlineToken = unwrapResult[i].token;
                uint256 underlineTokenAmount = unwrapResult[i].amount;

                // If token2buy is underline token of LP-token token2sell => this part of order is done
                if (underlineToken != asset) {
                    if (isCorrectUnwrap) {
                        swapBy1Inch(
                            underlineToken,
                            underlineTokenAmount,
                            decoded.inchData[i].contractAddress,
                            this.changeCalldataAmount(decoded.inchData[i].data, underlineTokenAmount)
                        );
                    } else { // we need to find correct 1inch data for that unwrapped token
                        for (uint16 j = 0; j < inchDataNTokens; ++j) {
                            if (unwrapResult[i].token == decoded.inchData[j].token) {
                                swapBy1Inch(
                                    underlineToken,
                                    underlineTokenAmount,
                                    decoded.inchData[j].contractAddress,
                                    this.changeCalldataAmount(decoded.inchData[j].data, underlineTokenAmount)
                                );
                                break;
                            }
                        }
                    }
                }
            }
        } else {
            swapBy1Inch(
                decoded.token2sell,
                decoded.token2sellAmount,
                decoded.inchData[0].contractAddress,
                decoded.inchData[0].data
            );
        }

        uint256 balanceAfter = IERC20(asset).balanceOf(address(this));
        uint256 tokenBReceived = balanceAfter - balanceBefore;

        if (tokenBReceived < amount) revert SwapNotProfitable(amount - tokenBReceived);
        uint256 swapProfit = tokenBReceived - amount;

        // Return flash-loan debt to Exchange contract
        TransferHelper.safeTransfer(asset, address(EXCHANGE), amount);

        // Send swapped {asset} minus debt from flash-loan to liquidator (it's user profit)
        TransferHelper.safeTransfer(asset, decoded.recipient, swapProfit);

        return true;
    }

    uint256[45] private __gap;
}
","keccak256":"0x72b3a3db6d617789ce3594768a1798657e337714d9b3af62e8abc9d1cafe383f","license":"BSL 1.1"},"contracts/exchange/contracts/CalldataDecoder.sol":{"content":"// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

struct SwapDescription {
    address srcToken;
    address dstToken;
    address srcReceiver;
    address dstReceiver;
    uint256 amount;
    uint256 minReturnAmount;
    uint256 flags;
}

/**
 * @notice Helper abstract contract to work with calldata
**/
abstract contract CalldataDecoder {
    /**
     * @notice We need this functions to check their selector and functionSelector in calldata
     * for changeCalldataAmount() function
    **/
    /* solhint-disable no-empty-blocks */
    function uniswapV3Swap(uint256, uint256, uint256[] calldata) public pure {}
    function unoswap(address, uint256, uint256, uint256[] calldata) public pure {}
    function swap(address, SwapDescription calldata, bytes calldata, bytes calldata) public pure {}
    function clipperSwap(
        address clipperExchange,
        address srcToken,
        address dstToken,
        uint256 inputAmount,
        uint256 outputAmount,
        uint256 goodUntil,
        bytes32 r,
        bytes32 vs
    ) public pure {}
    /* solhint-enable no-empty-blocks */

    // TODO: add comment about minAmount
    // TODO: use byteToChange position in TS + check how much gas spent
    /**
      * @notice
      * Helper function to change amount parameter on callback that depends on return by 1inch API calldata for swap
      * We set minAmount to 1 to be sure, that swap will be created. Route path don't depend on that -
      * this path was calculated previously in backend part and is stored in {data}.
      * @param data - calldata to edit
      * @param newAmount - new amount that should replace amount in {data}
      * @return edited calldata with new amount inside
     **/
    function changeCalldataAmount(bytes calldata data, uint256 newAmount) public pure returns (bytes memory) {
        bytes4 functionSelector = bytes4(data[:4]);
        if (functionSelector == this.uniswapV3Swap.selector) {
            (
                /*uint256 _amount */, /*uint256 minReturn*/, uint256[] memory pools
            ) = abi.decode(data[4:], (uint256, uint256, uint256[]));
            return abi.encodeWithSignature("uniswapV3Swap(uint256,uint256,uint256[])", newAmount, 1, pools);
        }
        if (functionSelector == this.unoswap.selector) {
            (
                address token, /* uint256 _amount */, /*uint256 minReturn*/, uint256[] memory pools
            ) = abi.decode(data[4:], (address,uint256,uint256,uint256[]));
            return abi.encodeWithSignature("unoswap(address,uint256,uint256,uint256[])", token, newAmount, 1, pools);
        }
        if (functionSelector == this.swap.selector) {
            (
                address caller, SwapDescription memory description, bytes memory permit, bytes memory swapData
            ) = abi.decode(data[4:], (address, SwapDescription, bytes, bytes));
            return abi.encodeWithSignature(
                "swap(address,(address,address,address,address,uint256,uint256,uint256),bytes,bytes)",
                caller,
                SwapDescription({
                    srcToken: description.srcToken,
                    dstToken: description.dstToken,
                    srcReceiver: description.srcReceiver,
                    dstReceiver: description.dstReceiver,
                    amount: newAmount,
                    minReturnAmount: 1,
                    flags: description.flags
                }),
                permit,
                swapData
            );
        }
        if (functionSelector == this.clipperSwap.selector) {
            (
                address clipperExchange,
                address srcToken,
                address dstToken,
                /*uint256 _inputAmount*/,
                /*uint256 outputAmount*/,
                uint256 goodUntil,
                bytes32 r,
                bytes32 vs
            ) = abi.decode(data[4:], (address, address, address, uint256, uint256, uint256, bytes32, bytes32));
            return abi.encodeWithSignature(
                "clipperSwap(address,address,address,uint256,uint256,uint256,bytes32,bytes32)",
                clipperExchange,
                srcToken,
                dstToken,
                newAmount,
                1,
                goodUntil,
                r,
                vs
            );
        }

        return data;
    }
}
","keccak256":"0xfa2bc27d4ef08083b91ed485503eccd94f0340205ffdb6db53c374f858a1bd2b","license":"BSL 1.1"},"contracts/exchange/interfaces/IArbitrageHelper.sol":{"content":"// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "./IExchangeCallback.sol";

struct FlashCallbackData {
    bool isLP;
    uint256 orderId;
    address token2sell;
    uint256 token2sellAmount;
    address recipient;
    InchData[] inchData;
}

struct ParaswapData {
    address contractAddress;
    address tokenTransferProxy;
    bytes data;
}

struct InchData {
    address contractAddress;
    bytes data;
    address token; // needed only if adapter unwrap not to all underlying tokens
}

interface IArbitrageHelper is IExchangeCallback {
    /* ===================== ERRORS ===================== */
    error NotAPool(address sender);
    error SwapNotProfitable(uint256 diffAmount);
    error SwapByParaSwapError();
    error SwapBy1InchError();
    error BadSender(address sender);
    error LPTokenNotSupported(address lpToken);
    error LPTokenNotRegistered(address lpToken);

    /* ===================== EVENTS ===================== */
    // event SwapByUniswap(address tokenA, address tokenB);

    /* ==================== MUTABLE METHODS ==================== */


    /**
      * @notice Register LP to use it adapter to withdraw or get price of LP later in swapLPWithFlashLoan();
      * @param lp - address of LP token
      * @param lpAdapter - address of LPAdapter for this LP token
    **/
    function registerLP(address lp, address lpAdapter) external;

    /**
      * @notice Reference implementation of Arbitrage bot contract, that use Exchange module to get profit
      * @dev Contract receive loan from exchange module, sell it on 1inch and get profit = received amount - fee
      * @param orderId - Id of order in Exchange module to fill
      * @param token2sell - Token in order to get as flash-loan
      * @param token2sellAmount - Amount of token in order to get as flash-loan
      * @param inchData - 1inch callData and contract to swap loan
     **/
    function swapWithFlashLoan(
        uint256 orderId,
        address token2sell,
        uint256 token2sellAmount,
        InchData memory inchData
    ) external;

    /**
      * @notice The same as swapWithFlashLoan, but loan is LP-token that we need to unwrap
      * @dev Contract receive LP-token from exchange module, unwrap it and swap with 1inch all underline tokens
      * to token that exchange module need.
      * @param orderId - Id of order in Exchange module to fill
      * @param token2sell - Token in order to get as flash-loan
      * @param token2sellAmount - Amount of token in order to get as flash-loan
      * @param inchData - 1inch callData and contract array to swap every underline token of LP
     **/
    function swapLPWithFlashLoan(
        uint256 orderId,
        address token2sell,
        uint256 token2sellAmount,
        InchData[] memory inchData
    ) external;
}","keccak256":"0xa63c45bc491dd84db783e35288487579cd961e2558ab558d3dd7495afe264120","license":"BSL 1.1"},"contracts/exchange/interfaces/IExchange.sol":{"content":"// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

enum DiscountType {
    aggressive,
    middle,
    conservative
}

struct Order {
    address token2sell;
    uint256 amountLeft;
    address token2buy;
    uint256 token2buyAmount;
    address beneficiary;
    uint256 creationTimestamp;
    DiscountType discountType;
    address caller;
}

struct OrderWithId {
    Order order;
    uint256 orderId;
}

interface IExchange {
    /* ===================== ERRORS ===================== */
    error OrderExist(uint256 orderId);
    error OrderNotExist(uint256 orderId);
    error IncorrectToken(address orderToken2buy, address inToken);
    error SmallAmountLeft();
    error BadOutAmount();
    error UserIsNotBeneficiary();
    error IsNotTopStaker(address staker);
    error IsNotStaker(address staker);
    error OrderNotForThisStaker(uint256 orderId, address staker);
    error NotEnoughReturnValue();
    error CallbackError();

    /* ======================== EVENTS ========================= */
    event OrderAdded(uint256 orderId);
    event OrderCanceled(uint256 orderId);
    event OrderFilled(uint256 orderId);
    event OrderFilledPartially(uint256 orderId);
    event LpOrdersAdded(address LPToken, uint256[] orderIds);

    /* ==================== MUTABLE METHODS ==================== */

    /**
      * @notice Add order to swap {token2sell} to {token2buy}.
      * @dev This swap is async, because depends on how arbitrage bots will fill this order. Emits OrderAdded() event.
      * Before addOrder user needs to approve token2sell for exchange contract
      * @param token2sell - Address of token to sell
      * @param amount2sell - Amount of {token2sell} to sell
      * @param token2buy - Address of token to buy
      * @param beneficiary - Address that will receive {token2buy} after filling order.
      * @param discountType - Speed of decreasing swap price. { 0 - aggressive, 1 - middle, 2 - conservative }
      * @param collateralJoin - address of collateral adapter
     **/
    function addOrder (
        address token2sell,
        uint256 amount2sell,
        address token2buy,
        address beneficiary,
        DiscountType discountType,
        address collateralJoin
    ) external returns (uint256);

    /**
      * @notice Only user who place order or who is beneficiary can cancelOrder();
      * @param orderId - Id of order to cancel.
     **/
    function cancelOrder (uint256 orderId) external;

    /**
      * @notice Fill order with flash-loan like logic
      * @dev Caller contract receive orders {token2sell} and after that his stableunitFillOrderCallback() is called.
      * At the end of thi callback contract should send some amount of another token back to Exchange contract.
      * @param orderId - Id of order to fill.
      * @param inAmount - Amount of {token2sell} to receive
      * @param data - encoded data to pass to callback function
     **/
    function fillOrderAsFlashLoan (
        uint256 orderId,
        uint256 inAmount,
        bytes memory data
    ) external returns (uint256 outAmount);

    /**
      * @notice Function to update price curve - how fast {token2sell} price will decrease/increase
      * @param discountType - discount type to change
      * @param newPriceCurve - parameters of formula
     **/
    function setPriceCurve(DiscountType discountType, uint256[2] calldata newPriceCurve) external;

    /* ==================== VIEW METHODS ==================== */
    /**
      * @return The amount of outcome that caller will receive if he will fill {orderId} with {inAmount} of {token2sell}
     **/
    function getExpectedOrderOutcome (uint256 orderId, uint256 inAmount) external view returns (uint256);

    /**
      * @return Order with id = {orderId}
     **/
    function getOrder (uint256 orderId) external view returns (Order memory);

    /**
      * @return The amount of not full-filled orders
     **/
    function getOrdersCount () external view returns (uint256);

    /**
      * @return The array of all not-filled orders. May be deleted after adding GraphQL for orderIds
     **/
    function getAllOrders (uint256 offset, uint256 limit) external view returns (OrderWithId[] memory);
}
