pragma solidity ^0.8.0;

import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";

import {Math} from "../utils/Math.sol";
import "../interfaces/ISafeEngine.sol";
import "../interfaces/ILiquidationEngine.sol";
import {AuthWithContractEnabled} from "../utils/AuthWithContractEnabled.sol";
import "../interfaces/IAccountingEngine.sol";
import "../../periphery/contracts/interfaces/oracle/ISuOracle.sol";

// FIXME: This contract was altered compared to the production version.
// It doesn't use LibNote anymore.
// New deployments of this contract will need to include custom events (TO DO).

contract AccountingEngine is Math, IAccountingEngine {
    // --- Auth ---
    mapping (address => uint) public authorizedAccounts;
    function addAuthorization(address usr) external isAuthorized { require(contractEnabled == 1, "AccountingEngine/not-contractEnabled"); authorizedAccounts[usr] = 1; }
    function removeAuthorization(address usr) external isAuthorized { authorizedAccounts[usr] = 0; }
    modifier isAuthorized {
        require(authorizedAccounts[msg.sender] == 1, "AccountingEngine/not-authorized");
        _;
    }

    // --- Data ---
    ISafeEngine public safeEngine;
    ILiquidationEngine public liquidationEngine;
    ISuOracle public oracle;
    address public stablePro;

    mapping (uint256 => uint256) public debtQueue;  // debt queue
    uint256 public totalQueuedDebt;   // Queued debt [rad]
    uint256 public contractEnabled;

    // --- Init ---
    constructor(address safeEngine_, address liquidationEngine_, address oracle_, address stablePro_) public {
        authorizedAccounts[msg.sender] = 1;
        safeEngine = ISafeEngine(safeEngine_);
        liquidationEngine = ILiquidationEngine(liquidationEngine_);
        oracle = ISuOracle(oracle_);
        stablePro = stablePro_;
        contractEnabled = 1;
    }

    /**
    * @notice Push debt (that the system tries to cover with collateral auctions) to a queue
     * @dev Debt is locked in a queue to give the system enough time to auction collateral
     *      and gather surplus
     * @param amountToRaise Amount of debt to push
     */
    function pushDebtToQueue(uint amountToRaise) external isAuthorized {
        debtQueue[block.timestamp] = _add(debtQueue[block.timestamp], amountToRaise);
        totalQueuedDebt = _add(totalQueuedDebt, amountToRaise);
    }
    /**
     * @notice A block of debt can be popped from the queue after popDebtDelay seconds passed since it was
     *         added there
     * @param era Timestamp of the block of debt that should be popped out
     */
    function popDebtFromQueue(uint era) external {
        // require(_add(era, popDebtDelay) <= block.timestamp, "AccountingEngine/popDebtDelay-not-finished");
        totalQueuedDebt = _sub(totalQueuedDebt, debtQueue[era]);
        debtQueue[era] = 0;
    }

    // Debt settlement
//    /**
//     * @notice Destroy an equal amount of coins and debt
//     * @dev We can only destroy debt that is not locked in the queue and also not in a debt auction
//     * @param rad Amount of coins/debt to destroy (number with 45 decimals)
//    **/
//    function settleDebt(uint rad) external {
//        require(rad <= safeEngine.coinBalance(address(this)), "AccountingEngine/insufficient-surplus");
//        require(rad <= _sub(_sub(safeEngine.debtBalance(address(this)), totalQueuedDebt), totalOnAuctionDebt), "AccountingEngine/insufficient-debt");
//        safeEngine.settleDebt(rad);
//    }

    /**
     * @notice Disable this contract (normally called by Global Settlement)
     * @dev When we disable, the contract tries to settle as much debt as possible (if there's any) with
            any surplus that's left in the system. After erasing debt, the contract will either transfer any
            remaining surplus right away (if disableCooldown == 0) or will only record the timestamp when
            it was disabled
    **/
    function disableContract() external isAuthorized {
        require(contractEnabled == 1, "AccountingEngine/not-contractEnabled");
        contractEnabled = 0;
        totalQueuedDebt = 0;
        safeEngine.settleDebt(min(safeEngine.coinBalance(address(this)), safeEngine.debtBalance(address(this))));
        emit DisableContract();
    }

    // user need to approve StablePro
    function swapStableProToStablecoin(
        address stablecoinToken,
        uint256 stableProAmountE18
    ) external {
        if (!liquidationEngine.hasWhitelistStablecoin(stablecoinToken)) revert BadStablecoin(stablecoinToken);

        uint256 stablecoinAmountEDecimal = stableProAmountE18 * 1e18 / oracle.getFiatPrice1e18(stablecoinToken);

        TransferHelper.safeTransferFrom(stablePro, msg.sender, address(this), stableProAmountE18);
        TransferHelper.safeTransfer(stablecoinToken, msg.sender, stablecoinAmountEDecimal);

        // middlewareDebt -= int256(stableProAmountE18);
        // TODO: should we settleDebt and do some manipulations in safeEngine?

        emit StableProSwapped(stableProAmountE18);
    }
}

