pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";

import {AuthWithoutContractEnabled} from "../utils/AuthWithoutContractEnabled.sol";
import "../interfaces/ILiquidationEngine.sol";
import "../interfaces/ISafeEngine.sol";
import "../interfaces/ICollateralJoin.sol";
import "../../exchange/interfaces/IExchange.sol";

import {Math} from "../utils/Math.sol";

interface AccountingEngineLike {
    function pushDebtToQueue(uint256) external;
}

contract LiquidationEngine is AuthWithoutContractEnabled, ILiquidationEngine, Math {
    ISafeEngine immutable public safeEngine;  // CDP Engine

    mapping (bytes32 => CollateralType) public collateralTypes;

    AccountingEngineLike public accountingEngine;   // Debt Engine
    uint256 public GlobalLiquidationLimit;  // Max DAI needed to cover debt+fees of active auctions [rad]
    uint256 public ActiveLiquidationAmount;  // Amt DAI needed to cover debt+fees of active auctions [rad]
    uint256 public LiquidatorReward;  // Reward in percent (1e18 == 1) for liquidation [wad]
    address public liquidator;  // Liquidator, Exchange module

    // --- Init ---
    constructor(address safeEngine_) public {
        safeEngine = ISafeEngine(safeEngine_);
        contractEnabled = 1;
        authorizedAccounts[msg.sender] = 1;
        emit AddAuthorization(msg.sender);
    }

    // --- Administration ---
    function modifyParameters(bytes32 what, address data) external isAuthorized {
        if (what == "accountingEngine") accountingEngine = AccountingEngineLike(data);
        else if (what == "liquidator") liquidator = data;
        else revert("LiquidationEngine/file-unrecognized-param");
        emit ModifyParameters(what, data);
    }
    function modifyParameters(bytes32 what, uint256 data) external isAuthorized {
        if (what == "GlobalLiquidationLimit") GlobalLiquidationLimit = data;
        else if (what == "LiquidatorReward") LiquidatorReward = data;
        else revert("LiquidationEngine/file-unrecognized-param");
        emit ModifyParameters(what, data);
    }
    function modifyParameters(bytes32 collateralType, bytes32 what, uint256 data) external isAuthorized {
        if (what == "liquidationPenalty") {
            require(data >= WAD, "LiquidationEngine/file-liquidationPenalty-lt-WAD");
            collateralTypes[collateralType].liquidationPenalty = data;
        } else if (what == "liquidationLimit") collateralTypes[collateralType].liquidationLimit = data;
        else revert("LiquidationEngine/file-unrecognized-param");
        emit ModifyParameters(collateralType, what, data);
    }

    function liquidationPenalty(bytes32 collateralType) external view returns (uint256) {
        return collateralTypes[collateralType].liquidationPenalty;
    }

    // --- CDP Liquidation: all bark and no bite ---
    function liquidateCDP(bytes32 collateralType, address cdp, address incentivesReceiver, address collateralJoin) external returns (uint256 id) {
        require(contractEnabled == 1, "LiquidationEngine/not-contractEnabled");

        (uint256 collateralAmount, uint256 generatedDebt) = safeEngine.safes(collateralType, cdp);
        CollateralType memory _cType = collateralTypes[collateralType];
        uint256 deltaGeneratedDebt;
        uint256 accumulatedRate;
        uint256 debtFloor;
        {
            uint256 safetyPrice;
            (, accumulatedRate, safetyPrice,, debtFloor) = safeEngine.collateralTypes(collateralType);
            require(safetyPrice > 0 && _mul(collateralAmount, safetyPrice) < _mul(generatedDebt, accumulatedRate), "LiquidationEngine/not-unsafe");

            // Get the minimum value between:
            // 1) Remaining space in the general GlobalLiquidationLimit
            // 2) Remaining space in the collateral liquidationLimit
            require(GlobalLiquidationLimit > ActiveLiquidationAmount && _cType.liquidationLimit > _cType.activeLiquidationAmount, "LiquidationEngine/liquidation-limit-hit");
            uint256 room = min(GlobalLiquidationLimit - ActiveLiquidationAmount, _cType.liquidationLimit - _cType.activeLiquidationAmount);

            // uint256.max()/(RAD*WAD) = 115,792,089,237,316
            deltaGeneratedDebt = min(generatedDebt, _mul(room, WAD) / accumulatedRate / _cType.liquidationPenalty);

            // Partial liquidation edge case logic
            if (generatedDebt > deltaGeneratedDebt) {
                if (_mul(generatedDebt - deltaGeneratedDebt, accumulatedRate) < debtFloor) {

                    // If the leftover Vault would be dusty, just liquidate it entirely.
                    // This will result in at least one of activeLiquidationAmount_i > liquidationLimit_i or ActiveLiquidationAmount > GlobalLiquidationLimit becoming true.
                    // The amount of excess will be bounded above by ceiling(debtFloor_i * liquidationPenalty_i / WAD).
                    // This deviation is assumed to be small compared to both liquidationLimit_i and GlobalLiquidationLimit, so that
                    // the extra amount of target DAI over the limits intended is not of economic concern.
                    deltaGeneratedDebt = generatedDebt;
                } else {

                    // In a partial liquidation, the resulting auction should also be non-dusty.
                    require(_mul(deltaGeneratedDebt, accumulatedRate) >= debtFloor, "LiquidationEngine/dusty-auction-from-partial-liquidation");
                }
            }
        }

        uint256 deltaCollateral = _mul(collateralAmount, deltaGeneratedDebt) / generatedDebt;

        require(deltaCollateral > 0, "LiquidationEngine/null-auction");
        require(deltaGeneratedDebt <= 2**255 && deltaCollateral <= 2**255, "LiquidationEngine/overflow");

        uint256 rewardCollateral = _mul(deltaCollateral, LiquidatorReward) / WAD;
        uint256 collateral2sell = _sub(deltaCollateral, rewardCollateral);
        safeEngine.confiscateSAFECollateralAndDebt(
            collateralType, cdp, incentivesReceiver, address(accountingEngine), -int256(rewardCollateral), 0
        );
        safeEngine.confiscateSAFECollateralAndDebt(
            collateralType, cdp, liquidator, address(accountingEngine), -int256(collateral2sell), -int256(deltaGeneratedDebt)
        );

        uint256 due = _mul(deltaGeneratedDebt, accumulatedRate);
        accountingEngine.pushDebtToQueue(due);

        {   // Avoid stack too deep
            // This calcuation will overflow if deltaGeneratedDebt*rate exceeds ~10^14
            uint256 amountToRaise = _mul(due, _cType.liquidationPenalty) / WAD;
            ActiveLiquidationAmount = _add(ActiveLiquidationAmount, amountToRaise);
            collateralTypes[collateralType].activeLiquidationAmount = _add(_cType.activeLiquidationAmount, amountToRaise);

//            id = AuctionLike(_cType.liquidator).startAuction({
//                debt: amountToRaise,
//                collateralToSell: deltaCollateral,
//                colReceiver: cdp,
//                incentivesReceiver: incentivesReceiver
//            });

            // TODO: create algorithm, how to choose stablecoin
            address stablecoin = EnumerableSetUpgradeable.at(_stablecoinWhitelist, 0);
            id = IExchange(liquidator).addOrder(
                address(ICollateralJoin(collateralJoin).collateral()),
                collateral2sell,
                // amountToRaise, TODO: add minAmount?
                stablecoin,
                address(accountingEngine),
                DiscountType.middle, // TODO: should depend on HF
                collateralJoin
            );
        }

        emit LiquidateCDP(collateralType, cdp, deltaCollateral, deltaGeneratedDebt, due, liquidator, id);
    }

    function reduceActiveLiquidationAmount(bytes32 collateralType, uint256 rad) external isAuthorized {
        ActiveLiquidationAmount = _sub(ActiveLiquidationAmount, rad);
        collateralTypes[collateralType].activeLiquidationAmount = _sub(collateralTypes[collateralType].activeLiquidationAmount, rad);
        emit ReduceActiveLiquidationAmount(collateralType, rad);
    }

    // --- Stablecoin Whitelist ---
    EnumerableSetUpgradeable.AddressSet internal _stablecoinWhitelist;

    function addWhitelistStablecoin(address _stablecoin) public isAuthorized {
        EnumerableSetUpgradeable.add(_stablecoinWhitelist, _stablecoin);
    }

    function removeWhitelistStablecoin(address _stablecoin) public isAuthorized {
        EnumerableSetUpgradeable.remove(_stablecoinWhitelist, _stablecoin);
    }

    function hasWhitelistStablecoin(address _stablecoin) public view returns(bool) {
        return EnumerableSetUpgradeable.contains(_stablecoinWhitelist, _stablecoin);
    }
}

