pragma solidity ^0.8.0;

/**
 * @title IDssMiddleware
 **/
interface IDssCdpManager {

    /* ==================== STRUCTS ==================== */

    struct List {
        uint prev;
        uint next;
    }

    /* ==================== EVENTS ===================== */

    event NewCdp(address indexed usr, address indexed own, uint indexed cdp);


    /* ==================== METHODS ==================== */

    // @notice Allow/disallow a usr address to manage the cdp.
    function cdpAllow(uint cdp, address usr, uint ok) external;

    // @notice Allow/disallow a usr address to quit to the the sender urn.
    function allowHandler(address usr, uint ok) external;

    // @notice Open a new cdp for a given usr address.
    function openSafe(bytes32 collateralType, address usr) external returns (uint);

    // @notice Give the cdp ownership to a dst address.
    function transferSafeOwnership(uint cdp, address dst) external;

    // @notice Modify the cdp keeping the generated DAI or collateral freed in the cdp urn address.
    function modifySAFECollateralization(uint cdp, int deltaCollateral, int deltaDebt) external;

    // @notice Transfer wad amount of cdp collateral from the cdp address to a dst address.
    function transferCollateral(uint cdp, address dst, uint wad) external;

    // @notice Transfer wad amount of any type of collateral (ilk) from the cdp address to a dst address.
    // This function has the purpose to take away collateral from the system that doesn't correspond to the cdp but was sent there wrongly.
    function transferCollateral(bytes32 ilk, uint cdp, address dst, uint wad) external;

    // @notice Transfer wad amount of DAI from the cdp address to a dst address.
    function transferInternalCoins(uint cdp, address dst, uint rad) external;

    // @notice Quit the system, migrating the cdp (lockedCollateral, generatedDebt) to a different dst urn
    function quit(uint cdp, address dst) external;

    // @notice Import a position from src urn to the urn owned by cdp
    function enter(address src, uint cdp) external;

    // @notice Move a position from cdpSrc urn to the cdpDst urn
    function moveSAFE(uint cdpSrc, uint cdpDst) external;
}

contracts/makerdao/interfaces/ILiquidati.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

/**
 * @title ITaxCollector
 **/
interface ILiquidationEngine {
    /* ==================== STRUCTS ==================== */

    struct CollateralType {
        uint256 liquidationPenalty;  // Liquidation Penalty                                          [wad]
        uint256 liquidationLimit;  // Max DAI needed to cover debt+fees of active auctions per collateralType [rad]
        uint256 activeLiquidationAmount;  // Amt DAI needed to cover debt+fees of active auctions per collateralType [rad]
    }

    /* ==================== EVENTS ===============-===== */

    event AddAuthorization(address indexed usr);
    event RemoveAuthorization(address indexed usr);

    event ModifyParameters(bytes32 indexed what, uint256 data);
    event ModifyParameters(bytes32 indexed what, address data);
    event ModifyParameters(bytes32 indexed collateralType, bytes32 indexed what, uint256 data);
    event ModifyParameters(bytes32 indexed collateralType, bytes32 indexed what, address liquidator);

    event LiquidateCDP(
        bytes32 indexed collateralType,
        address indexed cdp,
        uint256 lockedCollateral,
        uint256 generatedDebt,
        uint256 due,
        address liquidator,
        uint256 indexed id
    );
    event ReduceActiveLiquidationAmount(bytes32 indexed collateralType, uint256 rad);

    /* ==================== MUTABLE METHODS ==================== */

    // --- Administration ---
    function modifyParameters(bytes32 what, uint data) external;
    function modifyParameters(bytes32 what, address data) external;
    function modifyParameters(bytes32 collateralType, bytes32 what, uint data) external;

    /**
      * @notice Liquidate a Vault and start a Dutch auction to sell its collateral for DAI.
      * @dev The entire Vault will be liquidated except when the target amount of DAI to be raised in
      * the resulting auction (debt of Vault + liquidation penalty) causes either ActiveLiquidationAmount to exceed
      * GlobalLiquidationLimit or collateralType.activeLiquidationAmount to exceed collateralType.liquidationLimit by an economically significant amount. In that
      * case, a partial liquidation is performed to respect the global and per-collateralType limits on
      * outstanding DAI target. The one exception is if the resulting auction would likely
      * have too little collateral to be interesting to Keepers (debt taken from Vault < collateralType.debtFloor),
      * in which case the function reverts. Please refer to the code and comments within if
      * more detail is desired.
      * @param collateralType - collateral type
      * @param cdp - address of cdp position
      * @param incentivesReceiver - address that will receive the liquidation reward, if any.
      * @param collateralJoin - address of collateral adapter
    **/
    function liquidateCDP(bytes32 collateralType, address cdp, address incentivesReceiver, address collateralJoin) external returns (uint256 id);

    function reduceActiveLiquidationAmount(bytes32 collateralType, uint256 rad) external;

    // --- Whitelist ---
    function addWhitelistStablecoin(address _stablecoin) external;
    function removeWhitelistStablecoin(address _stablecoin) external;

    /* ==================== VIEW METHODS ==================== */

    function liquidationPenalty(bytes32 collateralType) external view returns (uint256);

    function hasWhitelistStablecoin(address _stablecoin) external view returns(bool);
}

