contracts/makerdao/utils/AuthWithContrac.content:
// SPDX-License-Identifier: AGPL-3.0-or-later

pragma solidity ^0.8.0;

import {IAuthWithContractEnabled} from "../interfaces/IAuthWithContractEnabled.sol";

abstract contract AuthWithContractEnabled is IAuthWithContractEnabled {
    error NotAuthorized();
    error NotContractEnabled();

    // --- Auth ---
    mapping (address => uint) public authorizedAccounts;
    function addAuthorization(address usr) external isAuthorized {
        if (contractEnabled != 1) revert NotContractEnabled();
        authorizedAccounts[usr] = 1;
    }
    function removeAuthorization(address usr) external isAuthorized {
        if (contractEnabled != 1) revert NotContractEnabled();
        authorizedAccounts[usr] = 0;
    }
    modifier isAuthorized {
        if (authorizedAccounts[msg.sender] != 1) revert NotAuthorized();
        _;
    }

    uint256 public contractEnabled; // Access flag, indicates whether this contract is still active
    function disableContract() external isAuthorized {
        contractEnabled = 0;
        emit DisableContract();
    }
}

