// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.6.0;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';

library TransferHelper {
    /// @notice Transfers tokens from the targeted address to the given destination
    /// @notice Errors with 'STF' if transfer fails
    /// @param token The contract address of the token to be transferred
    /// @param from The originating address from which the tokens will be transferred
    /// @param to The destination address of the transfer
    /// @param value The amount to be transferred
    function safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        (bool success, bytes memory data) =
            token.call(abi.encodeWithSelector(IERC20.transferFrom.selector, from, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'STF');
    }

    /// @notice Transfers tokens from msg.sender to a recipient
    /// @dev Errors with ST if transfer fails
    /// @param token The contract address of the token which will be transferred
    /// @param to The recipient of the transfer
    /// @param value The value of the transfer
    function safeTransfer(
        address token,
        address to,
        uint256 value
    ) internal {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(IERC20.transfer.selector, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'ST');
    }

    /// @notice Approves the stipulated contract to spend the given allowance in the given token
    /// @dev Errors with 'SA' if transfer fails
    /// @param token The contract address of the token to be approved
    /// @param to The target of the approval
    /// @param value The amount of the given token the target will be allowed to spend
    function safeApprove(
        address token,
        address to,
        uint256 value
    ) internal {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(IERC20.approve.selector, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'SA');
    }

    /// @notice Transfers ETH to the recipient address
    /// @dev Fails with `STE`
    /// @param to The destination of the transfer
    /// @param value The value to be transferred
    function safeTransferETH(address to, uint256 value) internal {
        (bool success, ) = to.call{value: value}(new bytes(0));
        require(success, 'STE');
    }
}

contracts/emergency/CircuitBreakerUnopti.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/ICircuitBreaker.sol";
import "../periphery/contracts/access-control/SuAuthenticated.sol";

/**
 * @title CircuitBreakerUnoptimized
 * @dev This is gas-unoptimized version for testing.
 * See the associated interface for full details.
 */
contract CircuitBreakerUnoptimized is ICircuitBreaker, SuAuthenticated {
    using SafeERC20 for IERC20;

    uint256 public constant WITHDRAWAL_PERIOD = 24 hours;
    /**
     * @dev Here we use sliding window algorithm (https://www.geeksforgeeks.org/window-sliding-technique/)
     * to count sum of funds passed during 1 day
     */
    uint256 public constant SLIDING_WINDOW_PARTS = 4;
    uint256 public constant SLIDING_WINDOW_SIZE = WITHDRAWAL_PERIOD / SLIDING_WINDOW_PARTS;
    uint256 public constant TRIGGER_RATE_LIMIT_LENGTH = 356 days;

    uint256 public fundsFlowThreshold;
    mapping(uint256 => uint256) public liqInPeriod;
    State public state;
    uint256 public triggeredRateLimitEndTimestamp;

    /* ==================== MUTABLE METHODS ==================== */
    function initialize(address _authControl) public initializer {
        __suAuthenticatedInit(_authControl);
    }

    // TODO: add onlyRole for our lending protocol
    function controlledTransfer(address token, address to, uint256 amount, uint256 value) external {
        if (state == State.Triggered) {
            if (block.timestamp < triggeredRateLimitEndTimestamp) {
                revert Triggered();
            } else {
                state = State.Ok;
                emit TriggeredRateLimitExpired();
            }
        }
        addFundsFlow(value);
        uint256 totalFundsFlow = totalLiquidityInPeriod();
        if (totalFundsFlow > fundsFlowThreshold) {
            revert ThresholdExceeded();
        } else
            if (totalFundsFlow > fundsFlowThreshold/2) {
                if (state != State.Warning) {
                    state = State.Warning;
                    emit ThresholdWarning();
                }
            }
        IERC20(token).safeTransferFrom(msg.sender, to, amount);
    }

    function addFundsFlow(uint256 value) internal {
        uint256 windowId = getWindowId();
        liqInPeriod[windowId] += value;
    }

    function setFundsFlowThreshold(uint256 _fundsFlowThreshold) external onlyDAO {
        fundsFlowThreshold = _fundsFlowThreshold;
    }

    function reset() external onlyAdmin {
        state = State.Ok;
        // flush funds flow
        uint256 windowId = getWindowId();
        for (uint256 i = 0; i < SLIDING_WINDOW_PARTS; i++) {
            liqInPeriod[windowId-i] = 0;
        }

        emit ResetByAdmin();
    }

    function triggerRateLimit() external onlyAlerter {
        state = State.Triggered;
        triggeredRateLimitEndTimestamp = block.timestamp + TRIGGER_RATE_LIMIT_LENGTH;

        emit RateLimitTriggered();
    }

    /* ==================== VIEW METHODS ==================== */
    function totalLiquidityInPeriod() public view returns (uint256) {
        uint256 windowId = getWindowId();
        uint256 totalFlow;
        for (uint256 i = 0; i < SLIDING_WINDOW_PARTS; i++) {
            totalFlow += liqInPeriod[windowId-i];
        }
        return totalFlow;
    }

    function getWindowId() public view returns (uint256) {
        return block.timestamp / SLIDING_WINDOW_SIZE;
    }
}

