// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "../interfaces/ISuPausable.sol";

/**
 * @dev Abstract contract which allows other contracts to use modifiers
 */
abstract contract SuPausableAbstract {
    error AlreadyPaused();
    error NotPaused();

    ISuPausable internal suPausable;

    function __suPausableAbstractInit(address _suPausable) internal {
        suPausable = ISuPausable(_suPausable);
    }

    /**
     * @dev Modifier to make a function callable only when the contract is not paused.
     */
    modifier whenNotPaused(address collateral) {
        if (suPausable.isPaused(collateral)) revert AlreadyPaused();
        _;
    }

    /**
     * @dev Modifier to make a function callable only when the contract is paused.
     */
    modifier whenPaused(address collateral) {
        if (!suPausable.isPaused(collateral)) revert NotPaused();
        _;
    }
}

