// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.0 <0.9.0;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Context.sol";

/**
 * This is fork on openzeppelin_v4 ERC20
 */
interface IGainable {
    /**
     * @dev Transfers amount of tokens from msg.sender and distributes it to all accounts
     *      proportional to the account's balances.
     *      No mints or burns happens, totalSupply remains unchanged.
     * @param amount of tokens to be distributed
     */
    function distribute(uint256 amount) external;
    event Distributed(uint256 amount, uint256 newMultiplicator1e18);
}

// TODO: check that changes with _preimageBalances work and don't break erc20 logic. Coverage should be 100%
// TODO: finish code for distribute function and test against mint, burn, and all erc20 logic

contract GainErc20v1 is Context, IERC20, IGainable {
    /**
     * Instead of _balances mapping we use _preimageBalances and special multiplicator >= 1 that:
     *      _balances[account] == _preimageBalances[account] * multiplicator
     *      multiplicator is coded as fraction _multiplicator1e18 / 1e18
     *  Original code with _balances preserved in // comments for clarity
     */
    // mapping (address => uint256) private _balances;
    mapping (address => uint256) private _preimageBalances;
    uint256 public _multiplicator1e18;

    mapping (address => mapping (address => uint256)) private _allowances;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;

    function distribute(uint256 amount) external {
        // TODO: test this implementation
        /**
         *  because it's just re-distribution of existing tokens, total supply doesn't change
         *  total values of recipients = totalSupply - amount
         *  total values of recipients = sum(_preimageBalances of recipients) * multiplicator
         *  after distribution, totalSupply = sum(_preimageBalances of recipients) * newMultiplicator
         *  let s := sum(_preimageBalances of recipients)
         *  totalSupply - amount = s * multiplicator && totalSupply = s * newMultiplicator
         *  <=> totalSupply = s * multiplicator + amount = s * newMultiplicator
         *  <=> s = (totalSupply - amount) / multiplicator && newMultiplicator = totalSupply / s
         *
         *  now, if multiplicator increased, totalSupply != sum(_preimageBalances of everyone) * newMultiplicator
         *  so we have to decrease balanceOf(this) for this amount
         *  however, this is not burn, since totalSupply shouldn't change
         *  after that we can increase multiplicator
         */
        // @dev  straightforward implementation
        //
        //        this.transferFrom(_msgSender(), address(this), amount);
        //        uint256 s = (_totalSupply - amount) * 1e18 / _multiplicator1e18;
        //        uint256 newMultiplicator1e18 = _totalSupply * 1e18 / s;
        //        require(newMultiplicator1e18 > _multiplicator1e18);
        //        uint256 thisBalance = _preimageBalances[address(this)] * _multiplicator1e18 / 1e18;
        //        _preimageBalances[address(this)] = (thisBalance - amount) * 1e18 / _multiplicator1e18;
        //        _multiplicator1e18 = newMultiplicator1e18;

        // @dev simplified implementation

        this.transferFrom(_msgSender(), address(this), amount);

        // same as this.balanceOf(address(this));
        uint256 thisBalance = _preimageBalances[address(this)] * _multiplicator1e18 / 1e18;

        // @dev for ACTION_CASE[3] with transfer that can lost some tokens:
        // thisBalance =        19999999999999999999,
        // amount =             20000000000000000000,
        // _multiplicator1e18 = 1500000000000000000

        // "burn" amount
        _preimageBalances[address(this)] = (thisBalance - amount) * 1e18 / _multiplicator1e18;

        // allocate the same amount via increasing multiplicator
        uint256 newMultiplicator1e18 = _multiplicator1e18 * _totalSupply / (_totalSupply - amount);
        _multiplicator1e18 = newMultiplicator1e18;

        emit Distributed(amount, newMultiplicator1e18);
    }

    //===================================================================
    // We need to disable custom-errors rule to behave like standard ERC20 token
    /* solhint-disable custom-errors */

    /**
     * @dev Sets the values for {name} and {symbol}.
     *
     * The defaut value of {decimals} is 18. To select a different value for
     * {decimals} you should overload it.
     *
     * All three of these values are immutable: they can only be set once during
     * construction.
     */
    constructor (string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
        _multiplicator1e18 = 1 * 1e18;
    }

    /**
     * @dev Returns the name of the token.
     */
    function name() public view virtual returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` tokens should
     * be displayed to a user as `5,05` (`505 / 10 ** 2`).
     *
     * Tokens usually opt for a value of 18, imitating the relationship between
