    /**
     * @dev Sets `amount` as the allowance of `spender` over the `owner` s tokens.
     *
     * This internal function is equivalent to `approve`, and can be used to
     * e.g. set automatic allowances for certain subsystems, etc.
     *
     * Emits an {Approval} event.
     *
     * Requirements:
     *
     * - `owner` cannot be the zero address.
     * - `spender` cannot be the zero address.
     */
    function _approve(address owner, address spender, uint256 amount) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    /**
     * @dev Spend `amount` form the allowance of `owner` toward `spender`.
     *
     * Does not update the allowance amount in case of infinite allowance.
     * Revert if not enough allowance is available.
     *
     * Might emit an {Approval} event.
     */
    function _spendAllowance(address owner, address spender, uint256 amount) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(currentAllowance >= amount, "ERC20: insufficient allowance");
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    /**
     * @dev Hook that is called before any transfer of tokens. This includes
     * minting and burning.
     *
     * Calling conditions:
     *
     * - when `from` and `to` are both non-zero, `amount` of ``from``'s tokens
     * will be transferred to `to`.
     * - when `from` is zero, `amount` tokens will be minted for `to`.
     * - when `to` is zero, `amount` of ``from``'s tokens will be burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _beforeTokenTransfer(address from, address to, uint256) internal {
        if (from != address(0)) {
            harvest(from);
        }
        if (to != address(0)) {
            harvest(to);
        }
    }

    /**
     * @dev Hook that is called after any transfer of tokens. This includes
     * minting and burning.
     *
     * Calling conditions:
     *
     * - when `from` and `to` are both non-zero, `amount` of ``from``'s tokens
     * has been transferred to `to`.
     * - when `from` is zero, `amount` tokens have been minted for `to`.
     * - when `to` is zero, `amount` of ``from``'s tokens have been burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    // solhint-disable-next-line no-empty-blocks
    function _afterTokenTransfer(address from, address to, uint256 amount) internal virtual {}

    /* solhint-enable custom-errors */
    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[45] private __gap;
}

contracts/interfaces/ICircuitBreaker.sol.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "../lending/SuVaultParameters.sol";
import "../interfaces/ISuVaultParameters.sol";
import "../reward/RewardChefV2.sol";

/**
 * @dev
 * Ok - protocol is working well, there is no abnormal withdrawals
 * Warning - users withdraws more than half of threshold, dev team should be notified about that
 * Triggered - admin trigger rate limit, so protocol can't withdraw tokens.
 * After some time it could be reset automatically, or manually by admin/DAO
*/
enum State { Ok, Warning, Triggered }

/**
 * @title Circuit Breaker
 * @dev This contract is a circuit breaker designed for financial safety in DeFi protocols.
 * It acts as a middleware, monitoring the flow of funds through itself and triggering an interrupt
 * if the flow of funds exceeds a certain threshold within a defined period. The threshold could be set
 * as a percentage of the Total Value Locked (TVL) per day, for example, 15% of TVL. Upon an interrupt,
 * there are three potential outcomes:
 *
 * 1. The admin manually resets the circuit breaker. In this case, all held funds are released immediately.
 *
 * 2. No action is taken by the admin or the DAO within the threshold period. In this case, all held funds
 * are released automatically when the threshold period expires.
 *
 * 3. The admin escalates the alarm. In this case, the DAO must resolve the situation via voting.
 *
 * To prevent sudden halts in fund flow, the contract emits a warning event when it is halfway to
 * reaching the interrupt condition. This warning can be caught by an admin to spot potential interrupt
 * scenarios in advance.
 */
interface ICircuitBreaker {
    /* ====================== VARS ====================== */

    // how much funds passed in selected window
    function liqInPeriod (uint256 windowId) external view returns ( uint256 );

    /* ===================== EVENTS ===================== */
    event ThresholdWarning();
    event TriggeredRateLimitExpired();
    event ResetByAdmin();
    event RateLimitTriggered();

    /* ===================== ERRORS ===================== */
    error Triggered();
    error ThresholdExceeded();

    /* ==================== MUTABLE METHODS ==================== */

    /**
     * @notice  Transfer funds to a specified address.
     *          If the threshold is exceeded, the transfer will be interrupted.
     * @dev This function will revert if the contract state is interrupted.
     * @param token The address of the erc20 token to transfer
     * @param to The address to transfer to.
     * @param amount The amount to be transferred.
     * @param value The total value of the transfer, in fiat amount, for ex. $100 should be passed as 100, not 100*1e18.
     *        value is needed to avoid expensive call to the oracle inside of the transfer code.
     */
    function controlledTransfer(address token, address to, uint256 amount, uint256 value) external;

    function setFundsFlowThreshold(uint256 _fundsFlowThreshold) external;

    function reset() external;

    function triggerRateLimit() external;
    /* ==================== VIEW METHODS ==================== */

    function totalLiquidityInPeriod() external view returns (uint256);

    function getWindowId() external view returns (uint256);
}

contracts/interfaces/IRewardChefV2.sol.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "../periphery/contracts/interfaces/oracle/ISuOracle.sol";

/**
 * @title RewardChefV2
 * @notice fork of MasterChefV2(13/05/2023) https://etherscan.io/address/0xef0881ec094552b2e128cf945ef17a6752b4ec5d#code
 * @dev This contract is based on MVC2, but uses "virtual" balances instead of storing real ERC20 tokens
 * and uses address of this assets instead of pid.
 * Rewards that are distributed have to be deposited using refillReward(uint256 amount, uint64 endBlock)
 **/
interface IRewardChefV2 {
    /**
      * @notice Info of each reward pool.
      * @param accSushiPerShare -
      * @param lastRewardBlock -
      * @param allocPoint - The amount of allocation points assigned to the pool.
      * Also known as the amount of REWARD_TOKEN to distribute per block.
      * @param lpSupply -
     **/
    struct PoolInfo {
        uint128 accSushiPerShare;
        uint64 lastRewardBlock;
        uint64 allocPoint;
        uint256 lpSupply;
    }

    /**
      * @notice Info of each user.
      * @param amount - token amount the user has provided.
      * @param rewardDebt - The amount of rewards entitled to the user.
      * @param multiplicator1e18 - deposit amount multiplicator for current user, that depends on his lockupPeriod
      * @param lockupPeriodSeconds - users lockupPeriod in seconds
      * @param lockupStartTimestamp - timestamp when user start lockup
     **/
    struct UserInfo {
        uint256 amount;
        int256 rewardDebt;

        uint256 multiplicator1e18;
        uint256 lockupPeriodSeconds;
        uint256 lockupStartTimestamp;
    }

    /**
      * @notice Lockup info struct for a view method getPossibleLockupPeriodsSeconds()
      * @param lockupPeriodSeconds - available lockupPeriod in seconds
      * @param multiplicator1e18 - deposit amount multiplicator for this lockup period
     **/
    struct ILockupPeriod {
        uint256 lockupPeriodSeconds;
        uint256 multiplicator1e18;
    }

    // =======================================EVENTS=============================================
    event VirtualDeposit(address indexed user, address indexed asset, uint256 amount);
    event VirtualWithdraw(address indexed user, address indexed asset, uint256 amount);
    event ResetAmount(
        address indexed user, address indexed asset, address indexed to, uint256 amount, uint256 lockupPeriodSeconds
    );
    event Harvest(address indexed user, address indexed asset, uint256 amount);
    event LogPoolAddition(address indexed asset, uint256 allocPoint);
    event LogSetPool(address indexed asset, uint256 allocPoint);
    event LogUpdatePool(address indexed asset, uint64 lastRewardBlock, uint256 lpSupply, uint256 accSushiPerShare);

    // =======================================ERRORS=============================================
    error BadEndBlockForRefill();
    error ZeroLockupPeriod();
    error ZeroMultiplicator();
    error PoolExist();
    error PoolNotExist();
    error DifferentLockup();
    error HarvestBeforeLockup();

    // =========================================VARS=============================================
    /**
     *  @dev Total allocation points. Must be the sum of all allocation points in all pools.
     * The good practice, to always keep this variable is equal 1000.
     */
    function totalAllocPoint() external view returns ( uint256 );

    // =======================================REWARDER=============================================
    // solhint-disable-next-line func-name-mixedcase
    function REWARD_TOKEN() external view returns ( IERC20Upgradeable );
    // solhint-disable-next-line func-name-mixedcase
    function ORACLE() external view returns ( ISuOracle );
    function rewardPerBlock() external view returns ( uint256 );
    function rewardEndBlock() external view returns ( uint256 );

    /**
     * @dev admin transfer REWARD_TOKEN to contract to increase rewards per block
     */
    function refillReward(uint256 amount, uint64 endBlock) external;
    /**
     * @dev returns total amount of rewards allocated to the all pools on the rage (startBlock, endBlock]
     * i.e. excluding startBlock but including endBlock
     */
    function rewardsBetweenBlocks(uint256 startBlock, uint256 endBlock) external returns ( uint256 );

    //=======================================LOCKUP LOGIC===========================================
    /**
     * @notice view method that return all lockupPeriods in ILockupPeriod struct
     */
    function getPossibleLockupPeriodsSeconds() external view returns (ILockupPeriod[] memory);

    /**
     * @notice method to add new LockupPeriods with it multiplicator
     * @dev If multiplicator1e18 == 0 => we remove lockupPeriodSeconds else we add it
     * @param lockupPeriodSeconds Amount of seconds of lockupPeriod we want to add/remove
     * @param multiplicator1e18 Multiplicator for that lockupPeriod
     */
    function setPossibleLockupPeriodsSeconds(uint256 lockupPeriodSeconds, uint256 multiplicator1e18) external;

    //================================CORE REWARD CHEF METHODS======================================
    /**
     * @notice Add a new reward pool. Can only be called by the admin. Can't add the same LP token more than once.
     * @param allocPoint AP of the new pool.
     * @param _asset Address of the ERC-20 token.
     */
    function add(uint256 allocPoint, address _asset) external;

    /**
     * @notice Update the given pool's REWARD_TOKEN allocation point. Can only be called by the admin.
     * Can't be called for asset that was not added by add() function.
     * @param _asset Address of the ERC-20 token.
     * @param _allocPoint New AP of the pool.
     */
    function set(address _asset, uint256 _allocPoint) external;

    /**
     * @notice View function to see pending REWARD_TOKEN on frontend.
     * @param _asset Address of the ERC-20 token.
     * @param _user Address of user.
     * @return pending REWARD_TOKEN reward for a given user.
     */
    function pendingSushi(address _asset, address _user) external view returns ( uint256 );

    /**
     * @notice Update reward variables of the given pool.
     * @param asset Asset address
     * @return updated pool info
     */
    function updatePool(address asset) external returns ( PoolInfo memory );

    /**
     * @notice Update reward variables for all pools. Be careful of gas spending!
     */
    function updateAllPools() external;

    /**
     * @notice analogues to MCV2 Deposit method, but can be called only by trusted address (onlyRewardAccess)
     * that is trusted to honestly calc how many "virtual" tokens have to be allocated for each user.
     * @param asset Asset address
     * @param to User for whom we will increase the virtual balance
     * @param amountEDecimal Quantity without taking into account the multiplier that we will add
     * @param lockupPeriodSeconds Current deposit lockup period. Can't increase amount with 2 different lockupPeriods
     */
    function increaseAmount(address asset, address to, uint256 amountEDecimal, uint256 lockupPeriodSeconds) external;

    /**
     * @notice Analogues to MVC2 Withdraw method, that can be called only by trusted address (onlyRewardAccess)
     * that is trusted to honestly calc how many "virtual" tokens have to be allocated for each user.
     * @param asset Asset address
     * @param to User for whom we will subtract the virtual balance
     * @param amountEDecimal Quantity without taking into account the multiplier that we will subtract
     */
    function decreaseAmount(address asset, address to, uint256 amountEDecimal) external;

    /**
     * @notice View method that return penalty for user that will decrease amount by {amountEDecimal}
     * @param asset Asset address
     * @param to User who will decrease his deposit amount
     * @param amountEDecimal Quantity without taking into account the multiplier that we will subtract
     */
    function getDecreaseAmountRewardPenalty(
        address asset,
        address to,
        uint256 amountEDecimal
    ) external view returns (uint256);

    /**
     * @notice Harvest proceeds for transaction sender to {to}.
     * @dev Here we send msg.sender pendingSushi to {to} and update lockup period.
     * @param asset Asset address
     * @param to Receiver of REWARD_TOKEN rewards.
     */
    function harvest(address asset, address to, uint256 newLockupPeriodSeconds) external;

    // TODO: check for exploits
    /**
     * @notice Withdraw without caring about rewards. EMERGENCY ONLY.
     * @param asset Asset address
     * @param to The address of the user whose information will be cleared
     */
    function resetAmount(address asset, address to) external;

    //================================VIEW METHODS======================================
    /**
     * @notice Method for frontend that return pool APR
     * @param asset Asset address
     */
    function getPoolApr(address asset) external view returns (uint256);

    /**
     * @notice Reward tokens can be locked for certain period of time.
     * So user have to wait until at least timestamp to be able to harvest them.
     * There's no lock or any restriction on withdrawing principle.
     * This function return end timestamp for that lock.
     * @param asset Asset address
     * @param user User address
     */
    function getHarvestLockupEndTimestamp(address asset, address user) external view returns (uint256);
}

contracts/interfaces/ISuCdpRegistry.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

/**
 * @title SuCdpRegistry
 * @notice This contract is needed to index all opened CDPs.
 * @dev It can be removed if there's more gas-efficient way to do that, such as graphQL, NFT-lps or other methods
 **/
interface ISuCdpRegistry {
    /// @notice Collateral Debt Position
    struct CDP {
        address asset; // collateral token
        address owner; // borrower account
    }

    /* ===================== EVENTS ===================== */

    /// @notice event emitted when a new CDP is created
    event Added(address indexed asset, address indexed owner);

    /// @notice event emitted when a CDP is closed
    event Removed(address indexed asset, address indexed owner);

    /* ===================== ERRORS ===================== */

    error ArgumentsLengthMismatch();

    /* ==================== MUTABLE METHODS ==================== */

    /// @notice method to add or remove owner from cdpList[asset]
    function checkpoint ( address asset, address owner ) external;

    /* ==================== VIEW METHODS ==================== */

    /// @notice read-only function to get the list of all cdps
    function getAllCdps (  ) external view returns ( CDP[] memory r );

    /// @notice read-only function to get the list of cdps for a given collateral
    function getCdpsByCollateral ( address asset ) external view returns ( CDP[] memory cdps );

    /// @notice read-only function to get the list of all cdps by borrower
    function getCdpsByOwner ( address owner ) external view returns ( CDP[] memory r );

    /// @notice total number of cdps
    function getCdpsCount (  ) external view returns ( uint256 totalCdpCount );

    /// @notice number of cdps for a given collateral
    function getCdpsCountForCollateral ( address asset ) external view returns ( uint256 );

    /// @notice alive means there are debts in the vault for this collateral of this borrower
    function isAlive ( address asset, address owner ) external view returns ( bool );

    /// @notice listed means there are created cdps in this contract for this collateral of this borrower
    function isListed ( address asset, address owner ) external view returns ( bool );
}

