// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";

import "../interfaces/ISuManagerLiquidation.sol";
import "../interfaces/ISuVault.sol";
import "../interfaces/ISuManager.sol";
import "../interfaces/ISuLiquidationMiddleware.sol";

import "../periphery/contracts/access-control/SuAuthenticated.sol";

/**
 * @title SuManagerLiquidation
 * @dev See ISuManagerLiquidation interface for full details.
 */
abstract contract SuManagerLiquidation is Initializable, SuAuthenticated, ISuManagerLiquidation {
    ISuVault private _vault;
    ISuManager private _manager;
    ISuLiquidationMiddleware private _liquidationMiddleware;
    address private _stablePro;
    address public foundation;

    function __suManagerLiquidationInit(
        address _suManager,
        address _suVault,
        address _suLiquidationMiddleware,
        address _suStablePro,
        address _foundation
    ) internal onlyInitializing {
        _manager = ISuManager(_suManager);
        _vault = ISuVault(_suVault);
        _liquidationMiddleware = ISuLiquidationMiddleware(_suLiquidationMiddleware);
        _stablePro = _suStablePro;
        foundation = _foundation;
    }

    function triggerLiquidation(address asset, address owner) external {
        if (_vault.liquidationBlock(asset, owner) > 0) revert LiquidationIsTriggered();
        if (!_manager.isLiquidatablePosition(asset, owner)) revert PositionIsSafe();

        // summarize debt with accumulated fees
        _vault.update(asset, owner);

        _vault.triggerLiquidation(asset, owner);

        uint256 collateralsEDecimal = _vault.collateralsEDecimal(asset, owner);
        uint256 debtsE18 = _vault.debtsE18(asset, owner);
        uint256 debtWithFeesE18 = _vault.debtWithFeesE18(asset, owner);

        // we need to cut all debt (with fees) of owner and send collateral to _liquidationMiddleware
        _vault.liquidate(
            asset,
            owner,
            address(this),
            collateralsEDecimal,
            debtWithFeesE18
        );

        // TODO: make percent depending on collateralsEDecimal
        uint256 liquidationReward = collateralsEDecimal * 5 / 1000; // 0.5%
        uint256 sellAmount = collateralsEDecimal - liquidationReward;

        TransferHelper.safeTransfer(asset, msg.sender, liquidationReward);
        TransferHelper.safeTransfer(asset, address(_liquidationMiddleware), sellAmount);

        _liquidationMiddleware.borrowCollateralToSell(asset, sellAmount, debtsE18);

        emit LiquidationTriggered(asset, debtsE18);
    }
}

