// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import "../interfaces/IExchange.sol";
import "../../staking/interfaces/ITopStakers.sol";
import "../interfaces/IExchangeCallback.sol";
import "../../makerdao/interfaces/ICollateralJoin.sol";
import "../../periphery/contracts/access-control/SuAuthenticated.sol";
import "../../periphery/contracts/interfaces/oracle/ISuOracleAggregator.sol";

/**
 * @title Exchange
 * @notice Main contract of Exchange module. Here we can add order or fill it to get profit.
 * @dev See IExchange interface for full details.
 */
contract Exchange is SuAuthenticated, ReentrancyGuardUpgradeable, IExchange {
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.UintSet;
    // todo: add enumerableSet of all IDs
    mapping (uint256 => Order) public orders;
    EnumerableSetUpgradeable.UintSet private orderIdSet;

    /**
     * @notice priceCurve is like price_t = price_0 * (1 - t/priceCurve[0]) - priceCurve[1]
     * from timePassed = 0 to t priceCurve decrease linear, but after t (when discount is 20%), it's constant
    **/
    mapping (DiscountType => uint256[2]) public priceCurve;

    /**
     * @notice Amount of minimum amountLeft in order. It's measured in fiat.
     * For example DUST_THRESHOLD = 100 * 1e18 => it's 100 USD
    **/
    uint256 public DUST_THRESHOLD;
    ISuOracleAggregator public ORACLE;
    ITopStakers public TOP_STAKERS;

    function initialize(
        address _authControl,
        address _oracleAddress,
        address _topStakersAddress
    ) public initializer {
        __suAuthenticatedInit(_authControl);

        // -20% for 3min/10h/24h
        priceCurve[DiscountType.aggressive] = [180 * 5, 0];
        priceCurve[DiscountType.middle] = [36_000 * 5, 0];
        priceCurve[DiscountType.conservative] = [86_400 * 5, 0];
        DUST_THRESHOLD = 100 * 1e18;

        ORACLE = ISuOracleAggregator(_oracleAddress);
        TOP_STAKERS = ITopStakers(_topStakersAddress);
    }

    function setPriceCurve(DiscountType discountType, uint256[2] calldata newPriceCurve) public onlyAdmin {
        priceCurve[discountType] = newPriceCurve;
    }

    function addOrder(
        address token2sell,
        uint256 amount2sell,
        address token2buy,
        address beneficiary,
        DiscountType discountType,
        address collateralJoin // or store this map in exchange module
    ) external returns (uint256 orderId) {
        // @notice The Exchange Module works with explicit real ERC20 tokens balance, so we need to acquire them.
        // If the caller is a user/contract that has some tokens in the protocol, they need to get them out of the protocol via exit.
        // Otherwise, the user/contract just needs to transfer tokens to the Exchange Module.
        if (collateralJoin == address(0)) {
            IERC20(token2sell).transferFrom(msg.sender, address(this), amount2sell);
            // TransferHelper.safeTransferFrom(token2sell, msg.sender, address(this), amount2sell);
        } else {
            ICollateralJoin(collateralJoin).exit(address(this), amount2sell);
        }

        orderId = uint256(
            keccak256(
                abi.encodePacked(block.number, msg.sender, token2sell, amount2sell, token2buy)
            )
        );
        if (orders[orderId].creationTimestamp != 0) orderId += 1;
        if (orders[orderId].creationTimestamp != 0) revert OrderExist(orderId);

        orders[orderId] = Order(
            token2sell,
            amount2sell,
            token2buy,
            0, // token2buyAmount at start should be 0
            beneficiary,
            block.timestamp,
            discountType,
            msg.sender
        );
        EnumerableSetUpgradeable.add(orderIdSet, orderId);
        emit OrderAdded(orderId);
    }

    function cancelOrder(uint256 orderId) public {
        Order memory order = orders[orderId];

        if (order.creationTimestamp == 0) revert OrderNotExist(orderId);
        if (msg.sender != order.beneficiary && msg.sender != order.caller) revert UserIsNotBeneficiary();

        TransferHelper.safeTransfer(order.token2sell, msg.sender, order.amountLeft);
        delete orders[orderId];
        EnumerableSetUpgradeable.remove(orderIdSet, orderId);

        emit OrderCanceled(orderId);
    }

    /**
     * @notice Return discounted price of token depending on passed time and type of discount
     * @dev orderPrice = x * 10^18 * 10^(18 - decimals) like in Oracle
    **/
    function getDiscountedTokenPrice(
        uint256 token2sellPrice,
        uint256 timePassed,
        DiscountType discountType
    ) internal view returns (uint256) {
        uint256 curveDiv = priceCurve[discountType][0];
        uint256 curveSub = priceCurve[discountType][1];

        if (timePassed > curveDiv) {
            // minimum is 80%;
            return token2sellPrice * 8 / 10;
        }

        return token2sellPrice - token2sellPrice * timePassed / curveDiv - curveSub;
    }

    function getExpectedOrderOutcome(uint256 orderId, uint256 inAmount) public view returns (uint256 outAmount) {
        Order memory order = orders[orderId];
        uint256 timePassed = block.timestamp - order.creationTimestamp;

        // Let's assume that: inAmount = x * 10^d1, discountedTokenPrice = p1 * 10^(36 - d1), priceB = p2 * 10^(36 - d2)
        // Then outAmount = x * 10^d1 * p1 * 10^(36 - d1) / (p2 * 10^(36 - d2)) = (x * p1 / p2) * 10^(d2)
        uint256 discountedTokenPrice = getDiscountedTokenPrice(
            ORACLE.getFiatPrice1e18(order.token2sell),
            timePassed,
            order.discountType
        );
        uint256 priceB = ORACLE.getFiatPrice1e18(order.token2buy);
        outAmount = inAmount * discountedTokenPrice / priceB;
    }

    function fillOrderAsFlashLoan(
        uint256 orderId,
        uint256 inAmount,
        bytes memory data
    ) external nonReentrant returns (uint256 outAmount) {
        Order storage order = orders[orderId];

        if (order.creationTimestamp == 0) revert OrderNotExist(orderId);

        uint256 timePassed = block.timestamp - order.creationTimestamp;

        // grace period
        if (timePassed <= 60) {
            // check that msg.sender is in TopStaker list + it's on position {orderId % x}
            uint256 numberOfStakers = TOP_STAKERS.topN();
            address user = tx.origin;
            if (!TOP_STAKERS.isTopStaker(user)) revert IsNotTopStaker(user);
            if (uint160(user) % numberOfStakers != orderId % numberOfStakers) {
                revert OrderNotForThisStaker(orderId, user);
            }
        } else {
            address user = tx.origin;
            if (!TOP_STAKERS.isStaker(user)) revert IsNotStaker(user);
        }

        if (inAmount > order.amountLeft) {
            inAmount = order.amountLeft;
        }

        // the same as in getExpectedOrderOutcome
        uint256 discountedTokenPrice = getDiscountedTokenPrice(
            ORACLE.getSafeFiatPrice1e18(order.token2sell),
            timePassed,
            order.discountType
        );
        uint256 priceB = ORACLE.getSafeFiatPrice1e18(order.token2buy);
        outAmount = inAmount * discountedTokenPrice / priceB;

        if (outAmount == 0) {
            revert BadOutAmount();
        }

        order.token2buyAmount += outAmount;
        order.amountLeft -= inAmount;

        // If in order some token amount left after updating it
        // then it should have value > DUST_THRESHOLD, counted in fiat
        if (order.amountLeft > 0 && discountedTokenPrice * order.amountLeft / 1e18 <= DUST_THRESHOLD) {
            revert SmallAmountLeft();
        }

        TransferHelper.safeTransfer(order.token2sell, msg.sender, inAmount);

        uint256 balanceBefore = IERC20(order.token2buy).balanceOf(address(this));
        if(!IExchangeCallback(msg.sender).stableunitFillOrderCallback(order.token2buy, outAmount, msg.sender, data)) {
            revert CallbackError();
        }
        uint256 balanceAfter = IERC20(order.token2buy).balanceOf(address(this));
        if (balanceAfter < balanceBefore + outAmount) revert NotEnoughReturnValue();

        TransferHelper.safeTransfer(order.token2buy, order.beneficiary, outAmount);

        if (order.amountLeft == 0) {
            delete orders[orderId];
            EnumerableSetUpgradeable.remove(orderIdSet, orderId);
            emit OrderFilled(orderId);
        } else {
            emit OrderFilledPartially(orderId);
        }
    }

    function fillOrderSimple(uint256 orderId, uint256 inAmount) external returns (uint256 outAmount) {
        Order storage order = orders[orderId];

        if (order.creationTimestamp == 0) revert OrderNotExist(orderId);

        uint256 timePassed = block.timestamp - order.creationTimestamp;

        // grace period
        // solhint-disable-next-line no-empty-blocks
        if (timePassed <= 60) {
            /**
                // check that msg.sender is in TopStaker list + it's on position {orderId % x}
                uint256 numberOfStakers = TOP_STAKERS.getTopStakersLength();
                // TODO: update top stakers to be able to give "stake power" to another address
                if (!TOP_STAKERS.isTopStaker(tx.origin)) revert IsNotTopStaker(tx.origin);
                if (
                    uint160(tx.origin) % numberOfStakers != orderId % numberOfStakers
                ) revert OrderNotForThisStaker(orderId, tx.origin);
            */
            // solhint-disable-next-line no-empty-blocks
        } else {
            // check that msg.sender is in TopStaker list
        }

        if (inAmount > order.amountLeft) {
            inAmount = order.amountLeft;
        }

        // the same as in getExpectedOrderOutcome
        uint256 discountedTokenPrice = getDiscountedTokenPrice(
            ORACLE.getSafeFiatPrice1e18(order.token2sell),
            timePassed,
            order.discountType
        );
        uint256 priceB = ORACLE.getSafeFiatPrice1e18(order.token2buy);
        outAmount = inAmount * discountedTokenPrice / priceB;

        if (outAmount == 0) {
            revert BadOutAmount();
        }

        order.token2buyAmount += outAmount;
        order.amountLeft -= inAmount;

        // If in order some token amount left after updating it
        // then it should have value > DUST_THRESHOLD, counted in fiat
        if (order.amountLeft > 0 && discountedTokenPrice * order.amountLeft / 1e18 <= DUST_THRESHOLD) {
            revert SmallAmountLeft();
        }

        TransferHelper.safeTransferFrom(order.token2buy, msg.sender, order.beneficiary, outAmount);
        TransferHelper.safeTransfer(order.token2sell, msg.sender, inAmount);

        if (order.amountLeft == 0) {
            delete orders[orderId];
            EnumerableSetUpgradeable.remove(orderIdSet, orderId);
            emit OrderFilled(orderId);
        } else {
            emit OrderFilledPartially(orderId);
        }
    }

    function getOrder (uint256 orderId) public view returns (Order memory) {
        return orders[orderId];
    }

    function getOrdersCount() public view returns (uint256) {
        return EnumerableSetUpgradeable.length(orderIdSet);
    }

    /**
     * @notice This method can be deleted after we'll add GraphQL to watch orders
    **/
    function getAllOrders(uint256 offset, uint256 limit) external view returns (OrderWithId[] memory) {
        uint256[] memory orderIdValues = EnumerableSetUpgradeable.values(orderIdSet);
        uint256 totalOrders = orderIdValues.length;
        if (totalOrders == 0) return new OrderWithId[](0);

        uint256 end = offset + limit > totalOrders ? totalOrders : offset + limit;
        uint256 len = end > offset ? end - offset : 0;
        OrderWithId[] memory result = new OrderWithId[](len);
        for (uint256 i = offset; i < end; i++) {
            uint256 orderId = orderIdValues[i];
            result[i - offset] = OrderWithId({
                order: orders[orderId],
                orderId: orderId
            });
        }
        return result;
    }

    function supportsInterface(bytes4 interfaceId) public pure override returns (bool) {
        return interfaceId == type(IExchange).interfaceId;
    }

    uint256[45] private __gap;
}

contracts/exchange/interfaces/IArbitrage.content:
// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "./IExchangeCallback.sol";

struct FlashCallbackData {
    bool isLP;
    uint256 orderId;
    address token2sell;
    uint256 token2sellAmount;
    address recipient;
    InchData[] inchData;
}

struct ParaswapData {
    address contractAddress;
    address tokenTransferProxy;
    bytes data;
}

struct InchData {
    address contractAddress;
    bytes data;
    address token; // needed only if adapter unwrap not to all underlying tokens
}

interface IArbitrageHelper is IExchangeCallback {
    /* ===================== ERRORS ===================== */
    error NotAPool(address sender);
    error SwapNotProfitable(uint256 diffAmount);
    error SwapByParaSwapError();
    error SwapBy1InchError();
    error BadSender(address sender);
    error LPTokenNotSupported(address lpToken);
    error LPTokenNotRegistered(address lpToken);

    /* ===================== EVENTS ===================== */
    // event SwapByUniswap(address tokenA, address tokenB);

    /* ==================== MUTABLE METHODS ==================== */


    /**
      * @notice Register LP to use it adapter to withdraw or get price of LP later in swapLPWithFlashLoan();
      * @param lp - address of LP token
      * @param lpAdapter - address of LPAdapter for this LP token
    **/
    function registerLP(address lp, address lpAdapter) external;

    /**
      * @notice Reference implementation of Arbitrage bot contract, that use Exchange module to get profit
      * @dev Contract receive loan from exchange module, sell it on 1inch and get profit = received amount - fee
      * @param orderId - Id of order in Exchange module to fill
      * @param token2sell - Token in order to get as flash-loan
      * @param token2sellAmount - Amount of token in order to get as flash-loan
      * @param inchData - 1inch callData and contract to swap loan
     **/
    function swapWithFlashLoan(
        uint256 orderId,
        address token2sell,
        uint256 token2sellAmount,
        InchData memory inchData
    ) external;

    /**
      * @notice The same as swapWithFlashLoan, but loan is LP-token that we need to unwrap
      * @dev Contract receive LP-token from exchange module, unwrap it and swap with 1inch all underline tokens
      * to token that exchange module need.
      * @param orderId - Id of order in Exchange module to fill
      * @param token2sell - Token in order to get as flash-loan
      * @param token2sellAmount - Amount of token in order to get as flash-loan
      * @param inchData - 1inch callData and contract array to swap every underline token of LP
     **/
    function swapLPWithFlashLoan(
        uint256 orderId,
        address token2sell,
        uint256 token2sellAmount,
        InchData[] memory inchData
    ) external;
}

contracts/exchange/interfaces/IExchange..content:
// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

enum DiscountType {
    aggressive,
    middle,
    conservative
}

struct Order {
    address token2sell;
    uint256 amountLeft;
    address token2buy;
    uint256 token2buyAmount;
    address beneficiary;
    uint256 creationTimestamp;
    DiscountType discountType;
    address caller;
}

struct OrderWithId {
    Order order;
    uint256 orderId;
}

interface IExchange {
    /* ===================== ERRORS ===================== */
    error OrderExist(uint256 orderId);
    error OrderNotExist(uint256 orderId);
    error IncorrectToken(address orderToken2buy, address inToken);
    error SmallAmountLeft();
    error BadOutAmount();
    error UserIsNotBeneficiary();
    error IsNotTopStaker(address staker);
    error IsNotStaker(address staker);
    error OrderNotForThisStaker(uint256 orderId, address staker);
    error NotEnoughReturnValue();
    error CallbackError();

    /* ======================== EVENTS ========================= */
    event OrderAdded(uint256 orderId);
    event OrderCanceled(uint256 orderId);
    event OrderFilled(uint256 orderId);
    event OrderFilledPartially(uint256 orderId);
    event LpOrdersAdded(address LPToken, uint256[] orderIds);

    /* ==================== MUTABLE METHODS ==================== */

    /**
      * @notice Add order to swap {token2sell} to {token2buy}.
      * @dev This swap is async, because depends on how arbitrage bots will fill this order. Emits OrderAdded() event.
      * Before addOrder user needs to approve token2sell for exchange contract
      * @param token2sell - Address of token to sell
      * @param amount2sell - Amount of {token2sell} to sell
      * @param token2buy - Address of token to buy
      * @param beneficiary - Address that will receive {token2buy} after filling order.
      * @param discountType - Speed of decreasing swap price. { 0 - aggressive, 1 - middle, 2 - conservative }
      * @param collateralJoin - address of collateral adapter
     **/
    function addOrder (
        address token2sell,
        uint256 amount2sell,
        address token2buy,
        address beneficiary,
        DiscountType discountType,
        address collateralJoin
    ) external returns (uint256);

    /**
      * @notice Only user who place order or who is beneficiary can cancelOrder();
      * @param orderId - Id of order to cancel.
     **/
    function cancelOrder (uint256 orderId) external;

    /**
      * @notice Fill order with flash-loan like logic
      * @dev Caller contract receive orders {token2sell} and after that his stableunitFillOrderCallback() is called.
      * At the end of thi callback contract should send some amount of another token back to Exchange contract.
      * @param orderId - Id of order to fill.
      * @param inAmount - Amount of {token2sell} to receive
      * @param data - encoded data to pass to callback function
     **/
    function fillOrderAsFlashLoan (
        uint256 orderId,
        uint256 inAmount,
        bytes memory data
    ) external returns (uint256 outAmount);

    /**
      * @notice Function to update price curve - how fast {token2sell} price will decrease/increase
      * @param discountType - discount type to change
      * @param newPriceCurve - parameters of formula
     **/
    function setPriceCurve(DiscountType discountType, uint256[2] calldata newPriceCurve) external;

    /* ==================== VIEW METHODS ==================== */
    /**
      * @return The amount of outcome that caller will receive if he will fill {orderId} with {inAmount} of {token2sell}
     **/
    function getExpectedOrderOutcome (uint256 orderId, uint256 inAmount) external view returns (uint256);

    /**
      * @return Order with id = {orderId}
     **/
    function getOrder (uint256 orderId) external view returns (Order memory);

    /**
      * @return The amount of not full-filled orders
     **/
    function getOrdersCount () external view returns (uint256);

    /**
      * @return The array of all not-filled orders. May be deleted after adding GraphQL for orderIds
     **/
    function getAllOrders (uint256 offset, uint256 limit) external view returns (OrderWithId[] memory);
}

contracts/exchange/interfaces/IExchangeC.content:
// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

interface IExchangeCallback {
    /**
     * @notice Callback that will be called by Exchange module in fillOrderAsFlashLoan()
     * after sending collateral to msg.sender.
     * In the implementation you must pay stablecoin for Exchange contract.
     * @dev Any contract that calls Exchange#fillOrderAsFlashLoan must implement this interface
     * @param asset - The address of the token that should be returned
     * @param amount - The amount of the {token} to return
     * @param initiator The address of the flashloan initiator
     * @return True if the execution of the operation succeeds, false otherwise
     */
    function stableunitFillOrderCallback (
        address asset,
        uint256 amount,
        address initiator,
        bytes calldata params
    ) external returns (bool);
}

contracts/interfaces/IRewardChefV2.sol.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "../periphery/contracts/interfaces/oracle/ISuOracle.sol";

/**
 * @title RewardChefV2
 * @notice fork of MasterChefV2(13/05/2023) https://etherscan.io/address/0xef0881ec094552b2e128cf945ef17a6752b4ec5d#code
 * @dev This contract is based on MVC2, but uses "virtual" balances instead of storing real ERC20 tokens
 * and uses address of this assets instead of pid.
 * Rewards that are distributed have to be deposited using refillReward(uint256 amount, uint64 endBlock)
 **/
interface IRewardChefV2 {
    /**
      * @notice Info of each reward pool.
      * @param accSushiPerShare -
      * @param lastRewardBlock -
      * @param allocPoint - The amount of allocation points assigned to the pool.
      * Also known as the amount of REWARD_TOKEN to distribute per block.
      * @param lpSupply -
     **/
    struct PoolInfo {
        uint128 accSushiPerShare;
        uint64 lastRewardBlock;
        uint64 allocPoint;
        uint256 lpSupply;
    }

    /**
      * @notice Info of each user.
      * @param amount - token amount the user has provided.
      * @param rewardDebt - The amount of rewards entitled to the user.
      * @param multiplicator1e18 - deposit amount multiplicator for current user, that depends on his lockupPeriod
      * @param lockupPeriodSeconds - users lockupPeriod in seconds
      * @param lockupStartTimestamp - timestamp when user start lockup
     **/
    struct UserInfo {
        uint256 amount;
        int256 rewardDebt;

        uint256 multiplicator1e18;
        uint256 lockupPeriodSeconds;
        uint256 lockupStartTimestamp;
    }

    /**
      * @notice Lockup info struct for a view method getPossibleLockupPeriodsSeconds()
      * @param lockupPeriodSeconds - available lockupPeriod in seconds
      * @param multiplicator1e18 - deposit amount multiplicator for this lockup period
     **/
    struct ILockupPeriod {
        uint256 lockupPeriodSeconds;
        uint256 multiplicator1e18;
    }

    // =======================================EVENTS=============================================
    event VirtualDeposit(address indexed user, address indexed asset, uint256 amount);
    event VirtualWithdraw(address indexed user, address indexed asset, uint256 amount);
    event ResetAmount(
        address indexed user, address indexed asset, address indexed to, uint256 amount, uint256 lockupPeriodSeconds
    );
    event Harvest(address indexed user, address indexed asset, uint256 amount);
    event LogPoolAddition(address indexed asset, uint256 allocPoint);
    event LogSetPool(address indexed asset, uint256 allocPoint);
    event LogUpdatePool(address indexed asset, uint64 lastRewardBlock, uint256 lpSupply, uint256 accSushiPerShare);

    // =======================================ERRORS=============================================
    error BadEndBlockForRefill();
    error ZeroLockupPeriod();
    error ZeroMultiplicator();
    error PoolExist();
    error PoolNotExist();
    error DifferentLockup();
    error HarvestBeforeLockup();

    // =========================================VARS=============================================
    /**
     *  @dev Total allocation points. Must be the sum of all allocation points in all pools.
     * The good practice, to always keep this variable is equal 1000.
     */
    function totalAllocPoint() external view returns ( uint256 );

    // =======================================REWARDER=============================================
    // solhint-disable-next-line func-name-mixedcase
    function REWARD_TOKEN() external view returns ( IERC20Upgradeable );
    // solhint-disable-next-line func-name-mixedcase
    function ORACLE() external view returns ( ISuOracle );
    function rewardPerBlock() external view returns ( uint256 );
    function rewardEndBlock() external view returns ( uint256 );

    /**
     * @dev admin transfer REWARD_TOKEN to contract to increase rewards per block
     */
    function refillReward(uint256 amount, uint64 endBlock) external;
    /**
     * @dev returns total amount of rewards allocated to the all pools on the rage (startBlock, endBlock]
     * i.e. excluding startBlock but including endBlock
     */
    function rewardsBetweenBlocks(uint256 startBlock, uint256 endBlock) external returns ( uint256 );

    //=======================================LOCKUP LOGIC===========================================
    /**
     * @notice view method that return all lockupPeriods in ILockupPeriod struct
     */
    function getPossibleLockupPeriodsSeconds() external view returns (ILockupPeriod[] memory);

    /**
     * @notice method to add new LockupPeriods with it multiplicator
     * @dev If multiplicator1e18 == 0 => we remove lockupPeriodSeconds else we add it
     * @param lockupPeriodSeconds Amount of seconds of lockupPeriod we want to add/remove
     * @param multiplicator1e18 Multiplicator for that lockupPeriod
     */
    function setPossibleLockupPeriodsSeconds(uint256 lockupPeriodSeconds, uint256 multiplicator1e18) external;

    //================================CORE REWARD CHEF METHODS======================================
    /**
     * @notice Add a new reward pool. Can only be called by the admin. Can't add the same LP token more than once.
     * @param allocPoint AP of the new pool.
     * @param _asset Address of the ERC-20 token.
     */
    function add(uint256 allocPoint, address _asset) external;

    /**
     * @notice Update the given pool's REWARD_TOKEN allocation point. Can only be called by the admin.
     * Can't be called for asset that was not added by add() function.
     * @param _asset Address of the ERC-20 token.
     * @param _allocPoint New AP of the pool.
     */
    function set(address _asset, uint256 _allocPoint) external;

    /**
     * @notice View function to see pending REWARD_TOKEN on frontend.
     * @param _asset Address of the ERC-20 token.
     * @param _user Address of user.
     * @return pending REWARD_TOKEN reward for a given user.
     */
    function pendingSushi(address _asset, address _user) external view returns ( uint256 );

    /**
     * @notice Update reward variables of the given pool.
     * @param asset Asset address
     * @return updated pool info
     */
    function updatePool(address asset) external returns ( PoolInfo memory );

    /**
     * @notice Update reward variables for all pools. Be careful of gas spending!
     */
    function updateAllPools() external;

    /**
     * @notice analogues to MCV2 Deposit method, but can be called only by trusted address (onlyRewardAccess)
     * that is trusted to honestly calc how many "virtual" tokens have to be allocated for each user.
     * @param asset Asset address
     * @param to User for whom we will increase the virtual balance
     * @param amountEDecimal Quantity without taking into account the multiplier that we will add
     * @param lockupPeriodSeconds Current deposit lockup period. Can't increase amount with 2 different lockupPeriods
     */
    function increaseAmount(address asset, address to, uint256 amountEDecimal, uint256 lockupPeriodSeconds) external;

    /**
     * @notice Analogues to MVC2 Withdraw method, that can be called only by trusted address (onlyRewardAccess)
     * that is trusted to honestly calc how many "virtual" tokens have to be allocated for each user.
     * @param asset Asset address
     * @param to User for whom we will subtract the virtual balance
     * @param amountEDecimal Quantity without taking into account the multiplier that we will subtract
     */
    function decreaseAmount(address asset, address to, uint256 amountEDecimal) external;

    /**
     * @notice View method that return penalty for user that will decrease amount by {amountEDecimal}
     * @param asset Asset address
     * @param to User who will decrease his deposit amount
     * @param amountEDecimal Quantity without taking into account the multiplier that we will subtract
     */
    function getDecreaseAmountRewardPenalty(
        address asset,
        address to,
        uint256 amountEDecimal
    ) external view returns (uint256);

    /**
     * @notice Harvest proceeds for transaction sender to {to}.
     * @dev Here we send msg.sender pendingSushi to {to} and update lockup period.
     * @param asset Asset address
     * @param to Receiver of REWARD_TOKEN rewards.
     */
    function harvest(address asset, address to, uint256 newLockupPeriodSeconds) external;

    // TODO: check for exploits
    /**
     * @notice Withdraw without caring about rewards. EMERGENCY ONLY.
     * @param asset Asset address
     * @param to The address of the user whose information will be cleared
     */
    function resetAmount(address asset, address to) external;

    //================================VIEW METHODS======================================
    /**
     * @notice Method for frontend that return pool APR in base of 1e18
     * @param asset Asset address
     */
    function getPoolApr(address asset) external view returns (uint256);

    /**
     * @notice Reward tokens can be locked for certain period of time.
     * So user have to wait until at least timestamp to be able to harvest them.
     * There's no lock or any restriction on withdrawing principle.
     * This function return end timestamp for that lock.
     * @param asset Asset address
     * @param user User address
     */
    function getHarvestLockupEndTimestamp(address asset, address user) external view returns (uint256);
}

