// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/IAccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/introspection/IERC165Upgradeable.sol";
import "../../access-control/SuAccessRoles.sol";

/**
 * @notice Access control for contracts
 * @dev External interface of AccessControl declared to support ERC165 detection.
 **/
interface ISuAccessControl is IAccessControlUpgradeable, IERC165Upgradeable {
    function setRoleAdmin(bytes32 role, bytes32 adminRole) external;
}

// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "../oracle/ISuOracle.sol";

struct WithdrawResult {
    address token;
    uint256 amount;
}

interface ILPAdapter is ISuOracle {
    /* ======================== ERRORS ======================== */
    error IsNotLP(address asset);
    error AlreadyRegistered(address asset);
    error LPOracleNotReady();

    /* ==================== MUTABLE METHODS ==================== */
    /**
      * @notice Unwrap LP token with depth = 2 (if underlying token is LP => it's also unwrapping)
      * @param asset - address of LP token to withdraw/unwrap
      * @param amount - amount of asset to withdraw/unwrap
     **/
    function withdraw(address asset, uint256 amount) external returns (WithdrawResult[] memory);

    /* ====================== VIEW METHODS ==================== */
    /**
      * @notice Returns if asset ig Balancer LP token, registered in our adapter
      * @param asset - address of LP token to check
     **/
    function isAdapterLP(address asset) external returns (bool);
}

// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "./ISuOracle.sol";

interface ISuOracleAggregator is ISuOracle {
    /* ===================== STRUCT ===================== */
    struct AssetLimitation {
        uint256 lowerBound;
        uint256 upperBound;
    }

    /* ===================== EVENTS ===================== */
    event OracleLowerBoundWarning(uint256 price);
    event OracleUpperBoundWarning(uint256 price);

    /* ===================== ERRORS ===================== */
    error NoOracleFound(address asset);
    error NoOracleImplementation();
    error BadOracleId();

    /* ====================== VARS ====================== */
    function assetToOracle (address asset) external view returns ( uint256 );
    function oracleImplementations (uint256 oracleId) external view returns ( ISuOracle );

    /* ==================== METHODS ==================== */

    /**
    * @notice Like getFiatPrice1e18 from ISuOracle, but also checks bounds and emit event if there is an error.
    * We should call it in non-view method where we need safePrice from Oracle.
    * @param asset - address of asset
    **/
    function getSafeFiatPrice1e18(address asset) external returns (uint256);

    /**
    * @notice Change lower bound of asset
    * @param asset - address of asset
    * @param newLowerBound - new value of lower bound
    **/
    function setLowerBound(address asset, uint256 newLowerBound) external;

    /**
    * @notice Change upper bound of asset
    * @param asset - address of asset
    * @param newUpperBound - new value of upper bound
    **/
    function setUpperBound(address asset, uint256 newUpperBound) external;

    /**
       * @notice assign address of oracle implementation to the oracleId
    * @param oracleId - number 0,1, etc to assign the oracle
    * @param oracleImplementation - an address with ISuOracle implementation contract
    **/
    function setOracleImplementation(uint256 oracleId, ISuOracle oracleImplementation) external;

    /**
    * @notice specify what oracleId should be used for each assets. Checks that oracleId has an implementation
    **/
    function setOracleIdForAssets(address[] memory assets, uint256 oracleId) external;

    /* ==================== VIEW METHODS ==================== */
    /**
    * @return true if oracle is set to this asset, false otherwise
    **/
    function hasPriceForAsset(address asset) external view returns(bool);
}

contracts/staking/interfaces/ITopStakers.content:
// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

// WARNING: Implementation is a mock, but the interface will be the same
interface ITopStakers {
    /* ===================== ERRORS ===================== */
    error NotStakerFound();
    error AmountMustBeMoreThatMinimum();
    error InsufficientStake(uint256 available, uint256 requested);

    /* ========================= VARS ========================== */

    function topN() external view returns(uint256);

    /* ==================== MUTABLE METHODS ==================== */

    /**
      * @notice Admin function to change number of top stakers
     **/
    function changeTopN(uint256 newTopN) external;

    /**
      * @notice Admin function to change veToken address
     **/
    function changeVeToken(address veToken) external;

    /**
      * @notice Admin function to change minStakingAmount
     **/
    function changeMinStakingAmount(uint256 minStakingAmount) external;

    /**
      * @notice User function to add yourself in stakers list to be able to be topStaker for liquidation
     **/
    function subscribe() external;

    /**
      * @notice User function to remove yourself from stakers list
     **/
    function unsubscribe() external;

    /**
      * @notice Function to remove user from stakers list, can be called only with onlyVotingEscrow role (for veToken)
     **/
    function unsubscribeOnBehalf(address user) external;

    /**
      * @notice Sometimes topStakers can be expired (lock date is over), this function remove such topStakers
     **/
    function cleanExpiredTopStakers() external;

    /* ==================== VIEW METHODS ==================== */

    /**
      * @notice Returns if user is top staker or not
     **/
    function isTopStaker(address staker) external view returns(bool);


    /**
      * @notice Returns if user is staker with not expired stake
     **/
    function isStaker(address user) external view returns(bool);

    /**
      * @notice Returns the list of users that are topStakers
     **/
    function getTopStakers() external view returns(address[] memory);

    /**
      * @notice Returns the amount of subscribed users (stakers list length)
     **/
    function getStakersLength() external view returns(uint256);
}
