// along with this program.  If not, see <https://www.gnu.org/licenses/>.

pragma solidity ^0.8.0;

import "../interfaces/ISafeEngine.sol";
import "../interfaces/IDssCdpManager.sol";

// CHANGED: interface created and struct and events moved there

contract CdpHandler {
    constructor(ISafeEngine safeEngine) public {
        safeEngine.approveSAFEModification(msg.sender);
    }
}

contract DssCdpManager is IDssCdpManager {
    ISafeEngine               public safeEngine;
    uint                      public cdpi;  // Auto incremental
    mapping (uint => address) public safes; // CDPId => CdpHandler
    mapping (uint => List)    public list;  // CDPId => Prev & Next CDPIds (double linked list)
    mapping (uint => address) public owns;  // CDPId => Owner
    mapping (uint => bytes32) public collateralTypes; // CDPId => CollateralType

    mapping (address => uint) public firstCdpId; // Owner => First CDPId
    mapping (address => uint) public lastCdpId; // Owner => Last CDPId
    mapping (address => uint) public cdpCount; // Owner => Amount of CDPs

    mapping (
        address => mapping (
            uint => mapping (
                address => uint
            )
        )
    ) public cdpCan; // Owner => CDPId => Allowed Addr => True/False

    mapping (
        address => mapping (
            address => uint
        )
    ) public handlerCan; // Cdp => Allowed Addr => True/False

    modifier cdpAllowed(uint cdp) {
        require(msg.sender == owns[cdp] || cdpCan[owns[cdp]][cdp][msg.sender] == 1, "cdp-not-allowed");
        _;
    }

    modifier handlerAlloweded(address urn) {
        require(msg.sender == urn || handlerCan[urn][msg.sender] == 1, "urn-not-allowed");
        _;
    }

    constructor(address safeEngine_) public {
        safeEngine = ISafeEngine(safeEngine_);
    }

    function _add(uint x, uint y) internal pure returns (uint z) {
        require((z = x + y) >= x);
    }

    function _sub(uint x, uint y) internal pure returns (uint z) {
        require((z = x - y) <= x);
    }

    function toInt(uint x) internal pure returns (int y) {
        y = int(x);
        require(y >= 0);
    }

    function cdpAllow(uint cdp, address usr, uint ok) public cdpAllowed(cdp) {
        cdpCan[owns[cdp]][cdp][usr] = ok;
    }

    function allowHandler(address usr, uint ok) public {
        handlerCan[msg.sender][usr] = ok;
    }

    function openSafe(bytes32 collateralType, address usr) public returns (uint) {
        require(usr != address(0), "usr-address-0");

        cdpi = _add(cdpi, 1);
        safes[cdpi] = address(new CdpHandler(safeEngine));
        owns[cdpi] = usr;
        collateralTypes[cdpi] = collateralType;

        // Add new CDP to double linked list and pointers
        if (firstCdpId[usr] == 0) {
            firstCdpId[usr] = cdpi;
        }
        if (lastCdpId[usr] != 0) {
            list[cdpi].prev = lastCdpId[usr];
            list[lastCdpId[usr]].next = cdpi;
        }
        lastCdpId[usr] = cdpi;
        cdpCount[usr] = _add(cdpCount[usr], 1);

        emit NewCdp(msg.sender, usr, cdpi);
        return cdpi;
    }

    function transferSafeOwnership(uint cdp, address dst) public cdpAllowed(cdp) {
        require(dst != address(0), "dst-address-0");
        require(dst != owns[cdp], "dst-already-owner");

        // Remove transferred CDP from double linked list of origin user and pointers
        if (list[cdp].prev != 0) {
            // Set the next pointer of the prev cdp (if exists) to the next of the transferred one
            list[list[cdp].prev].next = list[cdp].next;
        }
        if (list[cdp].next != 0) {
            // If wasn't the last one - Set the prev pointer of the next cdp to the prev of the transferred one
            list[list[cdp].next].prev = list[cdp].prev;
        } else {
            // If was the last one - Update last pointer of the owner
            lastCdpId[owns[cdp]] = list[cdp].prev;
        }
        if (firstCdpId[owns[cdp]] == cdp) {
            // If was the first one - Update first pointer of the owner
            firstCdpId[owns[cdp]] = list[cdp].next;
        }
        cdpCount[owns[cdp]] = _sub(cdpCount[owns[cdp]], 1);

        // Transfer ownership
        owns[cdp] = dst;

        // Add transferred CDP to double linked list of destiny user and pointers
        list[cdp].prev = lastCdpId[dst];
        list[cdp].next = 0;
        if (lastCdpId[dst] != 0) {
            list[lastCdpId[dst]].next = cdp;
        }
        if (firstCdpId[dst] == 0) {
            firstCdpId[dst] = cdp;
        }
        lastCdpId[dst] = cdp;
        cdpCount[dst] = _add(cdpCount[dst], 1);
    }

    function modifySAFECollateralization(
        uint cdp,
        int deltaCollateral,
        int deltaDebt
    ) public cdpAllowed(cdp) {
        address urn = safes[cdp];
        safeEngine.modifySAFECollateralization(
            collateralTypes[cdp],
            urn,
            urn,
            urn,
            deltaCollateral,
            deltaDebt
        );
    }

    function transferCollateral(
        uint cdp,
        address dst,
        uint wad
    ) public cdpAllowed(cdp) {
        safeEngine.transferCollateral(collateralTypes[cdp], safes[cdp], dst, wad);
    }

    function transferCollateral(
        bytes32 ilk,
        uint cdp,
        address dst,
        uint wad
    ) public cdpAllowed(cdp) {
        safeEngine.transferCollateral(ilk, safes[cdp], dst, wad);
    }

    function transferInternalCoins(uint cdp, address dst, uint rad) public cdpAllowed(cdp) {
        safeEngine.transferInternalCoins(safes[cdp], dst, rad);
    }

    function quit(uint cdp, address dst) public cdpAllowed(cdp) handlerAlloweded(dst) {
        (uint lockedCollateral, uint generatedDebt) = safeEngine.safes(collateralTypes[cdp], safes[cdp]);
        safeEngine.transferSAFECollateralAndDebt(
            collateralTypes[cdp],
            safes[cdp],
            dst,
            toInt(lockedCollateral),
            toInt(generatedDebt)
        );
    }

    function enter(address src, uint cdp) public handlerAlloweded(src) cdpAllowed(cdp) {
        (uint lockedCollateral, uint generatedDebt) = safeEngine.safes(collateralTypes[cdp], src);
        safeEngine.transferSAFECollateralAndDebt(
            collateralTypes[cdp],
            src,
            safes[cdp],
            toInt(lockedCollateral),
            toInt(generatedDebt)
        );
    }

    function moveSAFE(uint cdpSrc, uint cdpDst) public cdpAllowed(cdpSrc) cdpAllowed(cdpDst) {
        require(collateralTypes[cdpSrc] == collateralTypes[cdpDst], "non-matching-cdps");
        (uint lockedCollateral, uint generatedDebt) = safeEngine.safes(collateralTypes[cdpSrc], safes[cdpSrc]);
        safeEngine.transferSAFECollateralAndDebt(
            collateralTypes[cdpSrc],
            safes[cdpSrc],
            safes[cdpDst],
            toInt(lockedCollateral),
            toInt(generatedDebt)
        );
    }
}

