pragma solidity ^0.8.0;

import "../interfaces/ISafeEngine.sol";
import "../interfaces/IPriceFeed.sol";
import "../../periphery/contracts/access-control/SuAuthenticatedNonUpgradeable.sol";

// FIXME: This contract was altered compared to the production version.
// It doesn't use LibNote anymore.
// New deployments of this contract will need to include custom events (TO DO).

// CHANGED: Added SuAuthenticated instead of authorizedAccounts
// @notice this contract can be deprecated

contract OracleRelayer is SuAuthenticatedNonUpgradeable {
    // --- Data ---
    struct CollateralType {
        IPriceFeed priceFeed;
        uint256 liquidationRatio; // [ray]
    }

    mapping (bytes32 => CollateralType) public collateralTypes;

    ISafeEngine public safeEngine;
    uint256 public redemptionPrice;  // ref per StablePro [ray]

    // --- Events ---
    event Poke(
      bytes32 collateralType,
      bytes32 val,  // [wad]
      uint256 safetyPrice  // [ray]
    );

    // --- Init ---
    constructor(address accessControl, address safeEngine_) SuAuthenticatedNonUpgradeable(accessControl) public {
        safeEngine = ISafeEngine(safeEngine_);
        redemptionPrice = ONE;
        contractEnabled = 1;
    }

    // --- Disable ---
    event DisableContract();
    uint256 public contractEnabled; // Access flag, indicates whether this contract is still active
    function disableContract() external onlyDisablerAccess {
        contractEnabled = 0;
        emit DisableContract();
    }

    // --- Math ---
    uint constant ONE = 10 ** 27;

    function mul(uint x, uint y) internal pure returns (uint z) {
        require(y == 0 || (z = x * y) / y == x);
    }
    function rdiv(uint x, uint y) internal pure returns (uint z) {
        z = mul(x, ONE) / y;
    }

    // --- Administration ---
    function modifyParameters(bytes32 collateralType, bytes32 what, address priceFeed_) external onlyAdmin {
        require(contractEnabled == 1, "OracleRelayer/not-contractEnabled");
        if (what == "priceFeed") collateralTypes[collateralType].priceFeed = IPriceFeed(priceFeed_);
        else revert("OracleRelayer/modifyParameters-unrecognized-param");
    }
    function modifyParameters(bytes32 what, uint data) external onlyAdmin {
        require(contractEnabled == 1, "OracleRelayer/not-contractEnabled");
        if (what == "redemptionPrice") redemptionPrice = data;
        else revert("OracleRelayer/modifyParameters-unrecognized-param");
    }
    function modifyParameters(bytes32 collateralType, bytes32 what, uint data) external onlyAdmin {
        require(contractEnabled == 1, "OracleRelayer/not-contractEnabled");
        if (what == "liquidationRatio") collateralTypes[collateralType].liquidationRatio = data;
        else revert("OracleRelayer/modifyParameters-unrecognized-param");
    }

    // --- Update value ---
    function updateSafetyPrice(bytes32 collateralType) external {
        (bytes32 val, bool has) = collateralTypes[collateralType].priceFeed.peek();
        uint256 safetyPrice = has
            ? rdiv(
                rdiv(mul(uint(val), 10 ** 9), redemptionPrice),
                collateralTypes[collateralType].liquidationRatio
            )
            : 0;
        safeEngine.modifyParameters(collateralType, "safetyPrice", safetyPrice);
        emit Poke(collateralType, val, safetyPrice);
    }
}

