// SPDX-License-Identifier: AGPL-3.0-or-later

/// SAFEEngine.sol -- StablePro CDP database

// Copyright (C) 2018 Rain <rainbreak@riseup.net>
//
// This program is free software: you can redistribute it and/or modify
// it under the terms of the GNU Affero General Public License as published by
// the Free Software Foundation, either version 3 of the License, or
// (at your option) any later version.
//
// This program is distributed in the hope that it will be useful,
// but WITHOUT ANY WARRANTY; without even the implied warranty of
// MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the
// GNU Affero General Public License for more details.
//
// You should have received a copy of the GNU Affero General Public License
// along with this program.  If not, see <https://www.gnu.org/licenses/>.

pragma solidity ^0.8.0;

import {Math} from "../utils/Math.sol";
import {ISafeEngine} from "../interfaces/ISafeEngine.sol";
import "../../periphery/contracts/access-control/SuAuthenticatedNonUpgradeable.sol";

// FIXME: This contract was altered compared to the production version.
// It doesn't use LibNote anymore.
// New deployments of this contract will need to include custom events (TO DO).

// CHANGED: added interface, moved structs there.
// Maybe we'll need to add events in each method (need to discuss, in another branch dss has LogNote for that)
// Added SuAuthenticated instead of authorizedAccounts

contract SafeEngine is SuAuthenticatedNonUpgradeable, Math, ISafeEngine {
    // --- Disable ---
    uint public contractEnabled;
    function disableContract() external onlyDisablerAccess {
        contractEnabled = 0;
        emit DisableContract();
    }

    // --- Auth ---
    // Who can transfer collateral & debt in/out of a CDP
    mapping(address => mapping (address => uint)) public cdpRights;

    function approveSAFEModification(address usr) external { cdpRights[msg.sender][usr] = 1; }

    function denySAFEModification(address usr) external { cdpRights[msg.sender][usr] = 0; }

    function canModifySAFE(address cdp, address account) internal view returns (bool) {
        return either(cdp == account, cdpRights[cdp][account] == 1);
    }

    /// Total User Debt (drawn StablePro + stability fee accrued) = SAFE.generatedDebt * CollateralType.accumulatedRate

    // Data about each collateral type
    mapping (bytes32 => CollateralType)            public collateralTypes;
    // Data about each SAFE
    mapping (bytes32 => mapping (address => SAFE)) public safes;
    // Balance of each collateral type
    mapping (bytes32 => mapping (address => uint)) public tokenCollateral; // [wad]
    // Internal balance of pegged-coins
    mapping (address => uint256)                   public coinBalance;     // [rad]
    // Amount of debt held by an account. Coin & debt are like matter and antimatter. They nullify each other
    mapping (address => uint256)                   public debtBalance;     // [rad]

    uint256 public globalDebt;          // Total amount of debt (coins) currently issued [rad]
    uint256 public globalUnbackedDebt;  // 'Bad' debt that's not covered by collateral   [rad]
    uint256 public globalDebtCeiling;   // Maximum amount of debt that can be issued     [rad]

    // --- Init ---
    constructor(address accessControl) SuAuthenticatedNonUpgradeable(accessControl) public {
        contractEnabled = 1;
    }

    // --- Administration ---
    function initializeCollateralType(bytes32 collateralType) external onlyAdmin {
        require(collateralTypes[collateralType].accumulatedRate == 0, "SafeEngine/collateral-type-already-init");
        collateralTypes[collateralType].accumulatedRate = 10 ** 27;
    }
    function modifyParameters(bytes32 what, uint data) external onlyAdmin {
        require(contractEnabled == 1, "SafeEngine/not-contractEnabled");
        if (what == "globalDebtCeiling") globalDebtCeiling = data;
        else revert("SafeEngine/modifyParameters-unrecognized-param");
    }
    function modifyParameters(bytes32 collateralType, bytes32 what, uint data) external onlyAdmin {
        require(contractEnabled == 1, "SafeEngine/not-contractEnabled");
        if (what == "safetyPrice") collateralTypes[collateralType].safetyPrice = data;
        else if (what == "debtCeiling") collateralTypes[collateralType].debtCeiling = data;
        else if (what == "debtFloor") collateralTypes[collateralType].debtFloor = data;
        else revert("SafeEngine/modifyParameters-unrecognized-param");
    }

    // --- Fungibility ---
    function modifyCollateralBalance(bytes32 collateralType, address usr, int256 wad) external onlyCDPAccess {
        tokenCollateral[collateralType][usr] = _add(tokenCollateral[collateralType][usr], wad);
    }
    function transferCollateral(bytes32 collateralType, address src, address dst, uint256 wad) external {
        require(canModifySAFE(src, msg.sender), "SafeEngine/not-allowed");
        tokenCollateral[collateralType][src] = _sub(tokenCollateral[collateralType][src], wad);
        tokenCollateral[collateralType][dst] = _add(tokenCollateral[collateralType][dst], wad);
    }
    function transferInternalCoins(address src, address dst, uint256 rad) external {
        require(canModifySAFE(src, msg.sender), "SafeEngine/not-allowed");
        coinBalance[src] = _sub(coinBalance[src], rad);
        coinBalance[dst] = _add(coinBalance[dst], rad);
    }

    // --- CDP Manipulation ---
    function modifySAFECollateralization(
        bytes32 collateralType,
        address cdp,
        address collateralSource,
        address debtDestination,
        int deltaCollateral,
        int deltaDebt
    ) external {
        // system is contractEnabled
        require(contractEnabled == 1, "SafeEngine/not-contractEnabled");

        SAFE memory safe_ = safes[collateralType][cdp];
        CollateralType memory collateralType_ = collateralTypes[collateralType];
        // collateralType has been initialised
        require(collateralType_.accumulatedRate != 0, "SafeEngine/collateral-type-not-init");

        safe_.lockedCollateral = _add(safe_.lockedCollateral, deltaCollateral);
        safe_.generatedDebt = _add(safe_.generatedDebt, deltaDebt);
        collateralType_.debtAmount = _add(collateralType_.debtAmount, deltaDebt);

        int dAmountToRaise = _mul(collateralType_.accumulatedRate, deltaDebt);
        uint amountToRaise = _mul(collateralType_.accumulatedRate, safe_.generatedDebt);
        globalDebt = _add(globalDebt, dAmountToRaise);

        // either debt has decreased, or debt ceilings are not exceeded
        require(
            either(
                deltaDebt <= 0,
                both(
                    _mul(collateralType_.debtAmount, collateralType_.accumulatedRate) <= collateralType_.debtCeiling,
                    globalDebt <= globalDebtCeiling
                )
            ),
            "SafeEngine/ceiling-exceeded"
        );
        // safe is either less risky than before, or it is safe
        require(
            either(
                both(deltaDebt <= 0, deltaCollateral >= 0),
                amountToRaise <= _mul(safe_.lockedCollateral, collateralType_.safetyPrice)
            ),
            "SafeEngine/not-safe"
        );

        // safe is either more safe, or the owner consents
        require(
            either(both(deltaDebt <= 0, deltaCollateral >= 0), canModifySAFE(cdp, msg.sender)),
            "SafeEngine/not-allowed-u"
        );
        // collateral src consents
        require(either(deltaCollateral <= 0, canModifySAFE(collateralSource, msg.sender)), "SafeEngine/not-allowed-v");
        // debt dst consents
        require(either(deltaDebt >= 0, canModifySAFE(debtDestination, msg.sender)), "SafeEngine/not-allowed-w");

        // safe has no debt, or a non-dusty amount
        require(either(safe_.generatedDebt == 0, amountToRaise >= collateralType_.debtFloor), "SafeEngine/debtFloor");

        tokenCollateral[collateralType][collateralSource] =
                        _sub(tokenCollateral[collateralType][collateralSource], deltaCollateral);
        coinBalance[debtDestination] = _add(coinBalance[debtDestination], dAmountToRaise);

        safes[collateralType][cdp] = safe_;
        collateralTypes[collateralType] = collateralType_;
    }
    // --- CDP Fungibility ---
    function transferSAFECollateralAndDebt(
        bytes32 collateralType,
        address src,
        address dst,
        int deltaCollateral,
        int deltaDebt
    ) external {
        SAFE storage srcCDP = safes[collateralType][src];
        SAFE storage dstCDP = safes[collateralType][dst];
        CollateralType storage col = collateralTypes[collateralType];

        srcCDP.lockedCollateral = _sub(srcCDP.lockedCollateral, deltaCollateral);
        srcCDP.generatedDebt = _sub(srcCDP.generatedDebt, deltaDebt);
        dstCDP.lockedCollateral = _add(dstCDP.lockedCollateral, deltaCollateral);
        dstCDP.generatedDebt = _add(dstCDP.generatedDebt, deltaDebt);

        uint srcAmountToRaise = _mul(srcCDP.generatedDebt, col.accumulatedRate);
        uint dstAmountToRaise = _mul(dstCDP.generatedDebt, col.accumulatedRate);

        // both sides consent
        require(both(canModifySAFE(src, msg.sender), canModifySAFE(dst, msg.sender)), "SafeEngine/not-allowed");

        // both sides safe
        require(srcAmountToRaise <= _mul(srcCDP.lockedCollateral, col.safetyPrice), "SafeEngine/not-safe-src");
        require(dstAmountToRaise <= _mul(dstCDP.lockedCollateral, col.safetyPrice), "SafeEngine/not-safe-dst");

        // both sides non-dusty
        require(either(srcAmountToRaise >= col.debtFloor, srcCDP.generatedDebt == 0), "SafeEngine/debtFloor-src");
        require(either(dstAmountToRaise >= col.debtFloor, dstCDP.generatedDebt == 0), "SafeEngine/debtFloor-dst");
    }
    // --- CDP Confiscation ---
    function confiscateSAFECollateralAndDebt(
        bytes32 collateralType,
        address cdp,
        address collateralCounterparty,
        address debtCounterparty,
        int deltaCollateral,
        int deltaDebt
    ) external onlyCDPAccess {
        SAFE storage safe_ = safes[collateralType][cdp];
        CollateralType storage collateralType_ = collateralTypes[collateralType];

        safe_.lockedCollateral = _add(safe_.lockedCollateral, deltaCollateral);
        safe_.generatedDebt = _add(safe_.generatedDebt, deltaDebt);
        collateralType_.debtAmount = _add(collateralType_.debtAmount, deltaDebt);

        int deltaTotalIssuedDebt = _mul(collateralType_.accumulatedRate, deltaDebt);

        tokenCollateral[collateralType][collateralCounterparty] =
                        _sub(tokenCollateral[collateralType][collateralCounterparty], deltaCollateral);
        debtBalance[debtCounterparty] = _sub(debtBalance[debtCounterparty], deltaTotalIssuedDebt);
        globalUnbackedDebt = _sub(globalUnbackedDebt, deltaTotalIssuedDebt);
    }

    // --- Settlement ---

    function settleDebt(uint rad) external {
        address account      = msg.sender;
        debtBalance[account] = _sub(debtBalance[account], rad);
        coinBalance[account] = _sub(coinBalance[account], rad);
        globalUnbackedDebt   = _sub(globalUnbackedDebt,   rad);
        globalDebt           = _sub(globalDebt,   rad);
    }

    function createUnbackedDebt(address debtDestination, address coinDestination, uint rad) external onlyCDPAccess {
        debtBalance[debtDestination] = _add(debtBalance[debtDestination], rad);
        coinBalance[coinDestination] = _add(coinBalance[coinDestination], rad);
        globalUnbackedDebt           = _add(globalUnbackedDebt,   rad);
        globalDebt                   = _add(globalDebt,   rad);
    }

    // --- Rates ---
    function updateAccumulatedRate(
        bytes32 collateralType,
        address surplusDst,
        int accumulatedRate
    ) external onlyCDPAccess {
        require(contractEnabled == 1, "SafeEngine/not-contractEnabled");
        CollateralType storage collateralType_ = collateralTypes[collateralType];
        collateralType_.accumulatedRate        = _add(collateralType_.accumulatedRate, accumulatedRate);
        int deltaSurplus                       = _mul(collateralType_.debtAmount, accumulatedRate);
        coinBalance[surplusDst]                = _add(coinBalance[surplusDst], deltaSurplus);
        globalDebt                             = _add(globalDebt, deltaSurplus);
    }
}

